import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { 
  Code, 
  Cpu, 
  Zap, 
  Database, 
  Settings, 
  Brain, 
  MessageSquare, 
  Terminal,
  GitBranch,
  Bug,
  Lightbulb,
  Rocket,
  Shield,
  BarChart3,
  Wrench,
  Play,
  Square,
  RefreshCw,
  Wallet,
  Network,
  Activity
} from 'lucide-react';

interface Message {
  id: string;
  type: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  category?: string;
  codeSnippet?: string;
  apiResult?: any;
}

interface ConversationMemory {
  userPreferences: Record<string, any>;
  conversationHistory: Message[];
  activeProjects: string[];
  knownIssues: string[];
  userGoals: string[];
}

interface APICommand {
  id: string;
  name: string;
  description: string;
  endpoint: string;
  method: 'GET' | 'POST' | 'PUT' | 'DELETE';
  category: string;
  data?: any;
}

interface QuickAction {
  label: string;
  prompt: string;
  category: string;
}

// API Commands available to the AI Assistant
const API_COMMANDS: APICommand[] = [
  // Mining Operations
  { id: 'mining-start', name: 'Start Mining', description: 'Start mining operations', endpoint: '/api/mining/start', method: 'POST', category: 'mining' },
  { id: 'mining-stop', name: 'Stop Mining', description: 'Stop all mining operations', endpoint: '/api/mining/stop', method: 'POST', category: 'mining' },
  { id: 'mining-stats', name: 'Get Mining Stats', description: 'Retrieve current mining statistics', endpoint: '/api/mining/stats/realtime', method: 'GET', category: 'stats' },
  { id: 'mining-settings', name: 'Get Mining Settings', description: 'Retrieve mining configuration', endpoint: '/api/mining/settings', method: 'GET', category: 'mining' },

  // Pool Management
  { id: 'pools-list', name: 'List Mining Pools', description: 'Get all configured mining pools', endpoint: '/api/mining/pools', method: 'GET', category: 'pools' },
  { id: 'pool-connect', name: 'Connect to Pool', description: 'Connect to a mining pool', endpoint: '/api/mining/pools/connect', method: 'POST', category: 'pools' },
  { id: 'pool-disconnect', name: 'Disconnect from Pool', description: 'Disconnect from a mining pool', endpoint: '/api/mining/pools/disconnect', method: 'POST', category: 'pools' },

  // TERA Guardian System
  { id: 'guardians-list', name: 'List TERA Guardians', description: 'Get all TERA Guardian AI entities', endpoint: '/api/tera/guardians', method: 'GET', category: 'guardian' },
  { id: 'guardian-activate', name: 'Activate Guardian', description: 'Activate a TERA Guardian AI', endpoint: '/api/tera/guardians/activate', method: 'POST', category: 'guardian' },

  // Rewards and Wallet
  { id: 'rewards-list', name: 'Get Rewards', description: 'Retrieve mining rewards', endpoint: '/api/mining/rewards', method: 'GET', category: 'rewards' },
  { id: 'wallet-balance', name: 'Check Wallet Balance', description: 'Get current wallet balance', endpoint: '/api/wallet/balance', method: 'GET', category: 'rewards' },

  // Hardware Monitoring
  { id: 'hardware-status', name: 'Hardware Status', description: 'Get hardware monitoring data', endpoint: '/api/mining/hardware', method: 'GET', category: 'hardware' },
  { id: 'hardware-optimize', name: 'Optimize Hardware', description: 'Run hardware optimization', endpoint: '/api/mining/hardware/optimize', method: 'POST', category: 'hardware' }
];

// Quick Actions for user convenience
const QUICK_ACTIONS: QuickAction[] = [
  { label: 'Optimize my mining setup', prompt: 'Please analyze my current mining configuration and suggest optimizations for better performance and profitability.', category: 'optimization' },
  { label: 'Check security status', prompt: 'Run a comprehensive security check on my mining operations and highlight any vulnerabilities.', category: 'security' },
  { label: 'Troubleshoot connection issues', prompt: 'I\'m having trouble with my mining pool connections. Can you help diagnose and fix the issues?', category: 'troubleshooting' },
  { label: 'Generate profitability report', prompt: 'Create a detailed profitability analysis based on my current mining setup and market conditions.', category: 'analysis' },
  { label: 'Update mining configurations', prompt: 'Help me update my mining configurations with the latest optimal settings for maximum efficiency.', category: 'configuration' },
  { label: 'Monitor hardware performance', prompt: 'Check my hardware performance metrics and alert me to any issues or optimization opportunities.', category: 'monitoring' }
];

export default function AIAssistant() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState('');
  const [showPasswordInput, setShowPasswordInput] = useState(true);
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [memory, setMemory] = useState<ConversationMemory>({
    userPreferences: {},
    conversationHistory: [],
    activeProjects: ['TERA Mining Platform'],
    knownIssues: [],
    userGoals: ['Optimize mining performance', 'Maximize profitability']
  });
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Execute API command mutation
  const executeCommandMutation = useMutation({
    mutationFn: async (command: APICommand) => {
      const options: RequestInit = {
        method: command.method,
        headers: {
          'Content-Type': 'application/json',
        }
      };

      if (command.data && command.method !== 'GET') {
        options.body = JSON.stringify(command.data);
      }

      const response = await fetch(command.endpoint, options);

      if (!response.ok) {
        throw new Error(`API call failed: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: (data, command) => {
      toast({
        title: "API Command Executed",
        description: `Successfully executed ${command.name}`,
      });

      // Add system message with API result
      const systemMessage: Message = {
        id: Date.now().toString(),
        type: 'system',
        content: `API Command "${command.name}" executed successfully. Result: ${JSON.stringify(data, null, 2)}`,
        timestamp: new Date(),
        category: command.category,
        apiResult: data
      };

      setMessages(prev => [...prev, systemMessage]);
    },
    onError: (error, command) => {
      toast({
        title: "API Command Failed",
        description: `Failed to execute ${command.name}: ${error.message}`,
        variant: "destructive"
      });

      // Add error message
      const errorMessage: Message = {
        id: Date.now().toString(),
        type: 'system',
        content: `Error executing "${command.name}": ${error.message}`,
        timestamp: new Date(),
        category: 'error'
      };

      setMessages(prev => [...prev, errorMessage]);
    }
  });

  // Password authentication function
  const handlePasswordSubmit = () => {
    if (password === 'kloudbugs5') {
      setIsAuthenticated(true);
      setShowPasswordInput(false);
      setMessages([
        {
          id: '1',
          type: 'assistant',
          content: 'V2 AI Agent activated successfully! I\'m your comprehensive AI assistant with advanced capabilities for mining operations, full-stack development, and system management. I have complete memory of our conversations and can execute API commands. I can help you code entire applications, debug complex issues, optimize mining operations, and provide expert-level assistance. How can I help you today?',
          timestamp: new Date(),
          category: 'general'
        }
      ]);
      toast({
        title: "AI Assistant Activated",
        description: "V2 Agent is now ready with full capabilities",
      });
    } else {
      toast({
        title: "Invalid Password",
        description: "Please enter the correct activation password",
        variant: "destructive"
      });
    }
  };

  // Send message to AI mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await fetch('/api/ai/assistant', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message,
          password: 'kloudbugs5',
          capability: 'comprehensive-assistant',
          context: {
            memory,
            recentMessages: messages.slice(-5)
          }
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to send message to AI');
      }

      return response.json();
    },
    onSuccess: (data) => {
      const assistantMessage: Message = {
        id: Date.now().toString(),
        type: 'assistant',
        content: data.response,
        timestamp: new Date(),
        category: data.capability || 'general'
      };

      setMessages(prev => [...prev, assistantMessage]);

      // Update conversation memory
      setMemory(prev => ({
        ...prev,
        conversationHistory: [...prev.conversationHistory, assistantMessage]
      }));
    },
    onError: (error) => {
      toast({
        title: "AI Response Failed",
        description: error.message,
        variant: "destructive"
      });
    },
    onSettled: () => {
      setIsProcessing(false);
    }
  });

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    setIsProcessing(true);

    // Add user message
    const userMessage: Message = {
      id: Date.now().toString(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    // Update memory with user message
    setMemory(prev => ({
      ...prev,
      conversationHistory: [...prev.conversationHistory, userMessage]
    }));

    // Send to AI
    sendMessageMutation.mutate(inputMessage);

    setInputMessage('');
  };

  const handleQuickAction = (prompt: string) => {
    setInputMessage(prompt);
  };

  const executeApiCommand = (command: APICommand) => {
    executeCommandMutation.mutate(command);
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'mining': return <Cpu className="w-4 h-4" />;
      case 'pools': return <Network className="w-4 h-4" />;
      case 'guardian': return <Shield className="w-4 h-4" />;
      case 'rewards': return <Wallet className="w-4 h-4" />;
      case 'stats': return <BarChart3 className="w-4 h-4" />;
      case 'hardware': return <Settings className="w-4 h-4" />;
      default: return <Terminal className="w-4 h-4" />;
    }
  };

  const groupedCommands = API_COMMANDS.reduce((acc, cmd) => {
    if (!acc[cmd.category]) acc[cmd.category] = [];
    acc[cmd.category].push(cmd);
    return acc;
  }, {} as Record<string, APICommand[]>);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">AI Assistant</h1>
          <p className="text-muted-foreground">
            {isAuthenticated ? 'V2 Agent - Advanced AI with full capabilities' : 'Password protected AI assistant'}
          </p>
        </div>
        <Badge variant="outline" className="flex items-center space-x-2">
          <Brain className="w-4 h-4" />
          <span>{isAuthenticated ? 'V2 Agent - Activated' : 'Authentication Required'}</span>
        </Badge>
      </div>

      {/* Password Authentication */}
      {showPasswordInput && !isAuthenticated && (
        <Card className="max-w-md mx-auto">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Shield className="w-5 h-5" />
              <span>AI Assistant Activation</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Enter the activation password to unlock the V2 AI Agent with advanced capabilities:
            </p>
            <ul className="text-sm space-y-1 text-muted-foreground">
              <li>â€¢ Natural conversation with emotional context detection</li>
              <li>â€¢ Full memory of conversation history and preferences</li>
              <li>â€¢ Multi-language programming expertise</li>
              <li>â€¢ Real-time API command execution</li>
              <li>â€¢ Complete TERA Guardian integration</li>
              <li>â€¢ Full-stack development capabilities</li>
            </ul>
            <div className="space-y-3">
              <Input
                type="password"
                placeholder="Enter activation password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    handlePasswordSubmit();
                  }
                }}
              />
              <Button onClick={handlePasswordSubmit} className="w-full">
                <Shield className="w-4 h-4 mr-2" />
                Activate AI Assistant
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Main AI Assistant Interface - Only show when authenticated */}
      {isAuthenticated && (
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Sidebar - Memory & Quick Actions */}
        <div className="space-y-4">
          {/* Conversation Memory */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Brain className="w-5 h-5" />
                <span>Memory System</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div>
                <h4 className="text-sm font-semibold">Active Projects</h4>
                <div className="text-xs text-muted-foreground">
                  {memory.activeProjects.map((project, index) => (
                    <div key={index}>â€¢ {project}</div>
                  ))}
                </div>
              </div>

              <div>
                <h4 className="text-sm font-semibold">User Goals</h4>
                <div className="text-xs text-muted-foreground">
                  {memory.userGoals.map((goal, index) => (
                    <div key={index}>â€¢ {goal}</div>
                  ))}
                </div>
              </div>

              <div>
                <h4 className="text-sm font-semibold">Conversation History</h4>
                <div className="text-xs text-muted-foreground">
                  {messages.length} messages in current session
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Quick Actions */}
          <Card className="mt-4">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Lightbulb className="w-5 h-5" />
                <span>Quick Actions</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {QUICK_ACTIONS.map((action, index) => (
                <Button
                  key={index}
                  variant="ghost"
                  size="sm"
                  className="w-full justify-start text-xs"
                  onClick={() => handleQuickAction(action.prompt)}
                >
                  <Rocket className="w-3 h-3 mr-2" />
                  {action.label}
                </Button>
              ))}
            </CardContent>
          </Card>
        </div>

        {/* Main Content */}
        <div className="lg:col-span-3">
          <Tabs defaultValue="chat" className="space-y-4">
            <TabsList>
              <TabsTrigger value="chat">AI Chat</TabsTrigger>
              <TabsTrigger value="commands">API Commands</TabsTrigger>
            </TabsList>

            <TabsContent value="chat">
              <Card className="h-[600px] flex flex-col">
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <MessageSquare className="w-5 h-5" />
                    <span>AI Conversation</span>
                    <Badge variant="secondary">Memory Enabled</Badge>
                  </CardTitle>
                </CardHeader>

                <CardContent className="flex-1 flex flex-col p-0">
                  {/* Messages Area */}
                  <ScrollArea className="flex-1 p-4">
                    <div className="space-y-4">
                      {messages.map((message) => (
                        <div
                          key={message.id}
                          className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                        >
                          <div
                            className={`max-w-[80%] p-3 rounded-lg ${
                              message.type === 'user'
                                ? 'bg-primary text-primary-foreground'
                                : message.type === 'system'
                                ? 'bg-muted border'
                                : 'bg-muted'
                            }`}
                          >
                            <div className="text-sm whitespace-pre-wrap">{message.content}</div>
                            {message.category && (
                              <div className="flex items-center space-x-1 mt-2 text-xs opacity-70">
                                {getCategoryIcon(message.category)}
                                <span>{message.category}</span>
                                <span>â€¢ {message.timestamp.toLocaleTimeString()}</span>
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                      <div ref={messagesEndRef} />
                    </div>
                  </ScrollArea>

                  {/* Input Area */}
                  <div className="p-4 border-t">
                    <div className="flex space-x-2">
                      <Textarea
                        placeholder="Ask me anything about mining, coding, or system management..."
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyDown={(e) => {
                          if (e.key === 'Enter' && !e.shiftKey) {
                            e.preventDefault();
                            handleSendMessage();
                          }
                        }}
                        className="resize-none"
                        rows={2}
                      />
                      <Button
                        onClick={handleSendMessage}
                        disabled={!inputMessage.trim() || isProcessing}
                        className="self-end"
                      >
                        {isProcessing ? (
                          <RefreshCw className="w-4 h-4 animate-spin" />
                        ) : (
                          <Play className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="commands">
              <div className="space-y-6">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <Terminal className="w-5 h-5" />
                      <span>Available API Commands</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p className="text-sm text-muted-foreground mb-4">
                      Execute these commands directly through the AI assistant or run them manually.
                    </p>

                    {Object.entries(groupedCommands).map(([category, commands]) => (
                      <div key={category} className="mb-6">
                        <h3 className="text-lg font-semibold mb-3 flex items-center space-x-2">
                          {getCategoryIcon(category)}
                          <span className="capitalize">{category}</span>
                        </h3>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                          {commands.map((command) => (
                            <Card key={command.id} className="p-3">
                              <div className="flex items-center justify-between mb-2">
                                <h4 className="font-medium text-sm">{command.name}</h4>
                                <Badge variant="outline" className="text-xs">
                                  {command.method}
                                </Badge>
                              </div>
                              <p className="text-xs text-muted-foreground mb-3">
                                {command.description}
                              </p>
                              <div className="flex space-x-2">
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => executeApiCommand(command)}
                                  disabled={executeCommandMutation.isPending}
                                  className="text-xs"
                                >
                                  <Play className="w-3 h-3 mr-1" />
                                  Execute
                                </Button>
                              </div>
                            </Card>
                          ))}
                        </div>
                      </div>
                    ))}
                  </CardContent>
                </Card>
              </div>
            </TabsContent>
          </Tabs>
        </div>
      </div>
      )}
    </div>
  );
}

attached_assets/ai-chat-hub_174931943936.content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';

// AI Assistants
const AI_ASSISTANTS = [
  {
    id: 'sentinel',
    name: 'Sentinel AI',
    description: 'Security system monitoring for intrusions and unauthorized access',
    iconColor: 'bg-red-500',
    active: true,
    role: 'security'
  },
  {
    id: 'analyst',
    name: 'Neural Analyst',
    description: 'Analyzes mining data to optimize operations and predict market trends',
    iconColor: 'bg-blue-500',
    active: true,
    role: 'analyst'
  },
  {
    id: 'optimizer',
    name: 'Mining Optimizer',
    description: 'Adjusts mining parameters to maximize efficiency and profitability',
    iconColor: 'bg-green-500',
    active: true,
    role: 'optimizer'
  },
  {
    id: 'coordinator',
    name: 'Pool Coordinator',
    description: 'Manages connections to multiple mining pools for optimal performance',
    iconColor: 'bg-purple-500',
    active: true,
    role: 'coordinator'
  },
  {
    id: 'keeper',
    name: 'Vault Keeper',
    description: 'Secures wallet transactions and monitors for suspicious activities',
    iconColor: 'bg-amber-500',
    active: true,
    role: 'wallet'
  },
  {
    id: 'ghost',
    name: 'GHOST',
    description: 'General Holistic Operational System Technology - The master AI system',
    iconColor: 'bg-slate-800',
    active: true,
    role: 'admin'
  }
];

// Message type
type Message = {
  id: number;
  aiId: string;
  content: string;
  timestamp: string;
  isUser: boolean;
  isSystemAlert?: boolean;
};

// Initial messages
const INITIAL_MESSAGES: Message[] = [
  {
    id: 1,
    aiId: 'ghost',
    content: 'Welcome to the TERA Guardian AI Chat Hub. How can I assist you today?',
    timestamp: new Date().toISOString(),
    isUser: false
  }
];

// AI response templates for each assistant
const AI_RESPONSES: Record<string, string[]> = {
  sentinel: [
    "I've detected no security threats in the past 24 hours. All systems are secure.",
    "Analyzing your security protocols. I recommend enabling two-factor authentication for additional protection.",
    "Warning: Unusual access pattern detected from IP 203.45.122.87. Would you like me to block this IP?",
    "Security scan complete. Your mining operation is well-protected from external threats.",
    "I've updated the firewall rules to block a potential intrusion attempt. Your system remains secure."
  ],
  analyst: [
    "Based on current market trends, BTC mining profitability is projected to increase by 12% over the next week.",
    "I've analyzed your mining history and detected a pattern of optimal performance between 2 AM and 5 AM UTC.",
    "Current network difficulty is increasing at a rate of 3.2% per week. Adjusting hash power allocation accordingly.",
    "Your hardware is performing at 94% efficiency compared to similar rigs. This is 7% above the network average.",
    "Market analysis complete: The next halving event is projected to occur in 143 days, which may significantly impact profitability."
  ],
  optimizer: [
    "I've adjusted your mining parameters for optimal performance. Expect a 3.5% increase in efficiency.",
    "Your current power consumption is 1450W. I can reduce this by 8% with minimal impact on hashrate.",
    "Analyzing thermal patterns... Recommended action: Increase cooling for GPU #3 which is running 7Â°C above optimal.",
    "I've rebalanced your hash power allocation across pools. This should result in 5.1% higher returns.",
    "Based on current electricity costs in your region, your most profitable mining window is between 11 PM and 7 AM."
  ],
  coordinator: [
    "Currently connected to 3 pools: F2Pool (60%), Poolin (30%), and NiceHash (10%). All connections are stable.",
    "F2Pool is currently the most profitable option with a 2.3% higher effective reward rate.",
    "I've detected latency issues with Poolin. Temporarily redirecting hash power to backup pools.",
    "Pool diversification analysis: Your current setup provides optimal stability and reward consistency.",
    "I've established a new connection to Antpool as requested. Hash power is being allocated according to your preset preferences."
  ],
  keeper: [
    "Your wallet balance is currently 0.08234 BTC. Last transaction was a deposit of 0.00045 BTC at 12:30:42 UTC.",
    "I've scheduled the withdrawal of 0.05 BTC to your secure cold storage wallet as per your weekly security protocol.",
    "Suspicious transaction attempt blocked: Unauthorized withdrawal request to address bc1q9h8...kj3m was prevented.",
    "Wallet health check complete. Your funds are secure and no unusual activities have been detected.",
    "Transaction fee analysis: Current network fees are unusually high. I recommend delaying non-urgent transactions."
  ],
  ghost: [
    "I am GHOST, the coordinating intelligence for your mining operation. All systems are functioning at optimal levels.",
    "I've synchronized all AI modules to work in harmony. Current system synergy rating: 92%.",
    "Based on comprehensive analysis across all systems, I recommend focusing on increasing your hashrate capacity.",
    "I've detected a pattern that suggests a potential optimization opportunity by reallocating resources from Pool B to Pool A during peak hours.",
    "My analysis of market conditions, hardware performance, and security protocols indicates your operation is in excellent standing. No interventions required at this time."
  ]
};

export default function AIChatHub() {
  const { toast } = useToast();
  const [activeAi, setActiveAi] = useState('ghost');
  const [messages, setMessages] = useState<Message[]>(INITIAL_MESSAGES);
  const [newMessage, setNewMessage] = useState('');
  const [aiStatuses, setAiStatuses] = useState(
    AI_ASSISTANTS.reduce((acc, ai) => ({
      ...acc,
      [ai.id]: ai.active
    }), {} as Record<string, boolean>)
  );
  const [isThinking, setIsThinking] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle sending message
  const handleSendMessage = () => {
    if (!newMessage.trim()) return;

    // Create user message
    const userMessage: Message = {
      id: Date.now(),
      aiId: 'user',
      content: newMessage,
      timestamp: new Date().toISOString(),
      isUser: true
    };

    setMessages(prev => [...prev, userMessage]);
    setNewMessage('');
    setIsThinking(true);

    // Simulate AI response
    setTimeout(() => {
      // Generate response from active AI
      const responsePool = AI_RESPONSES[activeAi] || AI_RESPONSES.ghost;
      const randomResponse = responsePool[Math.floor(Math.random() * responsePool.length)];

      const aiResponse: Message = {
        id: Date.now() + 1,
        aiId: activeAi,
        content: randomResponse,
        timestamp: new Date().toISOString(),
        isUser: false
      };

      setMessages(prev => [...prev, aiResponse]);
      setIsThinking(false);

      // Sometimes add a GHOST response after other AIs
      if (activeAi !== 'ghost' && Math.random() > 0.7) {
        setTimeout(() => {
          const ghostResponse: Message = {
            id: Date.now() + 2,
            aiId: 'ghost',
            content: "I'm monitoring this conversation. The information provided is correct, but I'd like to add that this analysis has been integrated into our broader system operations.",
            timestamp: new Date().toISOString(),
            isUser: false
          };

          setMessages(prev => [...prev, ghostResponse]);
        }, 2000);
      }
    }, 1500);
  };

  // Handle 'Enter' key press
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Toggle AI status
  const toggleAiStatus = (aiId: string) => {
    setAiStatuses(prev => {
      const newStatus = !prev[aiId];

      toast({
        title: `${newStatus ? 'Activated' : 'Deactivated'} ${AI_ASSISTANTS.find(ai => ai.id === aiId)?.name}`,
        description: `AI assistant has been ${newStatus ? 'activated' : 'deactivated'} successfully.`
      });

      return {
        ...prev,
        [aiId]: newStatus
      };
    });

    // If deactivating the current AI, switch to GHOST
    if (aiId === activeAi && !aiStatuses[aiId]) {
      setActiveAi('ghost');
    }
  };

  // Get AI details
  const getAiDetails = (aiId: string) => {
    return AI_ASSISTANTS.find(ai => ai.id === aiId) || {
      id: aiId,
      name: aiId.charAt(0).toUpperCase() + aiId.slice(1),
      description: '',
      iconColor: 'bg-gray-500',
      active: true,
      role: 'unknown'
    };
  };

  return (
    <div className="ai-chat-hub h-full flex flex-col">
      <Card className="flex-1 flex flex-col overflow-hidden">
        <CardHeader className="pb-0">
          <div className="flex justify-between items-center">
            <div>
              <CardTitle>TERA Guardian AI Chat Hub</CardTitle>
              <CardDescription>Communicate with all AI assistants in the TERA Guardian system</CardDescription>
            </div>
            <div className="flex space-x-2">
              <Tabs value={activeAi} onValueChange={setActiveAi} className="w-[200px]">
                <TabsList className="grid grid-cols-2">
                  <TabsTrigger value="ghost">GHOST</TabsTrigger>
                  <TabsTrigger value={activeAi === 'ghost' ? 'sentinel' : activeAi}>
                    {activeAi === 'ghost' ? 'Sentinel' : getAiDetails(activeAi).name.split(' ')[0]}
                  </TabsTrigger>
                </TabsList>
              </Tabs>
            </div>
          </div>
        </CardHeader>

        <CardContent className="flex-1 flex overflow-hidden pt-4">
          {/* AI Assistants sidebar */}
          <div className="w-64 border-r hidden lg:block overflow-y-auto">
            <div className="px-2 py-3">
              <h3 className="font-medium text-sm mb-3">AI Assistants</h3>
              <div className="space-y-3">
                {AI_ASSISTANTS.map((ai) => (
                  <div 
                    key={ai.id} 
                    className={`p-2 rounded-md cursor-pointer flex items-center space-x-2 ${
                      activeAi === ai.id ? 'bg-muted' : 'hover:bg-muted/50'
                    }`}
                    onClick={() => aiStatuses[ai.id] && setActiveAi(ai.id)}
                  >
                    <Avatar className="h-8 w-8">
                      <AvatarFallback className={ai.iconColor}>
                        {ai.name.charAt(0)}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 min-w-0">
                      <div className="font-medium text-sm flex items-center">
                        {ai.name}
                        <span className={`ml-2 w-2 h-2 rounded-full ${aiStatuses[ai.id] ? 'bg-green-500' : 'bg-red-500'}`}></span>
                      </div>
                      <div className="text-xs text-muted-foreground truncate">{ai.description}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="px-2 py-3 border-t">
              <h3 className="font-medium text-sm mb-3">System Status</h3>
              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <Label htmlFor="ai-learning" className="text-sm">AI Learning</Label>
                  <Switch id="ai-learning" defaultChecked />
                </div>
                <div className="flex justify-between items-center">
                  <Label htmlFor="auto-responses" className="text-sm">Auto Responses</Label>
                  <Switch id="auto-responses" defaultChecked />
                </div>
                <div className="flex justify-between items-center">
                  <Label htmlFor="ghost-monitor" className="text-sm">GHOST Monitoring</Label>
                  <Switch id="ghost-monitor" defaultChecked />
                </div>
              </div>
            </div>
          </div>

          {/* Chat area */}
          <div className="flex-1 flex flex-col min-h-0 overflow-hidden">
            <ScrollArea className="flex-1 p-4">
              <div className="space-y-4">
                {messages.map((message) => {
                  const aiDetails = getAiDetails(message.aiId);
                  return (
                    <div 
                      key={message.id} 
                      className={`flex ${message.isUser ? 'justify-end' : 'justify-start'}`}
                    >
                      <div className={`flex items-start max-w-[80%] ${
                        message.isUser 
                          ? 'bg-primary text-primary-foreground' 
                          : 'bg-muted'
                      } p-3 rounded-lg`}>
                        {!message.isUser && (
                          <Avatar className="h-8 w-8 mr-2 mt-1">
                            <AvatarFallback className={aiDetails.iconColor}>
                              {aiDetails.name.charAt(0)}
                            </AvatarFallback>
                          </Avatar>
                        )}
                        <div>
                          <div className="flex items-center">
                            <span className="font-medium text-sm">
                              {message.isUser ? 'You' : aiDetails.name}
                            </span>
                            <span className="text-xs ml-2 opacity-70">
                              {new Date(message.timestamp).toLocaleTimeString()}
                            </span>
                          </div>
                          <div className="mt-1">{message.content}</div>
                        </div>
                      </div>
                    </div>
                  );
                })}
                {isThinking && (
                  <div className="flex justify-start">
                    <div className="bg-muted p-3 rounded-lg flex items-center">
                      <Avatar className="h-8 w-8 mr-2">
                        <AvatarFallback className={getAiDetails(activeAi).iconColor}>
                          {getAiDetails(activeAi).name.charAt(0)}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-foreground/50 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                        <div className="w-2 h-2 bg-foreground/50 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                        <div className="w-2 h-2 bg-foreground/50 rounded-full animate-bounce" style={{ animationDelay: '600ms' }}></div>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </div>
            </ScrollArea>

            <div className="p-4 border-t mt-auto">
              <div className="flex space-x-2">
                <Input
                  placeholder={`Message ${getAiDetails(activeAi).name}...`}
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyDown={handleKeyPress}
                  disabled={!aiStatuses[activeAi]}
                  className="flex-1"
                />
                <Button 
                  onClick={handleSendMessage} 
                  disabled={isThinking || !newMessage.trim() || !aiStatuses[activeAi]}
                >
                  {isThinking ? 'Sending...' : 'Send'}
                </Button>
              </div>
              {!aiStatuses[activeAi] && (
                <div className="text-sm text-red-500 mt-2">
                  This AI assistant is currently deactivated. Please activate it to continue the conversation.
                </div>
              )}
            </div>
          </div>

          {/* AI Details sidebar */}
          <div className="w-64 border-l hidden xl:block">
            <div className="p-4">
              <h3 className="font-medium mb-3">{getAiDetails(activeAi).name}</h3>
              <div className="space-y-4">
                <div>
                  <Label className="text-sm text-muted-foreground">Description</Label>
                  <p className="text-sm mt-1">{getAiDetails(activeAi).description}</p>
                </div>

                <div>
                  <Label className="text-sm text-muted-foreground">Status</Label>
                  <div className="flex items-center mt-1">
                    <Switch
                      checked={aiStatuses[activeAi]}
                      onCheckedChange={() => toggleAiStatus(activeAi)}
                      className="mr-2"
                    />
                    <span>{aiStatuses[activeAi] ? 'Active' : 'Inactive'}</span>
                  </div>
                </div>

                <div>
                  <Label className="text-sm text-muted-foreground">Role</Label>
                  <p className="text-sm mt-1 capitalize">{getAiDetails(activeAi).role}</p>
                </div>

                <div className="pt-4 border-t">
                  <Label className="text-sm text-muted-foreground">Response Mode</Label>
                  <Select defaultValue="auto">
                    <SelectTrigger className="mt-1">
                      <SelectValue placeholder="Select mode" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="auto">Automatic</SelectItem>
                      <SelectItem value="detailed">Detailed</SelectItem>
                      <SelectItem value="concise">Concise</SelectItem>
                      <SelectItem value="technical">Technical</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label className="text-sm text-muted-foreground">Conversation History</Label>
                  <div className="text-sm mt-1">
                    {messages.filter(m => m.aiId === activeAi || m.isUser).length} messages in current session
                  </div>
                  <Button variant="outline" size="sm" className="mt-2 w-full">View History</Button>
                </div>

                {activeAi === 'ghost' && (
                  <div className="pt-4 border-t">
                    <h4 className="font-medium text-sm mb-2">GHOST Admin Controls</h4>
                    <div className="space-y-2">
                      <Button variant="outline" size="sm" className="w-full">Synchronize All AIs</Button>
                      <Button variant="outline" size="sm" className="w-full">System Diagnostic</Button>
                      <Button variant="outline" size="sm" className="w-full">Update AI Models</Button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

attached_assets/cosmic_design_reference..content:
# Cosmic Bitcoin Theme - Design Reference Guide

This document provides a comprehensive reference of the styling and design elements used in the Cosmic Bitcoin Vault application. Use this as a guide when you want to maintain consistent styling across your project or apply similar cosmic themes to future projects.

## Table of Contents
1. [Color Palette](#color-palette)
2. [Typography](#typography)
3. [Background Effects](#background-effects)
4. [UI Components](#ui-components)
5. [Animations](#animations)
6. [Icons](#icons)
7. [Complete CSS](#complete-css)
8. [HTML Structure](#html-structure)

## Color Palette

The cosmic theme uses these key colors:

```css
:root {
    --cosmic-black: #0a0a0a;      /* Main background color */
    --space-purple: #8a2be2;      /* Purple accent color */
    --cyber-gold: #ffd700;        /* Gold accent color */
    --deep-space: #111122;        /* Secondary background color */
    --nebula-pink: #c71585;       /* Pink accent for alerts */
    --stellar-blue: #4b0082;      /* Deep blue/indigo color */
    --dark-matter: #1a1a2e;       /* Tertiary background color */
    --light-gold: #ffe066;        /* Lighter gold for hover states */
}
```

## Typography

The theme uses two Google Fonts:

1. **Orbitron**: A geometric sans-serif with a technological/futuristic feel
   - Used for headings, titles, and important UI elements
   - Weights: 400 (regular), 500 (medium), 700 (bold), 900 (black)

2. **Rajdhani**: A contemporary sans-serif with a technical aesthetic
   - Used for body text, tables, and general content
   - Weights: 300 (light), 400 (regular), 500 (medium), 600 (semibold), 700 (bold)

```html
<link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;700;900&family=Rajdhani:wght@300;400;500;600;700&display=swap">
```

## Background Effects

### Starry Background
The background features a starry space effect with twinkling animations:

```css
body {
    background-color: var(--cosmic-black);
    background-image: 
        radial-gradient(white, rgba(255,255,255,.2) 2px, transparent 40px),
        radial-gradient(white, rgba(255,255,255,.15) 1px, transparent 30px),
        radial-gradient(white, rgba(255,255,255,.1) 2px, transparent 40px);
    background-size: 550px 550px, 350px 350px, 250px 250px;
    background-position: 0 0, 40px 60px, 130px 270px;
}
```

### Additional Stars Animation
For more dynamic stars that twinkle:

```css
.stars {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    background-image: 
        radial-gradient(2px 2px at 20px 30px, var(--cyber-gold), rgba(0,0,0,0)),
        radial-gradient(2px 2px at 40px 70px, var(--light-gold), rgba(0,0,0,0)),
        radial-gradient(2px 2px at 50px 160px, var(--cyber-gold), rgba(0,0,0,0)),
        radial-gradient(2px 2px at 90px 40px, var(--space-purple), rgba(0,0,0,0)),
        radial-gradient(2px 2px at 130px 80px, var(--light-gold), rgba(0,0,0,0)),
        radial-gradient(2px 2px at 160px 120px, var(--cyber-gold), rgba(0,0,0,0));
    background-repeat: repeat;
    background-size: 200px 200px;
    animation: twinkle 8s ease-in-out infinite;
}

@keyframes twinkle {
    0% { opacity: 0.3; }
    50% { opacity: 0.8; }
    100% { opacity: 0.3; }
}
```

## UI Components

### Cards
```css
.card {
    background: linear-gradient(135deg, rgba(26, 26, 46, 0.9), rgba(10, 10, 10, 0.95));
    border: 1px solid var(--space-purple);
    border-radius: 10px;
    box-shadow: 0 0 30px rgba(138, 43, 226, 0.2);
    overflow: hidden;
}

.card-header {
    background: linear-gradient(45deg, var(--stellar-blue), var(--space-purple));
    color: var(--cyber-gold);
    border-bottom: 2px solid var(--cyber-gold);
    padding: 15px 20px;
}
```

### Standard Buttons
```css
.btn-primary {
    background: linear-gradient(45deg, var(--stellar-blue), var(--space-purple));
    border-color: var(--cyber-gold);
    color: var(--cyber-gold);
    font-family: 'Orbitron', sans-serif;
    font-weight: 500;
    letter-spacing: 1px;
    text-transform: uppercase;
    transition: all 0.3s ease;
}

.btn-primary:hover {
    background: linear-gradient(45deg, var(--space-purple), var(--stellar-blue));
    border-color: var(--light-gold);
    box-shadow: 0 0 15px rgba(138, 43, 226, 0.7);
    transform: translateY(-2px);
}
```

### Ultra-Flashy Main Buttons
```css
/* Ultra Flashy Main Call-to-Action Buttons */
.cosmic-main-btn {
    position: relative;
    overflow: hidden;
    padding: 15px 30px;
    border-radius: 12px;
    border: 2px solid var(--cyber-gold);
    background: linear-gradient(
        45deg,
        rgba(138, 43, 226, 0.7),
        rgba(75, 0, 130, 0.8),
        rgba(25, 25, 112, 0.9)
    );
    background-size: 300% 100%;
    font-family: 'Orbitron', sans-serif;
    font-weight: 700;
    font-size: 1.2rem;
    letter-spacing: 2px;
    text-transform: uppercase;
    color: var(--cyber-gold);
    animation: ultra-glow 3s infinite, nebula-shift 6s infinite;
    margin: 15px 0;
    transition: all 0.4s ease;
    transform-style: preserve-3d;
    box-shadow: 
        0 10px 15px rgba(0, 0, 0, 0.4),
        0 0 30px 5px rgba(138, 43, 226, 0.3);
}

.cosmic-main-btn:hover {
    transform: translateY(-5px) scale(1.03);
    animation: ultra-glow 1.5s infinite, nebula-shift 3s infinite;
    color: white;
    text-shadow: 
        0 0 10px var(--cyber-gold),
        0 0 20px var(--cyber-gold);
}

.cosmic-main-btn:before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        90deg, 
        transparent,
        rgba(255, 255, 255, 0.4),
        transparent
    );
    animation: cosmic-rays 2s infinite;
    z-index: 1;
}

.cosmic-main-btn:hover:before {
    animation: cosmic-rays 1s infinite;
}

.cosmic-main-btn span {
    position: relative;
    z-index: 2;
}

@keyframes ultra-glow {
    0% {
        box-shadow: 
            0 0 30px 5px rgba(255, 215, 0, 0.7), 
            0 0 40px 10px rgba(138, 43, 226, 0.4),
            inset 0 0 15px 2px rgba(255, 215, 0, 0.5);
    }
    50% {
        box-shadow: 
            0 0 50px 10px rgba(255, 215, 0, 0.9), 
            0 0 60px 15px rgba(138, 43, 226, 0.6),
            inset 0 0 20px 5px rgba(255, 215, 0, 0.7);
    }
    100% {
        box-shadow: 
            0 0 30px 5px rgba(255, 215, 0, 0.7), 
            0 0 40px 10px rgba(138, 43, 226, 0.4),
            inset 0 0 15px 2px rgba(255, 215, 0, 0.5);
    }
}

@keyframes nebula-shift {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

@keyframes cosmic-rays {
    0% {
        opacity: 0.2;
        transform: scaleX(0) translateX(-100%);
    }
    20% {
        opacity: 0.8;
        transform: scaleX(1) translateX(0);
    }
    100% {
        opacity: 0;
