  app.get('/api/radio/genres', (req, res) => {
    const fs = require('fs');
    const path = require('path');

    const genresPath = path.join(__dirname, '../client/public/audio/genres');
    const genres: any[] = [];

    const genreList = [
      { id: 'rock', name: 'Rock', icon: 'ðŸŽ¸' },
      { id: 'pop', name: 'Pop', icon: 'ðŸŽ¤' },
      { id: 'electronic', name: 'Electronic', icon: 'ðŸŽ›ï¸' },
      { id: 'jazz', name: 'Jazz', icon: 'ðŸŽ·' },
      { id: 'classical', name: 'Classical', icon: 'ðŸŽ¼' },
      { id: 'hip-hop', name: 'Hip-Hop', icon: 'ðŸŽ¤' },
      { id: 'country', name: 'Country', icon: 'ðŸ¤ ' },
      { id: 'blues', name: 'Blues', icon: 'ðŸŽº' },
      { id: 'reggae', name: 'Reggae', icon: 'ðŸŒ´' },
      { id: 'folk', name: 'Folk', icon: 'ðŸª•' }
    ];

    try {
      genreList.forEach(genre => {
        const genrePath = path.join(genresPath, genre.id);
        let trackCount = 0;
        let tracks: any[] = [];

        if (fs.existsSync(genrePath)) {
          const files = fs.readdirSync(genrePath);
          tracks = files
            .filter((file: string) => file.endsWith('.mp3') || file.endsWith('.wav') || file.endsWith('.ogg'))
            .map((file: string) => ({
              id: `${genre.id}-${file.replace(/\.[^/.]+$/, "")}`,
              title: file.replace(/\.[^/.]+$/, "").replace(/-/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase()),
              filename: file,
              url: `/audio/genres/${genre.id}/${file}`,
              genre: genre.id
            }));
          trackCount = tracks.length;
        }

        genres.push({
          ...genre,
          trackCount,
          tracks,
          folder: `/audio/genres/${genre.id}`
        });
      });

      res.json(genres);
    } catch (error) {
      console.error('Error scanning music genres:', error);
      res.json(genreList.map(genre => ({ ...genre, trackCount: 0, tracks: [], folder: `/audio/genres/${genre.id}` })));
    }
  });

