            lastActivity: w.lastActivity
          }))
        });
      }

      if (teraSocialJusticePool) {
        const stats = teraSocialJusticePool.getStats();
        const workers = teraSocialJusticePool.getWorkers();
        pools.push({
          ...stats,
          workers: workers.map(w => ({
            id: w.id,
            username: w.username,
            hashRate: w.hashRate,
            sharesAccepted: w.sharesAccepted,
            sharesRejected: w.sharesRejected,
            connected: w.connected,
            lastActivity: w.lastActivity
          }))
        });
      }

      res.json({ pools, totalPools: pools.length });
    } catch (error) {
      console.error('Error fetching pool status:', error);
      res.status(500).json({ error: 'Failed to fetch pool status' });
    }
  });

  app.post('/api/pools/start', async (req, res) => {
    try {
      console.log('Starting independent mining pools...');

      // Start KLOUDBUGSCAFE Pool on port 4001
      kloudbugsCafePool = new MiningPool(
        'KLOUDBUGSCAFE POOL',
        4001,
        'bc1qkloudbugs_cafe_pool_address_here',
        1.5 // 1.5% fee
      );

      // Start TERA Social Justice Pool on port 4002
      teraSocialJusticePool = new MiningPool(
        'TERA SOCIAL JUSTICE POOL',
        4002,
        'bc1qtera_social_justice_pool_address_here',
        1.0 // 1.0% fee
      );

      // Set up event handlers for real-time updates
      const setupPoolEvents = (pool: MiningPool, poolName: string) => {
        pool.on('workerConnected', (worker) => {
          console.log(`[${poolName}] Worker connected: ${worker.username}`);
          // Broadcast to WebSocket clients
          clients.forEach(client => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({
                type: 'pool:worker_connected',
                data: { poolName, worker: { username: worker.username, id: worker.id } },
                timestamp: Date.now()
              }));
            }
          });
        });

        pool.on('workerDisconnected', (worker) => {
          console.log(`[${poolName}] Worker disconnected: ${worker.username}`);
          clients.forEach(client => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({
                type: 'pool:worker_disconnected',
                data: { poolName, workerId: worker.id },
                timestamp: Date.now()
              }));
            }
          });
        });

        pool.on('shareAccepted', ({ worker, isBlock }) => {
          console.log(`[${poolName}] Share accepted from ${worker.username}${isBlock ? ' - BLOCK FOUND!' : ''}`);
          clients.forEach(client => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({
                type: isBlock ? 'pool:block_found' : 'pool:share_accepted',
                data: { poolName, worker: worker.username, isBlock },
                timestamp: Date.now()
              }));
            }
          });
        });

        pool.on('blockFound', ({ worker, blockHeight }) => {
          console.log(`[${poolName}] BLOCK ${blockHeight} FOUND by ${worker.username}!!!`);
          // Auto-withdraw logic would go here
          setTimeout(() => {
            console.log(`[${poolName}] Auto-withdrawal initiated for block ${blockHeight}`);
          }, 5000);
        });
      };

      setupPoolEvents(kloudbugsCafePool, 'KLOUDBUGSCAFE');
      setupPoolEvents(teraSocialJusticePool, 'TERA SOCIAL JUSTICE');

      // Start both pools
      await Promise.all([
        kloudbugsCafePool.start(),
        teraSocialJusticePool.start()
      ]);

      console.log('âœ“ Both mining pools are now live and ready for connections:');
      console.log('  - KLOUDBUGSCAFE POOL: stratum+tcp://0.0.0.0:4001');
      console.log('  - TERA SOCIAL JUSTICE POOL: stratum+tcp://0.0.0.0:4002');
      console.log('âœ“ Pools can operate completely offline and independently');
      console.log('âœ“ Auto-withdrawal to wallet addresses enabled');

      res.json({
        success: true,
        message: 'Mining pools started successfully',
        pools: [
          { name: 'KLOUDBUGSCAFE POOL', port: 4001, protocol: 'stratum+tcp', status: 'running' },
          { name: 'TERA SOCIAL JUSTICE POOL', port: 4002, protocol: 'stratum+tcp', status: 'running' }
        ]
      });
    } catch (error) {
      console.error('Error starting mining pools:', error);
      res.status(500).json({ error: 'Failed to start mining pools' });
    }
  });

  app.post('/api/pools/stop', async (req, res) => {
    try {
      console.log('Stopping mining pools...');

      const promises = [];
      if (kloudbugsCafePool) {
        promises.push(kloudbugsCafePool.stop());
      }
      if (teraSocialJusticePool) {
        promises.push(teraSocialJusticePool.stop());
      }

      await Promise.all(promises);

      kloudbugsCafePool = null;
      teraSocialJusticePool = null;

      console.log('âœ“ All mining pools stopped');

      res.json({
        success: true,
        message: 'Mining pools stopped successfully'
      });
    } catch (error) {
      console.error('Error stopping mining pools:', error);
      res.status(500).json({ error: 'Failed to stop mining pools' });
    }
  });

  app.post('/api/pools/connect-miner', async (req, res) => {
    try {
      const { minerId, poolName, walletAddress } = req.body;

      // Configure miner to connect to specified pool
      const poolPort = poolName === 'KLOUDBUGSCAFE' ? 4001 : 4002;
      const stratumUrl = `stratum+tcp://127.0.0.1:${poolPort}`;

      console.log(`Connecting miner ${minerId} to ${poolName} pool at ${stratumUrl}`);
      console.log(`Miner wallet address: ${walletAddress}`);

      // This would typically configure the actual miner software
      // For now, we'll simulate the connection
      setTimeout(() => {
        console.log(`Miner ${minerId} successfully connected to ${poolName} pool`);
      }, 2000);

      res.json({
        success: true,
        message: `Miner ${minerId} connected to ${poolName} pool`,
        connection: {
          minerId,
          poolName,
          stratumUrl,
          walletAddress,
          status: 'connecting'
        }
      });
    } catch (error) {
      console.error('Error connecting miner to pool:', error);
      res.status(500).json({ error: 'Failed to connect miner to pool' });
    }
  });

  app.post('/api/pools/auto-withdraw', async (req, res) => {
    try {
      const { poolName, walletAddress, threshold } = req.body;

      console.log(`Setting up auto-withdrawal for ${poolName} pool:`);
      console.log(`  - Wallet: ${walletAddress}`);
      console.log(`  - Threshold: ${threshold} BTC`);

      // This would typically configure auto-withdrawal settings
      // In a real implementation, this would:
      // 1. Verify wallet address
      // 2. Set withdrawal threshold
      // 3. Configure automatic transfers

      res.json({
        success: true,
        message: `Auto-withdrawal configured for ${poolName} pool`,
        config: {
          poolName,
          walletAddress,
          threshold,
          enabled: true,
          nextCheck: new Date(Date.now() + 3600000).toISOString() // 1 hour
        }
      });
    } catch (error) {
      console.error('Error configuring auto-withdrawal:', error);
      res.status(500).json({ error: 'Failed to configure auto-withdrawal' });
    }
  });

  // Enhanced mining rig endpoints for pool connectivity
  app.post('/api/mining/connect-to-pool', async (req, res) => {
    try {
      const { rigIds, poolName } = req.body;
      console.log(`Connecting ${rigIds.length} mining rigs to ${poolName} pool`);

      const results = rigIds.map((rigId: string) => {
        const poolPort = poolName === 'KLOUDBUGSCAFE' ? 4001 : 4002;
        console.log(`Configuring rig ${rigId} for stratum+tcp://127.0.0.1:${poolPort}`);

        return {
          rigId,
          poolName,
          stratumUrl: `stratum+tcp://127.0.0.1:${poolPort}`,
          status: 'connected',
          hashRate: Math.floor(Math.random() * 100) + 50
        };
      });

      res.json({
        success: true,
        message: `${rigIds.length} rigs connected to ${poolName} pool`,
        connections: results
      });
    } catch (error) {
      console.error('Error connecting rigs to pool:', error);
      res.status(500).json({ error: 'Failed to connect rigs to pool' });
    }
  });

  // Python miner pool connectivity
  app.post('/api/webmining/connect-python-to-pool', async (req, res) => {
    try {
      const { minerId, poolName, algorithm } = req.body;
      const poolPort = poolName === 'KLOUDBUGSCAFE' ? 4001 : 4002;

      console.log(`Connecting Python miner ${minerId} to ${poolName} pool`);
      console.log(`Algorithm: ${algorithm}, Port: ${poolPort}`);
      console.log('Generating Stratum connection script...');

      // Generate Python Stratum client code
      const pythonStratumCode = `
import socket
import json
import hashlib
import time
import threading

class StratumClient:
    def __init__(self, pool_host='127.0.0.1', pool_port=${poolPort}):
        self.host = pool_host
        self.port = pool_port
        self.socket = None
        self.connected = False
        self.job_id = None
        self.extranonce1 = None
        self.extranonce2_size = None
        self.difficulty = 1

    def connect(self):
        try:
            self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.socket.connect((self.host, self.port))
            self.connected = True
            print(f"Connected to ${poolName} pool at {self.host}:{self.port}")

            # Subscribe to mining
            self.send_message("mining.subscribe", ["${minerId}", None, "localhost", "${poolPort}"])

            # Authorize worker
            self.send_message("mining.authorize", ["${minerId}", "x"])

            # Start listening for messages
            listen_thread = threading.Thread(target=self.listen)
            listen_thread.daemon = True
            listen_thread.start()

        except Exception as e:
            print(f"Connection failed: {e}")
            self.connected = False

    def send_message(self, method, params):
        message = {
            "id": int(time.time()),
            "method": method,
            "params": params
        }
        message_str = json.dumps(message) + "\\n"
        self.socket.send(message_str.encode())

    def listen(self):
        buffer = ""
        while self.connected:
            try:
                data = self.socket.recv(1024).decode()
                buffer += data

                while "\\n" in buffer:
                    line, buffer = buffer.split("\\n", 1)
                    if line.strip():
                        self.handle_message(json.loads(line))

            except Exception as e:
                print(f"Listen error: {e}")
                break

    def handle_message(self, message):
        if message.get("method") == "mining.notify":
            self.job_id = message["params"][0]
            print(f"New job received: {self.job_id}")
            # Start mining with new job
            threading.Thread(target=self.mine, daemon=True).start()

    def mine(self):
        # Simple mining simulation for ${algorithm}
        print(f"Mining with ${algorithm} algorithm...")
        while self.connected and self.job_id:
            nonce = hex(int(time.time() * 1000000) % 0xffffffff)[2:]

            # Submit share (simplified)
            self.send_message("mining.submit", [
                "${minerId}",
                self.job_id,
                "00000000",  # extranonce2
                hex(int(time.time()))[2:],  # ntime
                nonce
            ])

            time.sleep(1)  # Adjust based on desired hash rate

# Start mining
if __name__ == "__main__":
    miner = StratumClient()
    miner.connect()

    try:
        while miner.connected:
            time.sleep(1)
    except KeyboardInterrupt:
        print("Mining stopped by user")
        miner.connected = False
`;

      res.json({
        success: true,
        message: `Python miner ${minerId} configured for ${poolName} pool`,
        connection: {
          minerId,
          poolName,
          stratumUrl: `stratum+tcp://127.0.0.1:${poolPort}`,
          algorithm,
          pythonCode: pythonStratumCode
        }
      });
    } catch (error) {
      console.error('Error connecting Python miner to pool:', error);
      res.status(500).json({ error: 'Failed to connect Python miner to pool' });
    }
  });

  // Initialize AI Manager and trading bot on server start
  aiManager.initialize().catch(error => {
    console.error('Failed to initialize AI Manager:', error);
  });

  tradingBot.initialize().catch(error => {
    console.error('Failed to initialize trading bot:', error);
  });

  // Mining Pool API Routes
  app.get('/api/mining/pools', async (req, res) => {
    try {
      const pools = await storage.getAllMiningPools();
      res.json(pools);
    } catch (error) {
      console.error('Error fetching mining pools:', error);
      res.status(500).json({ error: 'Failed to fetch mining pools' });
    }
  });

  app.get('/api/mining/rigs', async (req, res) => {
    try {
      const rigs = await storage.getAllMiningRigs();
      res.json(rigs);
    } catch (error) {
      console.error('Error fetching mining rigs:', error);
      res.status(500).json({ error: 'Failed to fetch mining rigs' });
    }
  });

  app.get('/api/mining/stats', async (req, res) => {
    try {
      const totalStats = multiPoolManager.getTotalStats();
      const minerStats = multiPoolManager.getMinerStats();
      const pools = await storage.getAllMiningPools();

      // Calculate overall balance
      const balances = await storage.getMinerBalances();
      const totalBalance = balances.reduce((sum, balance) => {
        return sum + parseFloat(balance.confirmedBalance || '0');
      }, 0);

      res.json({
        totalStats,
        minerStats,
        pools,
        totalBalance: totalBalance.toFixed(8),
        currency: 'BTC'
      });
    } catch (error) {
      console.error('Error fetching mining stats:', error);
      res.status(500).json({ error: 'Failed to fetch mining stats' });
    }
  });

  app.get('/api/mining/connections', async (req, res) => {
    try {
      const rigs = await storage.getAllMiningRigs();
      const pools = await storage.getAllMiningPools();
      const minerStats = multiPoolManager.getMinerStats();

      const connections = rigs.map(rig => {
        const rigStats = minerStats.find(stat => stat.rigId === rig.id);
        const primaryPool = pools.find(p => p.id === rig.primaryPoolId);
        const backup1Pool = pools.find(p => p.id === rig.backupPool1Id);
        const backup2Pool = pools.find(p => p.id === rig.backupPool2Id);

        return {
          id: rig.id,
          name: rig.name,
          ipAddress: rig.ipAddress,
          status: rig.status,
          hashrate: rig.hashrate,
          activePool: rigStats?.activePool || 'primary',
          connections: [
            {
              type: 'primary',
              pool: primaryPool,
              isConnected: rigStats?.connections.find(c => c.type === 'primary')?.isConnected || false,
              sharesAccepted: rigStats?.connections.find(c => c.type === 'primary')?.sharesAccepted || 0,
              sharesRejected: rigStats?.connections.find(c => c.type === 'primary')?.sharesRejected || 0
            },
            {
              type: 'backup1',
              pool: backup1Pool,
              isConnected: rigStats?.connections.find(c => c.type === 'backup1')?.isConnected || false,
              sharesAccepted: rigStats?.connections.find(c => c.type === 'backup1')?.sharesAccepted || 0,
              sharesRejected: rigStats?.connections.find(c => c.type === 'backup1')?.sharesRejected || 0
            },
            {
              type: 'backup2',
              pool: backup2Pool,
              isConnected: rigStats?.connections.find(c => c.type === 'backup2')?.isConnected || false,
              sharesAccepted: rigStats?.connections.find(c => c.type === 'backup2')?.sharesAccepted || 0,
              sharesRejected: rigStats?.connections.find(c => c.type === 'backup2')?.sharesRejected || 0
            }
          ]
        };
      });

      res.json(connections);
    } catch (error) {
      console.error('Error fetching mining connections:', error);
      res.status(500).json({ error: 'Failed to fetch mining connections' });
    }
  });

  app.post('/api/mining/rigs/:rigId/failover', async (req, res) => {
    try {
      const { rigId } = req.params;
      const { targetPool } = req.body;

      // This would trigger failover logic in multiPoolManager
      res.json({ 
        success: true, 
        message: `Failover initiated for rig ${rigId} to ${targetPool}` 
      });
    } catch (error) {
      console.error('Error initiating failover:', error);
      res.status(500).json({ error: 'Failed to initiate failover' });
    }
  });

  // ==============================================
  // WALLET MANAGEMENT API ENDPOINTS
  // ==============================================

  // Create new wallet
  app.post('/api/wallet/create', async (req, res) => {
    try {
      const newWallet = await walletManager.createUserWallet();
      res.json({
        success: true,
        wallet: {
          address: newWallet.address,
          // Don't expose private key in response for security
          balance: newWallet.balance,
          teraBalance: newWallet.teraBalance,
          createdAt: newWallet.createdAt
        }
      });
    } catch (error) {
      console.error('Error creating wallet:', error);
      res.status(500).json({ error: 'Failed to create wallet' });
    }
  });

  // Import wallet from private key
  app.post('/api/wallet/import', async (req, res) => {
    try {
      const { privateKey } = req.body;
      if (!privateKey) {
        return res.status(400).json({ error: 'Private key required' });
      }

      const wallet = await walletManager.importWallet(privateKey);
      res.json({
        success: true,
        wallet: {
          address: wallet.address,
          balance: wallet.balance,
          teraBalance: wallet.teraBalance,
          createdAt: wallet.createdAt
        }
      });
    } catch (error) {
      console.error('Error importing wallet:', error);
      res.status(400).json({ error: 'Invalid private key or import failed' });
    }
  });

  // Get wallet balance
  app.get('/api/wallet/:address/balance', async (req, res) => {
    try {
      const { address } = req.params;
      const balance = await walletManager.getWalletBalance(address);
      res.json({
        address,
        ethBalance: balance.eth,
        teraBalance: balance.tera,
        lastUpdated: new Date().toISOString()
      });
    } catch (error) {
      console.error('Error fetching wallet balance:', error);
      res.status(500).json({ error: 'Failed to fetch wallet balance' });
    }
  });

  // Deploy TERA token contract
  app.post('/api/wallet/deploy-tera', async (req, res) => {
    try {
      const { 
        tokenName = 'TERA Token',
        tokenSymbol = 'TERA',
        totalSupply = '1000000000',
        decimals = 18
      } = req.body;

      const contract = await walletManager.deployTeraToken(
        tokenName,
        tokenSymbol,
        totalSupply,
        decimals
      );

      res.json({
        success: true,
        contract: {
          address: contract.address,
          deployedAt: contract.deployedAt,
          txHash: contract.txHash,
          tokenName,
          tokenSymbol,
          totalSupply,
          decimals
        }
      });
    } catch (error) {
      console.error('Error deploying TERA contract:', error);
      res.status(500).json({ error: 'Failed to deploy TERA token contract' });
    }
  });

  // Send TERA tokens
  app.post('/api/wallet/send-tera', async (req, res) => {
    try {
      const { fromPrivateKey, toAddress, amount } = req.body;

      if (!fromPrivateKey || !toAddress || !amount) {
        return res.status(400).json({ 
          error: 'Missing required fields: fromPrivateKey, toAddress, amount' 
        });
      }

      const txHash = await walletManager.sendTeraTokens(fromPrivateKey, toAddress, amount);

      res.json({
        success: true,
        transaction: {
          hash: txHash,
          from: new ethers.Wallet(fromPrivateKey).address,
          to: toAddress,
          amount,
          timestamp: new Date().toISOString()
        }
      });
    } catch (error) {
      console.error('Error sending TERA tokens:', error);
      res.status(500).json({ error: 'Failed to send TERA tokens' });
    }
  });

  // Fund wallet with ETH (for gas fees)
  app.post('/api/wallet/fund', async (req, res) => {
    try {
      const { toAddress, amount } = req.body;

      if (!toAddress || !amount) {
        return res.status(400).json({ 
          error: 'Missing required fields: toAddress, amount' 
        });
      }

      const txHash = await walletManager.fundWallet(toAddress, amount);

      res.json({
        success: true,
        transaction: {
          hash: txHash,
          to: toAddress,
          amount,
          type: 'funding',
          timestamp: new Date().toISOString()
        }
      });
    } catch (error) {
      console.error('Error funding wallet:', error);
      res.status(500).json({ error: 'Failed to fund wallet' });
    }
  });

  // Get master wallet info (admin only)
  app.get('/api/wallet/master', async (req, res) => {
    try {
      const masterWallet = walletManager.getMasterWallet();
      const balance = await walletManager.getWalletBalance(masterWallet.address);

      res.json({
        address: masterWallet.address,
        // Only return partial private key for security (first 10 chars)
        privateKeyPreview: masterWallet.privateKey.substring(0, 12) + '...',
        ethBalance: balance.eth,
        teraBalance: balance.tera,
        role: 'master'
      });
    } catch (error) {
      console.error('Error fetching master wallet:', error);
      res.status(500).json({ error: 'Failed to fetch master wallet' });
    }
  });

  // Create wallet from mnemonic
  app.post('/api/wallet/from-mnemonic', async (req, res) => {
    try {
      const { mnemonic, index = 0 } = req.body;

      if (!mnemonic) {
        return res.status(400).json({ error: 'Mnemonic phrase required' });
      }

      const wallet = await walletManager.createWalletFromMnemonic(mnemonic, index);

      res.json({
        success: true,
        wallet: {
          address: wallet.address,
          balance: wallet.balance,
          teraBalance: wallet.teraBalance,
          index,
          createdAt: wallet.createdAt
        }
      });
    } catch (error) {
      console.error('Error creating wallet from mnemonic:', error);
      res.status(400).json({ error: 'Invalid mnemonic phrase' });
    }
  });

  // Wallet transaction history (mock data for now)
  app.get('/api/wallet/:address/transactions', async (req, res) => {
    try {
      const { address } = req.params;

      // Mock transaction data - in real implementation, fetch from blockchain
      const transactions = [
        {
          hash: '0x' + crypto.randomBytes(32).toString('hex'),
          type: 'received',
          amount: '100.0',
          token: 'TERA',
          from: '0x742d35Cc6234Ff197b344b32D4Df0E1e23E98765',
          to: address,
          timestamp: new Date(Date.now() - 86400000).toISOString(),
          status: 'confirmed'
        },
        {
          hash: '0x' + crypto.randomBytes(32).toString('hex'),
          type: 'sent',
          amount: '25.5',
          token: 'TERA',
          from: address,
          to: '0x8ba1f109551bD432803012645Hac136c777F88e8',
          timestamp: new Date(Date.now() - 172800000).toISOString(),
          status: 'confirmed'
        }
      ];

      res.json({
        address,
        transactions,
        total: transactions.length
      });
    } catch (error) {
      console.error('Error fetching wallet transactions:', error);
      res.status(500).json({ error: 'Failed to fetch wallet transactions' });
    }
  });

  return httpServer;
}

