interface SocialProject {
  id: string;
  name: string;
  description: string;
  category: 'education' | 'healthcare' | 'environment' | 'community' | 'technology';
  fundingGoal: number;
  currentFunding: number;
  tokenAllocation: number;
  status: 'proposed' | 'active' | 'completed' | 'paused';
  startDate: Date;
  endDate?: Date;
  impact: {
    peopleHelped: number;
    communitiesReached: number;
    sustainabilityScore: number;
  };
  milestones: ProjectMilestone[];
}

interface ProjectMilestone {
  id: string;
  title: string;
  description: string;
  targetDate: Date;
  completed: boolean;
  completedDate?: Date;
  tokenReward: number;
}

interface TokenMetrics {
  totalSupply: number;
  circulatingSupply: number;
  socialProjectsAllocated: number;
  communityRewards: number;
  platformDevelopment: number;
  marketPrice?: number;
  marketCap?: number;
  volume24h?: number;
}

interface MiningOperation {
  id: string;
  name: string;
  location: string;
  type: 'bitcoin' | 'ethereum' | 'multi';
  hashrate: number;
  powerConsumption: number; // watts
  efficiency: number; // J/TH for Bitcoin
  status: 'online' | 'offline' | 'maintenance';
  dailyRevenue: number;
  dailyCost: number;
  profitability: number;
  temperature: number;
  uptime: number; // percentage
  lastMaintenance: Date;
