import { 
  users, 
  trades, 
  botSettings, 
  priceData,
  miningRigs,
  miningPools,
  poolConnections,
  minerBalances,
  type User, 
  type InsertUser,
  type Trade,
  type InsertTrade,
  type BotSettings,
  type InsertBotSettings,
  type PriceData,
  type InsertPriceData,
  type MiningRig,
  type InsertMiningRig,
  type MiningPool,
  type InsertMiningPool,
  type PoolConnection,
  type InsertPoolConnection,
  type MinerBalance,
  type InsertMinerBalance
} from "@shared/schema";

export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Trade operations
  createTrade(trade: InsertTrade): Promise<Trade>;
  getTrades(limit?: number): Promise<Trade[]>;
  getTradesByDateRange(startDate: Date, endDate: Date): Promise<Trade[]>;
  updateTrade(id: number, updates: Partial<InsertTrade>): Promise<Trade | undefined>;

  // Bot settings operations
  getBotSettings(): Promise<BotSettings>;
  updateBotSettings(settings: Partial<InsertBotSettings>): Promise<BotSettings>;

  // Price data operations
  addPriceData(data: InsertPriceData): Promise<PriceData>;
  getRecentPriceData(limit?: number): Promise<PriceData[]>;
  getPriceDataByTimeRange(startTime: Date, endTime: Date): Promise<PriceData[]>;

  // Mining operations
  getAllMiningRigs(): Promise<MiningRig[]>;
  getMiningRig(id: string): Promise<MiningRig | undefined>;
  createMiningRig(rig: InsertMiningRig): Promise<MiningRig>;
  updateMiningRig(id: string, updates: Partial<InsertMiningRig>): Promise<MiningRig | undefined>;

  getAllMiningPools(): Promise<MiningPool[]>;
  getMiningPool(id: string): Promise<MiningPool | undefined>;
  createMiningPool(pool: InsertMiningPool): Promise<MiningPool>;
  updateMiningPool(id: string, updates: Partial<InsertMiningPool>): Promise<MiningPool | undefined>;

  // Pool connection operations
  getPoolConnections(rigId: string): Promise<PoolConnection[]>;
  upsertPoolConnection(connection: InsertPoolConnection): Promise<PoolConnection>;

  // Miner balance operations
  getMinerBalances(rigId?: string): Promise<MinerBalance[]>;
  updateMinerBalance(rigId: string, poolId: string, balance: Partial<InsertMinerBalance>): Promise<MinerBalance>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private trades: Map<number, Trade>;
  private priceDataStore: Map<number, PriceData>;
  private botSettingsStore: BotSettings;
  private miningRigsStore: Map<string, MiningRig>;
  private miningPoolsStore: Map<string, MiningPool>;
  private poolConnectionsStore: Map<string, PoolConnection>;
  private minerBalancesStore: Map<string, MinerBalance>;

  private currentUserId: number;
  private currentTradeId: number;
  private currentPriceDataId: number;

  constructor() {
    this.users = new Map();
    this.trades = new Map();
    this.priceDataStore = new Map();
    this.miningRigsStore = new Map();
    this.miningPoolsStore = new Map();
    this.poolConnectionsStore = new Map();
    this.minerBalancesStore = new Map();
    this.currentUserId = 1;
    this.currentTradeId = 1;
    this.currentPriceDataId = 1;

    // Initialize default bot settings
    this.botSettingsStore = {
      id: 1,
      isActive: false,
      currentPositionSize: "1.00",
      maxPositionSize: "500.00",
      takeProfitPercent: "0.500",
      stopLossPercent: "0.300",
      consecutiveWins: 0,
      consecutiveLosses: 0,
      totalTrades: 0,
      winningTrades: 0,
      portfolioValue: "1000.00",
      lastUpdateTimestamp: new Date(),
    };

    // Initialize sample mining pools
    this.initializeSamplePools();

    // Initialize sample mining rigs
    this.initializeSampleRigs();
  }

  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createTrade(insertTrade: InsertTrade): Promise<Trade> {
    const id = this.currentTradeId++;
    const trade: Trade = { 
      ...insertTrade, 
      id, 
      timestamp: new Date() 
    };
    this.trades.set(id, trade);
    return trade;
  }

  async getTrades(limit: number = 50): Promise<Trade[]> {
    const allTrades = Array.from(this.trades.values());
    return allTrades
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, limit);
  }

  async getTradesByDateRange(startDate: Date, endDate: Date): Promise<Trade[]> {
    return Array.from(this.trades.values()).filter(
      trade => trade.timestamp >= startDate && trade.timestamp <= endDate
    );
  }

  async updateTrade(id: number, updates: Partial<InsertTrade>): Promise<Trade | undefined> {
    const existingTrade = this.trades.get(id);
    if (!existingTrade) return undefined;

    const updatedTrade = { ...existingTrade, ...updates };
    this.trades.set(id, updatedTrade);
    return updatedTrade;
  }

  async getBotSettings(): Promise<BotSettings> {
    return this.botSettingsStore;
  }

  async updateBotSettings(updates: Partial<InsertBotSettings>): Promise<BotSettings> {
    this.botSettingsStore = {
      ...this.botSettingsStore,
      ...updates,
      lastUpdateTimestamp: new Date(),
    };
    return this.botSettingsStore;
  }

  async addPriceData(data: InsertPriceData): Promise<PriceData> {
    const id = this.currentPriceDataId++;
    const priceData: PriceData = {
      ...data,
      id,
      timestamp: new Date(),
    };
    this.priceDataStore.set(id, priceData);
    return priceData;
  }

  async getRecentPriceData(limit: number = 100): Promise<PriceData[]> {
    const allData = Array.from(this.priceDataStore.values());
    return allData
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, limit);
  }

  async getPriceDataByTimeRange(startTime: Date, endTime: Date): Promise<PriceData[]> {
    return Array.from(this.priceDataStore.values()).filter(
      data => data.timestamp >= startTime && data.timestamp <= endTime
    );
  }

  // Mining operations implementation
  async getAllMiningRigs(): Promise<MiningRig[]> {
    return Array.from(this.miningRigsStore.values());
  }

  async getMiningRig(id: string): Promise<MiningRig | undefined> {
    return this.miningRigsStore.get(id);
  }

  async createMiningRig(insertRig: InsertMiningRig): Promise<MiningRig> {
    const id = crypto.randomUUID();
    const rig: MiningRig = {
      ...insertRig,
      id,
      createdAt: new Date(),
      lastUpdate: new Date(),
    };
    this.miningRigsStore.set(id, rig);
    return rig;
  }

  async updateMiningRig(id: string, updates: Partial<InsertMiningRig>): Promise<MiningRig | undefined> {
    const existingRig = this.miningRigsStore.get(id);
    if (!existingRig) return undefined;

    const updatedRig = { 
      ...existingRig, 
      ...updates,
      lastUpdate: new Date(),
    };
    this.miningRigsStore.set(id, updatedRig);
    return updatedRig;
  }

  async getAllMiningPools(): Promise<MiningPool[]> {
    return Array.from(this.miningPoolsStore.values());
  }

  async getMiningPool(id: string): Promise<MiningPool | undefined> {
    return this.miningPoolsStore.get(id);
  }

  async createMiningPool(insertPool: InsertMiningPool): Promise<MiningPool> {
    const id = crypto.randomUUID();
    const pool: MiningPool = {
      ...insertPool,
      id,
      createdAt: new Date(),
    };
    this.miningPoolsStore.set(id, pool);
    return pool;
  }

  async updateMiningPool(id: string, updates: Partial<InsertMiningPool>): Promise<MiningPool | undefined> {
    const existingPool = this.miningPoolsStore.get(id);
    if (!existingPool) return undefined;

    const updatedPool = { ...existingPool, ...updates };
    this.miningPoolsStore.set(id, updatedPool);
    return updatedPool;
  }

  async getPoolConnections(rigId: string): Promise<PoolConnection[]> {
    return Array.from(this.poolConnectionsStore.values()).filter(
      conn => conn.rigId === rigId
    );
  }

  async upsertPoolConnection(insertConnection: InsertPoolConnection): Promise<PoolConnection> {
    // Find existing connection by rigId, poolId, and connectionType
    const existingKey = `${insertConnection.rigId}-${insertConnection.poolId}-${insertConnection.connectionType}`;
    const existing = this.poolConnectionsStore.get(existingKey);

    if (existing) {
      const updated: PoolConnection = {
        ...existing,
        ...insertConnection,
        lastActivity: new Date(),
      };
      this.poolConnectionsStore.set(existingKey, updated);
      return updated;
    } else {
      const id = crypto.randomUUID();
      const connection: PoolConnection = {
        ...insertConnection,
        id,
        createdAt: new Date(),
        lastActivity: new Date(),
      };
      this.poolConnectionsStore.set(existingKey, connection);
      return connection;
    }
  }

  async getMinerBalances(rigId?: string): Promise<MinerBalance[]> {
    const balances = Array.from(this.minerBalancesStore.values());
