import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import { Navigation } from "@/components/navigation";
import { CircularAINav } from "@/components/circular-ai-nav";
import { TeraGuardianChat } from "@/components/tera-guardian-chat";
import { KloudRadio } from "@/components/kloud-radio";
import Dashboard from "@/pages/dashboard";
import Trading from "@/pages/trading";
import GhostAI from "@/pages/ghost-ai";
import MiningRigs from "@/pages/mining-rigs";
import CustomPools from "@/pages/custom-pools";
import CryptoPortfolio from "@/pages/crypto-portfolio";
import TERJusticeAI from "@/pages/terajustice-ai";
import TeraAIs from "@/pages/tera-ais";
import Cafe from "@/pages/cafe";
import TeraToken from "@/pages/tera-token";
import Platform from "@/pages/platform";
import AdminJournal from "@/pages/admin-journal";
import Web3Wallet from "@/pages/web3-wallet";
import WalletControl from "@/pages/wallet-control";
// TERA AI Family
import TeraGuardian from "@/pages/tera-guardian";
import TeraAlgo from "@/pages/tera-algo";
import TeraMiner from "@/pages/tera-miner";
import TeraSecure from "@/pages/tera-secure";
import TeraExchange from "@/pages/tera-exchange";
import TeraCommunity from "@/pages/tera-community";
import WebMining from "@/pages/webmining";
import ApiTesting from "@/pages/api-testing";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <div className="space-y-6">
      {/* Header with Navigation */}
      <header className="relative flex items-center justify-between p-6 border-b border-cyan-500/30 bg-gradient-to-r from-slate-900/50 via-slate-800/30 to-slate-900/50 backdrop-blur-sm">
        <div className="flex items-center gap-4">
          <div className="relative">
            <img 
              src="/kloud-bugs-logo.png" 
              alt="TERA CORE Logo" 
              className="w-16 h-16 object-contain holographic-glow"
            />
            <div className="absolute inset-0 bg-gradient-to-r from-cyan-400/20 via-purple-500/20 to-pink-500/20 rounded-full animate-pulse"></div>
          </div>
          <div>
            <h1 className="text-3xl font-bold kloud-title">
              TERA CORE - KLOUD BUGS PRO
            </h1>
            <p className="text-sm text-cyan-300/80 font-light tracking-wide">
              Advanced Cryptocurrency & Social Impact Platform
            </p>
          </div>
        </div>
        <Navigation />

        {/* Holographic background effects */}
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/5 via-purple-500/5 to-pink-500/5 pointer-events-none"></div>
        <div className="absolute top-0 left-1/4 w-32 h-32 bg-cyan-400/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-0 right-1/4 w-24 h-24 bg-purple-500/10 rounded-full blur-2xl animate-pulse delay-1000"></div>
      </header>

      {/* Circular AI Navigation */}
      <CircularAINav />

      {/* TERA Guardian Chat Assistant */}
      <TeraGuardianChat />

      {/* KLOUD BUGS Radio */}
      <KloudRadio />

      {/* Main Content */}
      <main className="px-4">
        <Switch>
          <Route path="/" component={Dashboard} />
          <Route path="/trading" component={Trading} />
          <Route path="/ghost-ai" component={GhostAI} />
          <Route path="/mining-rigs" component={MiningRigs} />
          <Route path="/webmining" component={WebMining} />
          <Route path="/api-testing" component={ApiTesting} />
          <Route path="/custom-pools" component={CustomPools} />
          <Route path="/crypto-portfolio" component={CryptoPortfolio} />
          <Route path="/tera" component={TERJusticeAI} />
          <Route path="/tera-ais" component={TeraAIs} />
          <Route path="/cafe" component={Cafe} />
          <Route path="/tera-token" component={TeraToken} />
          <Route path="/platform" component={Platform} />
          <Route path="/admin-journal" component={AdminJournal} />
          <Route path="/web3-wallet" component={Web3Wallet} />
          <Route path="/wallet-control" component={WalletControl} />
          {/* TERA AI Family Routes */}
          <Route path="/tera-guardian" component={TeraGuardian} />
          <Route path="/tera-algo" component={TeraAlgo} />
          <Route path="/tera-miner" component={TeraMiner} />
          <Route path="/tera-secure" component={TeraSecure} />
          <Route path="/tera-exchange" component={TeraExchange} />
          <Route path="/tera-community" component={TeraCommunity} />
          <Route component={NotFound} />
        </Switch>
      </main>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider defaultTheme="dark" storageKey="kloud-bot-theme">
      <QueryClientProvider client={queryClient}>
        <TooltipProvider>
          <div className="min-h-screen bg-background text-foreground" style={{ opacity: 1, transition: 'none' }}>
            <Toaster />
            <Router />
          </div>
        </TooltipProvider>
      </QueryClientProvider>
    </ThemeProvider>
  );
}

export default App;

