import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface AutoTradingPanelProps {
  isActive: boolean;
  onToggle: (enabled: boolean) => void;
  className?: string;
}

export function AutoTradingPanel({ isActive, onToggle, className }: AutoTradingPanelProps) {
  const { toast } = useToast();
  const [isUpdating, setIsUpdating] = useState(false);
  const [alertSettings, setAlertSettings] = useState({
    priceAlerts: true,
    signalAlerts: true,
    tradeAlerts: true,
    aiPredictionAlerts: true,
    minConfidence: 75,
    soundEnabled: true,
    autoTradeEnabled: isActive
  });

  const handleAutoTradeToggle = async (enabled: boolean) => {
    try {
      setIsUpdating(true);
      await apiRequest('POST', '/api/bot/auto-trade', { enabled });

      setAlertSettings(prev => ({ ...prev, autoTradeEnabled: enabled }));
      onToggle(enabled);

      toast({
        title: enabled ? 'Auto-Trading Enabled' : 'Auto-Trading Disabled',
        description: enabled 
          ? 'Bot will now automatically execute trades based on AI and technical analysis'
          : 'Bot will only generate signals without executing trades',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to toggle auto-trading mode',
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const handleSettingChange = (setting: string, value: boolean | number) => {
    setAlertSettings(prev => ({ ...prev, [setting]: value }));
  };

  return (
    <Card className={`trading-ai-card ${className}`}>
      <CardHeader>
        <CardTitle className="flex items-center space-x-3">
          <img 
            src="/kloud-bugs-logo.png" 
            alt="KLOUD BUGS" 
            className="w-6 h-6 object-contain holographic-glow"
          />
          <span className="trading-ai-title">KLOUD BUGS Auto-Trading</span>
          <Badge 
            variant={isActive ? "default" : "outline"}
            className={`${isActive ? "bg-green-600" : ""} holographic-glow`}
          >
            {isActive ? 'ACTIVE' : 'MANUAL'}
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Auto-Trading Toggle */}
        <div className="bg-gray-700 rounded-lg p-4">
          <div className="flex items-center justify-between mb-3">
            <div>
              <Label htmlFor="autoTrade" className="text-base font-medium">
                Auto-Trading Mode
              </Label>
              <p className="text-sm text-gray-400 mt-1">
                Enable fully automated trading based on AI predictions and technical signals
              </p>
            </div>
            <Switch
              id="autoTrade"
              checked={alertSettings.autoTradeEnabled}
              onCheckedChange={handleAutoTradeToggle}
              disabled={isUpdating}
            />
          </div>

          {alertSettings.autoTradeEnabled && (
            <div className="mt-3 p-3 bg-green-500/10 border border-green-500/30 rounded-lg">
              <div className="flex items-center space-x-2">
                <i className="fas fa-check-circle text-green-400" />
                <span className="text-sm text-green-300 font-medium">
                  Auto-trading is active
                </span>
              </div>
              <p className="text-xs text-green-400 mt-1">
                Bot will execute trades automatically when confidence levels are met
              </p>
            </div>
          )}
        </div>

        <Separator className="bg-gray-600" />

        {/* Alert Settings */}
        <div className="space-y-4">
          <h3 className="text-sm font-medium text-gray-300">Alert Preferences</h3>

          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <Label htmlFor="priceAlerts" className="text-sm">
                Price Movement Alerts
              </Label>
              <Switch
                id="priceAlerts"
                checked={alertSettings.priceAlerts}
                onCheckedChange={(checked) => handleSettingChange('priceAlerts', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="signalAlerts" className="text-sm">
                Trading Signal Alerts
              </Label>
              <Switch
                id="signalAlerts"
                checked={alertSettings.signalAlerts}
                onCheckedChange={(checked) => handleSettingChange('signalAlerts', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="tradeAlerts" className="text-sm">
                Trade Execution Alerts
              </Label>
              <Switch
                id="tradeAlerts"
                checked={alertSettings.tradeAlerts}
                onCheckedChange={(checked) => handleSettingChange('tradeAlerts', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="aiAlerts" className="text-sm">
                AI Prediction Alerts
              </Label>
              <Switch
                id="aiAlerts"
                checked={alertSettings.aiPredictionAlerts}
                onCheckedChange={(checked) => handleSettingChange('aiPredictionAlerts', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="soundAlerts" className="text-sm">
                Sound Notifications
              </Label>
              <Switch
                id="soundAlerts"
                checked={alertSettings.soundEnabled}
                onCheckedChange={(checked) => handleSettingChange('soundEnabled', checked)}
              />
            </div>
          </div>
        </div>

        <Separator className="bg-gray-600" />

        {/* Trading Thresholds */}
        <div className="space-y-4">
          <h3 className="text-sm font-medium text-gray-300">Trading Thresholds</h3>

          <div>
            <Label htmlFor="minConfidence" className="text-sm text-gray-400">
              Minimum Confidence for Auto-Trading (%)
            </Label>
            <div className="flex items-center space-x-3 mt-2">
              <Input
                id="minConfidence"
                type="number"
                min="50"
                max="95"
                value={alertSettings.minConfidence}
                onChange={(e) => handleSettingChange('minConfidence', parseInt(e.target.value))}
                className="bg-gray-700 border-gray-600 w-20"
              />
              <div className="flex-1">
                <div className="w-full bg-gray-600 rounded-full h-2">
                  <div
                    className="bg-blue-600 h-2 rounded-full transition-all"
                    style={{ width: `${alertSettings.minConfidence}%` }}
                  ></div>
                </div>
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Higher values = fewer but more confident trades
            </p>
          </div>
        </div>

        <Separator className="bg-gray-600" />

        {/* Risk Management */}
        <div className="space-y-4">
          <h3 className="text-sm font-medium text-gray-300">Risk Management</h3>

          <div className="grid grid-cols-2 gap-3 text-sm">
            <div className="bg-gray-700 rounded p-3">
              <div className="text-gray-400">Max Trades/Hour</div>
              <div className="font-semibold text-white">10</div>
            </div>
            <div className="bg-gray-700 rounded p-3">
              <div className="text-gray-400">Risk Level</div>
              <div className="font-semibold text-yellow-400">Moderate</div>
            </div>
          </div>

          <div className="bg-yellow-500/10 border border-yellow-500/30 rounded-lg p-3">
            <div className="flex items-start space-x-2">
              <i className="fas fa-exclamation-triangle text-yellow-400 text-sm mt-0.5" />
              <div>
                <div className="text-sm text-yellow-300 font-medium">
                  Auto-Trading Risks
                </div>
                <p className="text-xs text-yellow-400 mt-1">
                  Automated trading involves financial risk. Monitor performance and adjust settings as needed.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="space-y-3">
          <h3 className="text-sm font-medium text-gray-300">Quick Actions</h3>

          <div className="grid grid-cols-2 gap-3">
            <Button
              variant="outline"
              size="sm"
              className="text-green-400 border-green-400 hover:bg-green-400/20"
              disabled={!alertSettings.autoTradeEnabled}
            >
              <i className="fas fa-play mr-2" />
              Resume
            </Button>
            <Button
              variant="outline"
              size="sm"
              className="text-yellow-400 border-yellow-400 hover:bg-yellow-400/20"
              disabled={!alertSettings.autoTradeEnabled}
            >
              <i className="fas fa-pause mr-2" />
              Pause
            </Button>
          </div>

          <Button
            variant="destructive"
            size="sm"
            className="w-full"
          >
            <i className="fas fa-stop mr-2" />
            Emergency Stop All
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/bot-controls.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Switch } from '@/components/ui/switch';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

export function BotControls() {
  const { toast } = useToast();
  const [isUpdating, setIsUpdating] = useState(false);
  const [audioEnabled, setAudioEnabled] = useState(true);

  // Fetch bot status and settings
  const { data: botData } = useQuery<any>({
    queryKey: ['/api/bot/status'],
    refetchInterval: 2000,
  });

  const isActive = botData?.state?.isActive || false;
  const currentPosition = botData?.state?.currentPosition || null;
  const settings = botData?.settings || {
    maxPositionSize: '100',
    takeProfitPercent: '2',
    stopLossPercent: '1'
  };

  const [formSettings, setFormSettings] = useState({
    maxPositionSize: settings.maxPositionSize || '100',
    takeProfitPercent: settings.takeProfitPercent || '2',
    stopLossPercent: settings.stopLossPercent || '1'
  });

  const handleStart = async () => {
    try {
      setIsUpdating(true);
      await apiRequest('POST', '/api/bot/start');
      toast({
        title: 'Bot Started',
        description: 'Trading bot is now active and monitoring markets.',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to start trading bot.',
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const onSettingsUpdate = () => {
    // Refetch data after settings update
  };

  const handleStop = async () => {
    try {
      setIsUpdating(true);
      await apiRequest('POST', '/api/bot/stop');
      toast({
        title: 'Bot Stopped',
        description: 'Trading bot has been stopped.',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to stop trading bot.',
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const handleEmergencyStop = async () => {
    if (!confirm('Are you sure you want to execute an emergency stop? This will immediately close all positions.')) {
      return;
    }

    try {
      setIsUpdating(true);
      await apiRequest('POST', '/api/bot/emergency-stop');
      toast({
        title: 'Emergency Stop Executed',
        description: 'All positions have been closed and the bot has been stopped.',
        variant: 'destructive',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to execute emergency stop.',
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const handleForceSignal = async (type: 'BUY' | 'SELL') => {
    if (!confirm(`Are you sure you want to force a ${type} signal?`)) {
      return;
    }

    try {
      setIsUpdating(true);
      await apiRequest('POST', '/api/bot/force-signal', { type });
      toast({
        title: 'Signal Executed',
        description: `Force ${type} signal has been executed.`,
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: error instanceof Error ? error.message : `Failed to execute ${type} signal.`,
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const handleSettingsUpdate = async () => {
    try {
      setIsUpdating(true);
      await apiRequest('PUT', '/api/bot/settings', formSettings);
      onSettingsUpdate();
      toast({
        title: 'Settings Updated',
        description: 'Bot settings have been updated successfully.',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to update bot settings.',
        variant: 'destructive',
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const formatDuration = (milliseconds: number) => {
    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    }
    return `${minutes}m ${seconds % 60}s`;
  };

  return (
    <div className="space-y-6">
      {/* KLOUD BUGS Bot Status */}
      <Card className="trading-ai-card">
        <CardHeader>
          <CardTitle className="flex items-center space-x-3">
            <img 
              src="/kloud-bugs-logo.png" 
              alt="KLOUD BUGS" 
              className="w-8 h-8 object-contain holographic-glow"
            />
            <div className={`w-4 h-4 rounded-full ${isActive ? 'status-online' : 'status-offline'}`} />
            <span className="trading-ai-title">KLOUD BUGS STATUS</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="flex space-x-4">
            {!isActive ? (
              <button 
                onClick={handleStart}
                disabled={isUpdating}
                className="cosmic-main-btn flex-1 bg-gradient-to-r from-green-500 via-emerald-500 to-teal-500"
              >
                <span className="flex items-center justify-center">
                  <i className="fas fa-rocket mr-3 text-xl" />
                  ACTIVATE KLOUD BOT
                </span>
              </button>
            ) : (
              <button 
                onClick={handleStop}
                disabled={isUpdating}
                className="cosmic-main-btn flex-1 bg-gradient-to-r from-orange-500 via-yellow-500 to-amber-500"
              >
                <span className="flex items-center justify-center">
                  <i className="fas fa-pause mr-3 text-xl" />
                  DEACTIVATE BOT
                </span>
              </button>
            )}
          </div>

          <div className="grid grid-cols-2 gap-4">
            <button
              onClick={() => handleForceSignal('BUY')}
              disabled={isUpdating || !isActive || currentPosition?.isOpen}
              className="cosmic-action-btn bg-gradient-to-r from-green-600 via-emerald-600 to-teal-600 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <i className="fas fa-arrow-up mr-2" />
              <span>FORCE BUY</span>
            </button>
            <button
              onClick={() => handleForceSignal('SELL')}
              disabled={isUpdating || !isActive || currentPosition?.isOpen}
              className="cosmic-action-btn bg-gradient-to-r from-red-600 via-pink-600 to-rose-600 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <i className="fas fa-arrow-down mr-2" />
              <span>FORCE SELL</span>
            </button>
          </div>

          <button 
            onClick={handleEmergencyStop}
            disabled={isUpdating}
            className="cosmic-action-btn w-full bg-gradient-to-r from-red-600 via-pink-600 to-purple-600 border-red-500"
          >
            <i className="fas fa-exclamation-triangle mr-3" />
            <span>EMERGENCY SHUTDOWN</span>
          </button>
        </CardContent>
      </Card>

      {/* Current Position - Cosmic Style */}
      {currentPosition?.isOpen && (
        <Card className="cosmic-card">
          <CardHeader>
            <CardTitle>Current Position</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <div className="text-gray-400">Position Type</div>
                <div className={`font-medium ${
                  currentPosition.type === 'BUY' ? 'text-green-400' : 'text-red-400'
                }`}>
                  {currentPosition.type}
                </div>
              </div>
              <div>
                <div className="text-gray-400">Position Size</div>
                <div className="font-medium">${currentPosition.amount.toFixed(2)}</div>
              </div>
              <div>
                <div className="text-gray-400">Entry Price</div>
                <div className="font-medium">${currentPosition.entryPrice.toLocaleString()}</div>
              </div>
              <div>
                <div className="text-gray-400">Current P&L</div>
                <div className={`font-medium ${
                  currentPosition.pnl >= 0 ? 'text-green-400' : 'text-red-400'
                }`}>
                  {currentPosition.pnl >= 0 ? '+' : ''}${currentPosition.pnl.toFixed(2)}
                </div>
              </div>
              <div>
                <div className="text-gray-400">Duration</div>
                <div className="font-medium">{formatDuration(currentPosition.duration)}</div>
              </div>
              <div>
                <div className="text-gray-400">Current Price</div>
                <div className="font-medium">${currentPosition.currentPrice.toLocaleString()}</div>
              </div>
            </div>

            <Separator className="bg-gray-600" />

            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <div className="text-gray-400">Take Profit</div>
                <div className="text-green-400 font-medium">
                  ${currentPosition.takeProfit.toLocaleString()}
                </div>
              </div>
              <div>
                <div className="text-gray-400">Stop Loss</div>
                <div className="text-red-400 font-medium">
                  ${currentPosition.stopLoss.toLocaleString()}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Bot Settings */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <CardTitle>Bot Settings</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-3">
            <div>
              <Label htmlFor="currentSize" className="text-sm text-gray-400">
                Current Position Size
              </Label>
              <Input
                id="currentSize"
                value={`$${settings.currentPositionSize}`}
                disabled
                className="bg-gray-700 border-gray-600"
              />
            </div>

            <div>
              <Label htmlFor="maxSize" className="text-sm text-gray-400">
                Max Position Size ($)
              </Label>
              <Input
                id="maxSize"
                type="number"
                value={formSettings.maxPositionSize}
                onChange={(e) => setFormSettings(prev => ({
                  ...prev,
                  maxPositionSize: e.target.value
                }))}
                className="bg-gray-700 border-gray-600"
              />
            </div>

            <div>
              <Label htmlFor="takeProfit" className="text-sm text-gray-400">
                Take Profit (%)
              </Label>
              <Input
                id="takeProfit"
                type="number"
                step="0.1"
                value={formSettings.takeProfitPercent}
                onChange={(e) => setFormSettings(prev => ({
                  ...prev,
                  takeProfitPercent: e.target.value
                }))}
                className="bg-gray-700 border-gray-600"
              />
            </div>

            <div>
              <Label htmlFor="stopLoss" className="text-sm text-gray-400">
                Stop Loss (%)
              </Label>
              <Input
                id="stopLoss"
                type="number"
                step="0.1"
                value={formSettings.stopLossPercent}
                onChange={(e) => setFormSettings(prev => ({
                  ...prev,
                  stopLossPercent: e.target.value
                }))}
                className="bg-gray-700 border-gray-600"
              />
            </div>

            <Button
              onClick={handleSettingsUpdate}
              disabled={isUpdating}
              className="w-full"
            >
              Update Settings
            </Button>
          </div>

          <Separator className="bg-gray-600" />

          <div className="flex items-center justify-between">
            <Label htmlFor="audio" className="text-sm text-gray-400">
              Audio Alerts
            </Label>
            <Switch
              id="audio"
              checked={audioEnabled}
              onCheckedChange={setAudioEnabled}
            />
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/custom-ai-manager..content:
import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { Plus, TestTube2, Trash2, CheckCircle, XCircle } from 'lucide-react';

interface CustomAIForm {
  id: string;
  name: string;
  endpoint: string;
  apiKey: string;
  modelName: string;
  type: 'prediction' | 'analysis' | 'sentiment' | 'strategy';
  headers: string; // JSON string
}

interface CustomAIManagerProps {
  models: any[];
  onModelAdded?: () => void;
}

export function CustomAIManager({ models, onModelAdded }: CustomAIManagerProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isAddingModel, setIsAddingModel] = useState(false);
  const [form, setForm] = useState<CustomAIForm>({
    id: '',
    name: '',
    endpoint: '',
    apiKey: '',
    modelName: '',
    type: 'prediction',
    headers: '{}'
  });

  const [testResults, setTestResults] = useState<Record<string, any>>({});

  // Add custom model mutation
  const addModelMutation = useMutation({
    mutationFn: async (modelData: any) => {
      return apiRequest('/api/ai/models', {
        method: 'POST',
        body: JSON.stringify(modelData)
      });
    },
    onSuccess: () => {
      toast({
        title: "AI Model Added",
        description: "Your custom AI model has been successfully integrated!"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/ai/models'] });
      setIsAddingModel(false);
      setForm({
        id: '',
        name: '',
        endpoint: '',
        apiKey: '',
        modelName: '',
        type: 'prediction',
        headers: '{}'
      });
      if (onModelAdded) onModelAdded();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to add custom AI model",
        variant: "destructive"
      });
    }
  });

  // Remove model mutation
  const removeModelMutation = useMutation({
    mutationFn: async (modelId: string) => {
      return apiRequest(`/api/ai/models/${modelId}`, {
        method: 'DELETE'
      });
    },
    onSuccess: () => {
      toast({
        title: "AI Model Removed",
        description: "Custom AI model has been removed successfully"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/ai/models'] });
    }
  });

  // Test model mutation
  const testModelMutation = useMutation({
    mutationFn: async (modelId: string) => {
      return apiRequest(`/api/ai/models/${modelId}/test`, {
        method: 'POST'
      });
    },
    onSuccess: (data, modelId) => {
      setTestResults(prev => ({ ...prev, [modelId]: data }));
      toast({
        title: data.success ? "Test Successful" : "Test Failed",
        description: data.success ? "Your AI model is working correctly!" : data.error,
        variant: data.success ? "default" : "destructive"
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const headers = JSON.parse(form.headers);
      const modelData = {
        ...form,
        headers
      };
      addModelMutation.mutate(modelData);
    } catch (error) {
      toast({
        title: "Invalid Headers",
        description: "Please provide valid JSON for headers",
        variant: "destructive"
      });
    }
  };

  const customModels = models.filter(m => m.provider === 'custom');

  const generateModelId = () => {
    const timestamp = Date.now().toString(36);
    const random = Math.random().toString(36).substr(2, 5);
    return `custom-${form.type}-${timestamp}-${random}`;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-cyber-gold">Custom AI Models</h3>
          <p className="text-sm text-muted-foreground">
            Integrate your own AI models into KLOUD BOT PRO
          </p>
        </div>
        <Button 
          onClick={() => setIsAddingModel(true)} 
          className="cosmic-button"
          disabled={isAddingModel}
        >
          <Plus className="h-4 w-4 mr-2" />
          Add AI Model
        </Button>
      </div>

      {/* Add Model Form */}
      {isAddingModel && (
        <Card className="cosmic-card border-cyan-500/30">
          <CardHeader>
            <CardTitle className="text-cyber-gold">Add Custom AI Model</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="name">Model Name</Label>
                  <Input
                    id="name"
                    value={form.name}
                    onChange={(e) => setForm(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="My Custom AI Model"
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="type">Model Type</Label>
                  <Select 
                    value={form.type} 
                    onValueChange={(value: any) => setForm(prev => ({ ...prev, type: value }))}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="prediction">Price Prediction</SelectItem>
                      <SelectItem value="analysis">Market Analysis</SelectItem>
                      <SelectItem value="sentiment">Sentiment Analysis</SelectItem>
                      <SelectItem value="strategy">Trading Strategy</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="endpoint">API Endpoint</Label>
                  <Input
                    id="endpoint"
                    value={form.endpoint}
                    onChange={(e) => setForm(prev => ({ ...prev, endpoint: e.target.value }))}
                    placeholder="https://your-ai-api.com/predict"
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="modelName">Model Name/ID</Label>
                  <Input
                    id="modelName"
                    value={form.modelName}
                    onChange={(e) => setForm(prev => ({ ...prev, modelName: e.target.value }))}
                    placeholder="your-model-v1"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="apiKey">API Key</Label>
                <Input
                  id="apiKey"
                  type="password"
                  value={form.apiKey}
                  onChange={(e) => setForm(prev => ({ ...prev, apiKey: e.target.value }))}
                  placeholder="your-api-key"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="headers">Custom Headers (JSON)</Label>
                <Textarea
                  id="headers"
                  value={form.headers}
                  onChange={(e) => setForm(prev => ({ ...prev, headers: e.target.value }))}
                  placeholder='{"X-Custom-Header": "value"}'
                  className="h-20"
                />
              </div>

              <div className="hidden">
                <Input
                  value={form.id || generateModelId()}
                  onChange={(e) => setForm(prev => ({ ...prev, id: e.target.value }))}
                />
              </div>

              <div className="flex gap-2">
                <Button 
                  type="submit" 
                  className="cosmic-button"
                  disabled={addModelMutation.isPending}
                >
                  {addModelMutation.isPending ? 'Adding...' : 'Add Model'}
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsAddingModel(false)}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      {/* Custom Models List */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {customModels.map((model) => (
          <Card key={model.id} className="cosmic-card">
            <CardHeader className="pb-3">
              <CardTitle className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span className="text-sm">{model.name}</span>
                  {model.active ? (
                    <CheckCircle className="h-4 w-4 text-green-500" />
                  ) : (
                    <XCircle className="h-4 w-4 text-red-500" />
                  )}
                </div>
                <Badge variant="outline" className="bg-purple-500/20 text-purple-300">
                  CUSTOM
                </Badge>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="text-xs text-muted-foreground">
                <div><strong>Type:</strong> {model.type.toUpperCase()}</div>
                <div><strong>Model:</strong> {model.model}</div>
                <div><strong>Endpoint:</strong> {model.endpoint?.substring(0, 40)}...</div>
              </div>

              {testResults[model.id] && (
                <div className={`text-xs p-2 rounded border ${
                  testResults[model.id].success 
                    ? 'bg-green-500/10 border-green-500/30 text-green-400'
                    : 'bg-red-500/10 border-red-500/30 text-red-400'
                }`}>
                  {testResults[model.id].success ? 'âœ… Working' : 'âŒ Error: ' + testResults[model.id].error}
                </div>
              )}

              <div className="flex gap-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => testModelMutation.mutate(model.id)}
                  disabled={testModelMutation.isPending}
                  className="flex-1"
                >
                  <TestTube2 className="h-3 w-3 mr-1" />
                  Test
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={() => removeModelMutation.mutate(model.id)}
                  disabled={removeModelMutation.isPending}
                >
                  <Trash2 className="h-3 w-3" />
                </Button>
              </div>

              <div className="text-xs text-muted-foreground">
                Last used: {new Date(model.lastUsed).toLocaleTimeString()}
              </div>
            </CardContent>
          </Card>
        ))}

        {customModels.length === 0 && (
          <Card className="cosmic-card col-span-full">
            <CardContent className="text-center py-8">
              <div className="text-muted-foreground">
                <Plus className="h-12 w-12 mx-auto mb-2 opacity-50" />
                <p>No custom AI models added yet</p>
                <p className="text-sm">Add your own AI models to enhance trading capabilities</p>
              </div>
            </CardContent>
          </Card>
        )}
      </div>

      {/* API Integration Guide */}
      <Card className="cosmic-card border-cyan-500/20">
        <CardHeader>
          <CardTitle className="text-cyber-gold">API Integration Guide</CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="text-sm space-y-2">
            <p><strong>Your AI model should accept POST requests with this format:</strong></p>
            <pre className="bg-black/50 p-3 rounded text-xs text-green-400">
{`{
  "type": "prediction|analysis|sentiment|strategy",
  "marketData": {
    "price": 43000,
    "change24h": 2.5,
    "volume": 1000000
  },
  "indicators": {
    "rsi": 65,
    "macd": 0.5,
    "bollinger": { "upper": 44000, "lower": 42000 }
  }
}`}
            </pre>

            <p><strong>Expected response format:</strong></p>
            <pre className="bg-black/50 p-3 rounded text-xs text-cyan-400">
{`{
  "prediction": "BUY|SELL|HOLD",
  "confidence": 85,
  "reasoning": "Technical analysis shows...",
  "timeframe": "1-4 hours",
  "targetPrice": 45000,
  "stopLoss": 41000
}`}
            </pre>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/live-trading-chart.content:
import { useEffect, useRef, useState } from 'react';
import { Chart, ChartConfiguration } from 'chart.js/auto';
import 'chartjs-adapter-date-fns';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';

interface LiveTradingChartProps {
  className?: string;
}

interface CandlestickData {
  x: number;
  o: number; // open
  h: number; // high
  l: number; // low
  c: number; // close
}

export function LiveTradingChart({ className }: LiveTradingChartProps) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstance = useRef<Chart | null>(null);
  const [timeframe, setTimeframe] = useState('1m');
  const [chartType, setChartType] = useState<'candlestick' | 'line'>('candlestick');

  // Fetch real-time OHLC data from Kraken
  const { data: ohlcData } = useQuery({
    queryKey: ['/api/market/ohlc'],
    refetchInterval: 5000, // Update every 5 seconds for live data
  });

  // Fetch market sentiment for overlay
  const { data: sentimentData } = useQuery({
    queryKey: ['/api/analytics/market-sentiment'],
    refetchInterval: 15000,
  });

  // Fetch technical indicators
  const { data: indicators } = useQuery({
    queryKey: ['/api/market/indicators'],
    refetchInterval: 10000,
  });

  useEffect(() => {
    if (!chartRef.current || !ohlcData) return;

    const ctx = chartRef.current.getContext('2d');
    if (!ctx) return;

    // Destroy existing chart
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // Prepare candlestick data
    const candlestickData: CandlestickData[] = ohlcData.map((item: any) => ({
      x: item.timestamp,
      o: item.open,
      h: item.high,
      l: item.low,
      c: item.close,
    }));

    // Prepare line data for simple view
    const lineData = ohlcData.map((item: any) => ({
      x: item.timestamp,
      y: item.close,
    }));

    // Get sentiment color
    const sentimentColor = sentimentData?.sentiment?.direction === 'BULLISH' ? 
      'rgba(34, 197, 94, 0.8)' : 
      sentimentData?.sentiment?.direction === 'BEARISH' ? 
      'rgba(239, 68, 68, 0.8)' : 
      'rgba(234, 179, 8, 0.8)';

    const config: ChartConfiguration = {
      type: chartType === 'candlestick' ? 'bar' : 'line',
      data: {
        datasets: [
          chartType === 'candlestick' ? {
            label: 'Bitcoin Price',
            data: candlestickData.map(candle => ({
              x: candle.x,
              y: [candle.l, candle.o, candle.c, candle.h], // [low, open, close, high]
            })),
            backgroundColor: (ctx: any) => {
              const candle = candlestickData[ctx.dataIndex];
              return candle.c >= candle.o ? 'rgba(34, 197, 94, 0.8)' : 'rgba(239, 68, 68, 0.8)';
            },
            borderColor: (ctx: any) => {
              const candle = candlestickData[ctx.dataIndex];
              return candle.c >= candle.o ? 'rgb(34, 197, 94)' : 'rgb(239, 68, 68)';
            },
            borderWidth: 1,
            barThickness: 'flex',
            maxBarThickness: 8,
          } : {
            label: 'Bitcoin Price',
            data: lineData,
            borderColor: sentimentColor,
            backgroundColor: sentimentColor.replace('0.8', '0.1'),
            borderWidth: 2,
            fill: true,
            tension: 0.1,
            pointRadius: 0,
            pointHoverRadius: 4,
          },

          // RSI Overlay (if indicators available)
          ...(indicators?.indicators?.rsi ? [{
            label: 'RSI',
            data: ohlcData.map((item: any, index: number) => ({
              x: item.timestamp,
              y: indicators.indicators.rsi, // This would need to be array for each point
            })),
            borderColor: 'rgba(168, 85, 247, 0.8)',
            backgroundColor: 'rgba(168, 85, 247, 0.1)',
            borderWidth: 1,
            fill: false,
            yAxisID: 'rsi',
            pointRadius: 0,
          }] : []),

          // Volume bars
          {
            label: 'Volume',
            data: ohlcData.map((item: any) => ({
              x: item.timestamp,
              y: item.volume,
            })),
            backgroundColor: 'rgba(59, 130, 246, 0.3)',
            borderColor: 'rgba(59, 130, 246, 0.8)',
            borderWidth: 1,
            yAxisID: 'volume',
            type: 'bar',
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        interaction: {
          intersect: false,
          mode: 'index',
        },
        plugins: {
          legend: {
            position: 'top',
            labels: {
              color: 'rgb(156, 163, 175)',
              filter: (item) => item.text !== 'Volume', // Hide volume from legend
            },
          },
          tooltip: {
            backgroundColor: 'rgba(17, 24, 39, 0.95)',
            titleColor: 'rgb(243, 244, 246)',
            bodyColor: 'rgb(209, 213, 219)',
            borderColor: 'rgb(75, 85, 99)',
            borderWidth: 1,
            callbacks: {
              title: (context) => {
                return new Date(context[0].parsed.x).toLocaleString();
              },
              label: (context) => {
                if (context.dataset.label === 'Volume') {
                  return `Volume: ${context.parsed.y.toFixed(2)}`;
                } else if (chartType === 'candlestick') {
                  const candle = candlestickData[context.dataIndex];
                  return [
                    `Open: $${candle.o.toLocaleString()}`,
                    `High: $${candle.h.toLocaleString()}`,
                    `Low: $${candle.l.toLocaleString()}`,
                    `Close: $${candle.c.toLocaleString()}`,
                  ];
                } else {
                  return `Price: $${context.parsed.y.toLocaleString()}`;
                }
              },
            },
          },
        },
        scales: {
          x: {
            type: 'time',
            time: {
              displayFormats: {
                minute: 'HH:mm',
                hour: 'MMM dd HH:mm',
              },
            },
            grid: {
              color: 'rgba(75, 85, 99, 0.3)',
            },
            ticks: {
              color: 'rgb(156, 163, 175)',
            },
          },
          y: {
            type: 'linear',
            position: 'right',
            grid: {
              color: 'rgba(75, 85, 99, 0.3)',
            },
            ticks: {
              color: 'rgb(156, 163, 175)',
              callback: (value) => `$${Number(value).toLocaleString()}`,
            },
          },
          volume: {
            type: 'linear',
            position: 'left',
            max: Math.max(...ohlcData.map((d: any) => d.volume)) * 4,
            grid: {
              display: false,
            },
            ticks: {
              display: false,
            },
          },
          ...(indicators?.indicators?.rsi ? {
            rsi: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 100,
              grid: {
                display: false,
              },
              ticks: {
                color: 'rgba(168, 85, 247, 0.8)',
                callback: (value: any) => `${value}%`,
              },
            },
          } : {}),
        },
        animation: {
          duration: 750,
        },
      },
    };

    chartInstance.current = new Chart(ctx, config);

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [ohlcData, sentimentData, indicators, chartType]);

  const getSentimentBadge = () => {
    if (!sentimentData?.sentiment) return null;

    const sentiment = sentimentData.sentiment;
    const color = sentiment.direction === 'BULLISH' ? 'bg-green-600' : 
                  sentiment.direction === 'BEARISH' ? 'bg-red-600' : 'bg-yellow-600';

    return (
      <Badge className={color}>
        {sentiment.direction} {sentiment.score > 0 ? '+' : ''}{sentiment.score}
      </Badge>
    );
  };

  return (
    <Card className={`bg-gray-800 border-gray-700 ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-chart-candlestick text-orange-400" />
            <span>Live Bitcoin Chart</span>
            {getSentimentBadge()}
          </CardTitle>

          <div className="flex items-center space-x-2">
            {/* Chart Type Toggle */}
            <div className="flex rounded-lg bg-gray-700 p-1">
              <Button
                variant={chartType === 'candlestick' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setChartType('candlestick')}
                className="text-xs"
              >
                Candlestick
              </Button>
              <Button
                variant={chartType === 'line' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setChartType('line')}
                className="text-xs"
              >
                Line
              </Button>
            </div>

            {/* Timeframe Selector */}
            <div className="flex rounded-lg bg-gray-700 p-1">
              {['1m', '5m', '15m', '1h'].map((tf) => (
                <Button
                  key={tf}
                  variant={timeframe === tf ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setTimeframe(tf)}
                  className="text-xs"
                >
                  {tf}
                </Button>
              ))}
            </div>
          </div>
        </div>

        {/* Live Market Info */}
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-4">
            {ohlcData && ohlcData.length > 0 && (
              <>
                <div>
                  <span className="text-gray-400">Last: </span>
                  <span className="font-semibold text-white">
                    ${ohlcData[ohlcData.length - 1]?.close?.toLocaleString()}
                  </span>
                </div>
                <div>
                  <span className="text-gray-400">24h High: </span>
                  <span className="font-semibold text-green-400">
                    ${Math.max(...ohlcData.map((d: any) => d.high)).toLocaleString()}
                  </span>
                </div>
                <div>
                  <span className="text-gray-400">24h Low: </span>
                  <span className="font-semibold text-red-400">
                    ${Math.min(...ohlcData.map((d: any) => d.low)).toLocaleString()}
                  </span>
                </div>
              </>
            )}
          </div>

          {indicators?.indicators && (
            <div className="flex items-center space-x-3 text-xs">
              <div>
                <span className="text-gray-400">RSI: </span>
                <span className={`font-semibold ${
                  indicators.indicators.rsi < 30 ? 'text-green-400' :
                  indicators.indicators.rsi > 70 ? 'text-red-400' : 'text-yellow-400'
                }`}>
                  {indicators.indicators.rsi.toFixed(1)}
                </span>
              </div>
              <div>
                <span className="text-gray-400">Signal: </span>
                <span className={`font-semibold ${
                  indicators.signal?.strength > 70 ? 'text-green-400' :
                  indicators.signal?.strength > 40 ? 'text-yellow-400' : 'text-red-400'
                }`}>
                  {indicators.signal?.strength}%
                </span>
              </div>
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent>
        <div className="relative h-96">
          <canvas ref={chartRef} className="w-full h-full" />

          {/* Live Trading Indicators Overlay */}
          {sentimentData?.sentiment && (
            <div className="absolute top-4 left-4 bg-gray-900/80 rounded-lg p-2 backdrop-blur-sm">
              <div className="text-xs space-y-1">
                <div className="flex items-center space-x-2">
                  <div className={`w-2 h-2 rounded-full ${
                    sentimentData.sentiment.direction === 'BULLISH' ? 'bg-green-400' :
                    sentimentData.sentiment.direction === 'BEARISH' ? 'bg-red-400' : 'bg-yellow-400'
                  }`} />
                  <span className="text-gray-300">
                    {sentimentData.sentiment.direction} Sentiment
                  </span>
                </div>
                <div className="text-gray-400">
                  Strength: {sentimentData.sentiment.strength}
                </div>
              </div>
            </div>
          )}

          {/* Volume Spike Alert */}
          {sentimentData?.volumeProfile?.volumeSpike && (
            <div className="absolute top-4 right-4 bg-orange-500/20 border border-orange-500/30 rounded-lg p-2 backdrop-blur-sm">
              <div className="flex items-center space-x-2 text-xs">
                <i className="fas fa-exclamation-triangle text-orange-400" />
                <span className="text-orange-300">Volume Spike</span>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

