import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';

interface MarketSelectorProps {
  onSelectionChange: (exchange: string, symbol: string) => void;
  className?: string;
}

const EXCHANGES = [
  {
    id: 'kraken',
    name: 'Kraken',
    icon: 'ðŸ™',
    status: 'connected',
    description: 'Professional crypto exchange'
  },
  {
    id: 'binance',
    name: 'Binance',
    icon: 'ðŸŸ¡',
    status: 'coming-soon',
    description: 'Global crypto exchange'
  },
  {
    id: 'coinbase',
    name: 'Coinbase Pro',
    icon: 'ðŸ”µ',
    status: 'coming-soon',
    description: 'US-based exchange'
  },
  {
    id: 'bybit',
    name: 'Bybit',
    icon: 'âš¡',
    status: 'coming-soon',
    description: 'Derivatives trading'
  }
];

const CRYPTOCURRENCIES = [
  {
    symbol: 'BTC',
    name: 'Bitcoin',
    icon: 'â‚¿',
    pairs: ['BTCUSD', 'BTCEUR', 'BTCUSDT'],
    color: '#F7931A',
    status: 'active'
  },
  {
    symbol: 'ETH',
    name: 'Ethereum',
    icon: 'Îž',
    pairs: ['ETHUSD', 'ETHEUR', 'ETHUSDT', 'ETHBTC'],
    color: '#627EEA',
    status: 'active'
  },
  {
    symbol: 'ADA',
    name: 'Cardano',
    icon: 'â—ˆ',
    pairs: ['ADAUSD', 'ADAEUR', 'ADAUSDT', 'ADABTC'],
    color: '#0033AD',
    status: 'active'
  },
  {
    symbol: 'SOL',
    name: 'Solana',
    icon: 'â—‰',
    pairs: ['SOLUSD', 'SOLEUR', 'SOLUSDT', 'SOLBTC'],
    color: '#9945FF',
    status: 'active'
  },
  {
    symbol: 'DOT',
    name: 'Polkadot',
    icon: 'â—',
    pairs: ['DOTUSD', 'DOTEUR', 'DOTUSDT', 'DOTBTC'],
    color: '#E6007A',
    status: 'coming-soon'
  },
  {
    symbol: 'LINK',
    name: 'Chainlink',
    icon: 'ðŸ”—',
    pairs: ['LINKUSD', 'LINKEUR', 'LINKUSDT', 'LINKBTC'],
    color: '#375BD2',
    status: 'active'
  }
];

export function MarketSelector({ onSelectionChange, className }: MarketSelectorProps) {
  const [selectedExchange, setSelectedExchange] = useState('kraken');
  const [selectedCrypto, setSelectedCrypto] = useState('BTC');
  const [selectedPair, setSelectedPair] = useState('BTCUSD');

  const handleExchangeChange = (exchangeId: string) => {
    setSelectedExchange(exchangeId);
    onSelectionChange(exchangeId, selectedPair);
  };

  const handleCryptoChange = (cryptoSymbol: string) => {
    setSelectedCrypto(cryptoSymbol);
    const crypto = CRYPTOCURRENCIES.find(c => c.symbol === cryptoSymbol);
    if (crypto && crypto.pairs.length > 0) {
      setSelectedPair(crypto.pairs[0]);
      onSelectionChange(selectedExchange, crypto.pairs[0]);
    }
  };

  const handlePairChange = (pair: string) => {
    setSelectedPair(pair);
    onSelectionChange(selectedExchange, pair);
  };

  const selectedCryptoData = CRYPTOCURRENCIES.find(c => c.symbol === selectedCrypto);

  return (
    <Card className={`cosmic-card ${className}`}>
      <CardHeader>
        <CardTitle className="flex items-center space-x-3" style={{fontFamily: 'Orbitron', letterSpacing: '1px'}}>
          <i className="fas fa-chart-line text-accent"></i>
          <span className="text-primary">MARKET SELECTION</span>
          <i className="fas fa-globe text-accent ml-auto"></i>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Exchange Selection */}
        <div className="space-y-3">
          <label className="text-sm font-medium text-muted-foreground" style={{fontFamily: 'Rajdhani', letterSpacing: '0.5px'}}>
            Exchange Platform
          </label>
          <div className="grid grid-cols-2 gap-3">
            {EXCHANGES.map((exchange) => (
              <button
                key={exchange.id}
                onClick={() => exchange.status === 'connected' ? handleExchangeChange(exchange.id) : null}
                disabled={exchange.status !== 'connected'}
                className={`
                  cosmic-action-btn p-4 text-left transition-all duration-300
                  ${selectedExchange === exchange.id 
                    ? 'bg-gradient-to-r from-purple-600 via-blue-600 to-cyan-600 border-accent' 
                    : 'bg-gradient-to-r from-gray-700 via-gray-600 to-gray-700 border-gray-500'
                  }
                  ${exchange.status !== 'connected' ? 'opacity-50 cursor-not-allowed' : ''}
                `}
              >
                <div className="flex items-center space-x-3">
                  <span className="text-2xl">{exchange.icon}</span>
                  <div>
                    <div className="font-bold text-sm">{exchange.name}</div>
                    <div className="text-xs text-muted-foreground">{exchange.description}</div>
                  </div>
                </div>
                {exchange.status === 'coming-soon' && (
                  <Badge variant="secondary" className="mt-2 text-xs">Coming Soon</Badge>
                )}
                {exchange.status === 'connected' && selectedExchange === exchange.id && (
                  <Badge variant="default" className="mt-2 text-xs bg-accent text-accent-foreground">Active</Badge>
                )}
              </button>
            ))}
          </div>
        </div>

        <Separator />

        {/* Cryptocurrency Selection */}
        <div className="space-y-3">
          <label className="text-sm font-medium text-muted-foreground" style={{fontFamily: 'Rajdhani', letterSpacing: '0.5px'}}>
            Cryptocurrency
          </label>
          <div className="grid grid-cols-3 gap-2">
            {CRYPTOCURRENCIES.map((crypto) => (
              <button
                key={crypto.symbol}
                onClick={() => crypto.status === 'active' ? handleCryptoChange(crypto.symbol) : null}
                disabled={crypto.status !== 'active'}
                className={`
                  cosmic-action-btn p-3 text-center transition-all duration-300
                  ${selectedCrypto === crypto.symbol 
                    ? 'bg-gradient-to-r from-yellow-500 via-orange-500 to-red-500 border-accent' 
                    : 'bg-gradient-to-r from-gray-700 via-gray-600 to-gray-700 border-gray-500'
                  }
                  ${crypto.status !== 'active' ? 'opacity-50 cursor-not-allowed' : ''}
                `}
                style={crypto.status === 'active' ? { borderColor: crypto.color } : {}}
              >
                <div className="text-xl mb-1" style={{ color: crypto.color }}>{crypto.icon}</div>
                <div className="font-bold text-xs">{crypto.symbol}</div>
                <div className="text-xs text-muted-foreground truncate">{crypto.name}</div>
                {crypto.status === 'coming-soon' && (
                  <Badge variant="secondary" className="mt-1 text-xs">Soon</Badge>
                )}
              </button>
            ))}
          </div>
        </div>

        <Separator />

        {/* Trading Pair Selection */}
        <div className="space-y-3">
          <label className="text-sm font-medium text-muted-foreground" style={{fontFamily: 'Rajdhani', letterSpacing: '0.5px'}}>
            Trading Pair
          </label>
          <Select value={selectedPair} onValueChange={handlePairChange}>
            <SelectTrigger className="cosmic-card border-border">
              <SelectValue placeholder="Select trading pair" />
            </SelectTrigger>
            <SelectContent className="cosmic-card border-border">
              {selectedCryptoData?.pairs.map((pair) => (
                <SelectItem key={pair} value={pair} className="hover:bg-muted">
                  <div className="flex items-center space-x-2">
                    <span style={{ color: selectedCryptoData.color }}>{selectedCryptoData.icon}</span>
                    <span className="font-medium">{pair}</span>
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Current Selection Summary */}
        <div className="bg-muted/50 rounded-lg p-4 space-y-2">
          <div className="text-sm font-medium text-muted-foreground">Current Configuration</div>
          <div className="flex items-center justify-between">
            <span className="text-sm">Exchange:</span>
            <Badge variant="outline" className="border-accent text-accent">
              {EXCHANGES.find(e => e.id === selectedExchange)?.name}
            </Badge>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm">Trading Pair:</span>
            <Badge variant="outline" className="border-primary text-primary">
              {selectedPair}
            </Badge>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm">Status:</span>
            <Badge variant="default" className="bg-green-600 text-white">
              <i className="fas fa-circle mr-1 text-xs"></i>
              Connected
            </Badge>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/market-sentiment-p.content:
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';

interface MarketSentimentPanelProps {
  className?: string;
}

export function MarketSentimentPanel({ className }: MarketSentimentPanelProps) {
  const { data: sentimentData, isLoading } = useQuery({
    queryKey: ['/api/analytics/market-sentiment'],
    refetchInterval: 15000, // Update every 15 seconds
  });

  const { data: riskData } = useQuery({
    queryKey: ['/api/analytics/risk'],
    refetchInterval: 30000,
  });

  if (isLoading) {
    return (
      <Card className={`bg-gray-800 border-gray-700 ${className}`}>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-chart-line text-blue-400 animate-pulse" />
            <span>Market Sentiment</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3 animate-pulse">
            <div className="h-4 bg-gray-700 rounded"></div>
            <div className="h-4 bg-gray-700 rounded w-3/4"></div>
            <div className="h-4 bg-gray-700 rounded w-1/2"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  const sentiment = sentimentData?.sentiment;
  const volumeProfile = sentimentData?.volumeProfile;
  const marketRegime = sentimentData?.marketRegime;

  const getSentimentColor = (direction: string) => {
    switch (direction) {
      case 'BULLISH':
        return 'text-green-400 bg-green-500/20 border-green-500/30';
      case 'BEARISH':
        return 'text-red-400 bg-red-500/20 border-red-500/30';
      default:
        return 'text-yellow-400 bg-yellow-500/20 border-yellow-500/30';
    }
  };

  const getRegimeColor = (type: string) => {
    switch (type) {
      case 'TRENDING':
        return 'text-blue-400';
      case 'RANGING':
        return 'text-purple-400';
      case 'VOLATILE':
        return 'text-orange-400';
      default:
        return 'text-gray-400';
    }
  };

  const getRiskColor = (level: string) => {
    switch (level) {
      case 'LOW':
        return 'text-green-400';
      case 'MEDIUM':
        return 'text-yellow-400';
      case 'HIGH':
        return 'text-red-400';
      default:
        return 'text-gray-400';
    }
  };

  return (
    <Card className={`bg-gray-800 border-gray-700 ${className}`}>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <i className="fas fa-chart-line text-blue-400" />
            <span>Market Sentiment</span>
          </div>
          {sentiment && (
            <Badge 
              variant="outline" 
              className={getSentimentColor(sentiment.direction)}
            >
              {sentiment.direction}
            </Badge>
          )}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {sentiment ? (
          <>
            {/* Main Sentiment Score */}
            <div className={`rounded-lg p-4 border ${getSentimentColor(sentiment.direction)}`}>
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center space-x-3">
                  <i className={`fas ${
                    sentiment.direction === 'BULLISH' ? 'fa-arrow-up' :
                    sentiment.direction === 'BEARISH' ? 'fa-arrow-down' :
                    'fa-arrows-alt-h'
                  } text-2xl`} />
                  <div>
                    <div className="text-lg font-bold">{sentiment.direction}</div>
                    <div className="text-sm opacity-75">Market Sentiment</div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-2xl font-bold">
                    {sentiment.score > 0 ? '+' : ''}{sentiment.score}
                  </div>
                  <div className="text-sm opacity-75">{sentiment.strength}</div>
                </div>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span>Sentiment Score</span>
                  <span>{sentiment.score}/100</span>
                </div>
                <Progress 
                  value={Math.abs(sentiment.score)} 
                  className="h-2 bg-gray-600"
                />
              </div>
            </div>

            {/* Sentiment Factors */}
            <div className="space-y-3">
              <div className="text-sm font-medium text-gray-300">Analysis Factors</div>
              <div className="space-y-2 max-h-32 overflow-y-auto custom-scrollbar">
                {sentiment.factors.map((factor, index) => (
                  <div key={index} className="bg-gray-700 rounded p-2">
                    <div className="flex items-start space-x-2">
                      <i className="fas fa-dot-circle text-blue-400 text-xs mt-1.5" />
                      <span className="text-sm text-gray-200">{factor}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <Separator className="bg-gray-600" />

            {/* Volume Analysis */}
            {volumeProfile && (
              <div className="space-y-3">
                <div className="text-sm font-medium text-gray-300">Volume Profile</div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="bg-gray-700 rounded p-3">
                    <div className="text-sm text-gray-400">Volume Trend</div>
                    <div className={`font-semibold ${
                      volumeProfile.volumeTrend === 'INCREASING' ? 'text-green-400' :
                      volumeProfile.volumeTrend === 'DECREASING' ? 'text-red-400' :
                      'text-yellow-400'
                    }`}>
                      {volumeProfile.volumeTrend}
                    </div>
                  </div>
                  <div className="bg-gray-700 rounded p-3">
                    <div className="text-sm text-gray-400">Breakout Signal</div>
                    <div className={`font-semibold ${
                      volumeProfile.breakoutConfirmation ? 'text-green-400' : 'text-gray-400'
                    }`}>
                      {volumeProfile.breakoutConfirmation ? 'CONFIRMED' : 'PENDING'}
                    </div>
                  </div>
                </div>

                {volumeProfile.volumeSpike && (
                  <div className="bg-orange-500/10 border border-orange-500/30 rounded-lg p-3">
                    <div className="flex items-center space-x-2">
                      <i className="fas fa-exclamation-circle text-orange-400" />
                      <span className="text-sm text-orange-300 font-medium">
                        Volume Spike Detected
                      </span>
                    </div>
                    <p className="text-xs text-orange-400 mt-1">
                      Current volume is significantly above average
                    </p>
                  </div>
                )}
              </div>
            )}

            <Separator className="bg-gray-600" />

            {/* Market Regime */}
            {marketRegime && (
              <div className="space-y-3">
                <div className="text-sm font-medium text-gray-300">Market Regime</div>
                <div className="bg-gray-700 rounded-lg p-3">
                  <div className="flex justify-between items-center mb-2">
                    <span className={`font-semibold ${getRegimeColor(marketRegime.type)}`}>
                      {marketRegime.type}
                    </span>
                    <span className="text-sm text-gray-400">
                      Strength: {marketRegime.strength}
                    </span>
                  </div>
                  <div className="text-xs text-gray-400">
                    {marketRegime.recommendation}
                  </div>
                </div>
              </div>
            )}

            <Separator className="bg-gray-600" />

            {/* Risk Metrics */}
            {riskData && (
              <div className="space-y-3">
                <div className="text-sm font-medium text-gray-300">Risk Assessment</div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="bg-gray-700 rounded p-3">
                    <div className="text-sm text-gray-400">Volatility</div>
                    <div className="text-lg font-bold text-white">
                      {riskData.riskMetrics.volatility.toFixed(1)}%
                    </div>
                  </div>
                  <div className="bg-gray-700 rounded p-3">
                    <div className="text-sm text-gray-400">Risk Level</div>
                    <div className={`text-lg font-bold ${getRiskColor(riskData.riskMetrics.riskLevel)}`}>
                      {riskData.riskMetrics.riskLevel}
                    </div>
                  </div>
                </div>

                {riskData.riskMetrics.drawdown > 5 && (
                  <div className="bg-red-500/10 border border-red-500/30 rounded-lg p-3">
                    <div className="flex items-center space-x-2">
                      <i className="fas fa-exclamation-triangle text-red-400" />
                      <span className="text-sm text-red-300 font-medium">
                        High Drawdown Warning
                      </span>
                    </div>
                    <p className="text-xs text-red-400 mt-1">
                      Current drawdown: {riskData.riskMetrics.drawdown.toFixed(1)}%
                    </p>
                  </div>
                )}
              </div>
            )}
          </>
        ) : (
          <div className="text-center text-gray-400 py-8">
            <i className="fas fa-chart-line text-4xl mb-4 opacity-50" />
            <p>No sentiment data available</p>
            <p className="text-sm">Analyzing market conditions...</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

