  { name: 'Platform', href: '/platform', icon: Layers },
];

export function Navigation() {
  const [location] = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="hidden md:flex items-center space-x-1 bg-muted/50 p-1 rounded-lg">
        {navigation.map((item) => {
          const Icon = item.icon;
          return (
            <Link key={item.name} href={item.href}>
              <Button
                variant={location === item.href ? 'default' : 'ghost'}
                size="sm"
                className={cn(
                  'flex items-center gap-2 px-2 py-1.5 text-sm',
                  location === item.href && 'bg-primary text-primary-foreground'
                )}
              >
                <Icon className="h-4 w-4" />
                {item.name}
              </Button>
            </Link>
          );
        })}
      </nav>

      {/* Mobile Navigation */}
      <div className="md:hidden">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="p-2"
        >
          {isMobileMenuOpen ? (
            <X className="h-5 w-5" />
          ) : (
            <Menu className="h-5 w-5" />
          )}
        </Button>

        {isMobileMenuOpen && (
          <div className="absolute top-full left-0 right-0 z-50 bg-background border rounded-lg shadow-lg mt-2 p-2">
            <div className="flex flex-col space-y-1">
              {navigation.map((item) => {
                const Icon = item.icon;
                return (
                  <Link key={item.name} href={item.href}>
                    <Button
                      variant={location === item.href ? 'default' : 'ghost'}
                      size="sm"
                      className={cn(
                        'w-full justify-start gap-2 px-2 py-1.5 text-sm',
                        location === item.href && 'bg-primary text-primary-foreground'
                      )}
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      <Icon className="h-4 w-4" />
                      {item.name}
                    </Button>
                  </Link>
                );
              })}
            </div>
          </div>
        )}
      </div>
    </>
  );
}

client/src/components/notification-syste.content:
import { useEffect, useState, createContext, useContext } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { formatDistanceToNow } from 'date-fns';

export interface Notification {
  id: string;
  type: 'trade' | 'signal' | 'scaling' | 'error' | 'info' | 'success';
  title: string;
  message: string;
  data?: any;
  timestamp: number;
  read: boolean;
}

const NotificationContext = createContext<{
  notifications: Notification[];
  addNotification: (notification: Omit<Notification, 'id' | 'timestamp' | 'read'>) => void;
  dismissNotification: (id: string) => void;
  markAsRead: (id: string) => void;
}>({
  notifications: [],
  addNotification: () => {},
  dismissNotification: () => {},
  markAsRead: () => {}
});

export function useNotifications() {
  const context = useContext(NotificationContext);
  if (!context) {
    // Return a default context if none exists
    return {
      notifications: [],
      addNotification: () => {},
      dismissNotification: () => {},
      markAsRead: () => {}
    };
  }
  return context;
}

export function NotificationSystem({ className }: { className?: string }) {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const unreadCount = notifications.filter(n => !n.read).length;

  const addNotification = (notification: Omit<Notification, 'id' | 'timestamp' | 'read'>) => {
    const newNotification: Notification = {
      ...notification,
      id: Math.random().toString(36).substr(2, 9),
      timestamp: Date.now(),
      read: false
    };
    setNotifications(prev => [newNotification, ...prev].slice(0, 50)); // Keep only latest 50
  };

  const dismissNotification = (id: string) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  const markAsRead = (id: string) => {
    setNotifications(prev => prev.map(n => n.id === id ? { ...n, read: true } : n));
  };

  // Auto-show panel when new notifications arrive
  useEffect(() => {
    if (unreadCount > 0) {
      setIsOpen(true);
    }
  }, [unreadCount]);

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'trade':
        return 'fas fa-exchange-alt text-blue-400';
      case 'signal':
        return 'fas fa-signal text-green-400';
      case 'scaling':
        return 'fas fa-arrow-up text-yellow-400';
      case 'error':
        return 'fas fa-exclamation-triangle text-red-400';
      default:
        return 'fas fa-info-circle text-gray-400';
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'trade':
        return 'border-blue-500/50 bg-blue-500/10';
      case 'signal':
        return 'border-green-500/50 bg-green-500/10';
      case 'scaling':
        return 'border-yellow-500/50 bg-yellow-500/10';
      case 'error':
        return 'border-red-500/50 bg-red-500/10';
      default:
        return 'border-gray-500/50 bg-gray-500/10';
    }
  };

  return (
    <NotificationContext.Provider 
      value={{ 
        notifications, 
        addNotification, 
        dismissNotification: dismissNotification, 
        markAsRead 
      }}
    >
      <div className={`fixed top-4 right-4 z-50 max-w-sm ${className}`}>
        {/* Notification Bell */}
        <div className="relative mb-4">
          <Button
            onClick={() => setIsOpen(!isOpen)}
            variant="outline"
            size="sm"
            className="relative bg-background/80 backdrop-blur-sm border-border/50"
          >
            <i className="fas fa-bell" />
            {unreadCount > 0 && (
              <Badge 
                variant="destructive" 
                className="absolute -top-2 -right-2 h-5 w-5 text-xs flex items-center justify-center"
              >
                {unreadCount}
              </Badge>
            )}
          </Button>
        </div>

        {/* Notification Panel */}
        {isOpen && (
          <Card className="bg-background/95 backdrop-blur-sm border-border/50 shadow-lg">
            <div className="p-4">
              <div className="flex items-center justify-between mb-3">
                <h3 className="font-semibold">Notifications</h3>
                <Button
                  onClick={() => setIsOpen(false)}
                  variant="ghost"
                  size="sm"
                >
                  <i className="fas fa-times" />
                </Button>
              </div>

              <div className="space-y-2 max-h-96 overflow-y-auto">
                {notifications.length === 0 ? (
                  <div className="text-center text-muted-foreground py-4">
                    No notifications
                  </div>
                ) : (
                  notifications.map((notification) => (
                    <div
                      key={notification.id}
                      className={`p-3 rounded-lg border ${getNotificationColor(notification.type)} ${
                        !notification.read ? 'opacity-100' : 'opacity-70'
                      }`}
                    >
                      <div className="flex items-start space-x-3">
                        <i className={getNotificationIcon(notification.type)} />
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <p className="text-sm font-medium truncate">
                              {notification.title}
                            </p>
                            <Button
                              onClick={() => dismissNotification(notification.id)}
                              variant="ghost"
                              size="sm"
                              className="h-6 w-6 p-0"
                            >
                              <i className="fas fa-times text-xs" />
                            </Button>
                          </div>
                          <p className="text-xs text-muted-foreground mt-1">
                            {notification.message}
                          </p>
                          <p className="text-xs text-muted-foreground mt-1">
                            {formatDistanceToNow(notification.timestamp)} ago
                          </p>
                        </div>
                      </div>
                      {!notification.read && (
                        <Button
                          onClick={() => markAsRead(notification.id)}
                          variant="ghost"
                          size="sm"
                          className="mt-2 text-xs"
                        >
                          Mark as read
                        </Button>
                      )}
                    </div>
                  ))
                )}
              </div>

              {notifications.length > 0 && (
                <div className="mt-3 pt-3 border-t border-border/50">
                  <Button
                    onClick={() => setNotifications([])}
                    variant="outline"
                    size="sm"
                    className="w-full"
                  >
                    Clear all
                  </Button>
                </div>
              )}
            </div>
          </Card>
        )}
      </div>
    </NotificationContext.Provider>
  );
}

