import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';

export function PerformanceMetrics({ className }: { className?: string }) {
  // Fetch performance data
  const { data: performance, isLoading: performanceLoading } = useQuery<any>({
    queryKey: ['/api/performance'],
    refetchInterval: 5000,
  });

  // Fetch bot status for scaling data
  const { data: botData, isLoading: botDataLoading } = useQuery<any>({
    queryKey: ['/api/bot/status'],
    refetchInterval: 2000,
  });

  // Show loading state while data is being fetched
  if (performanceLoading && botDataLoading) {
    return (
      <div className={`space-y-6 ${className}`}>
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-6">
            <div className="text-center text-gray-400">Loading performance metrics...</div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Use performance data from /api/performance endpoint if available, otherwise fall back to bot state
  const botPerformance = botData?.state?.performance || {};
  const performanceData = performance || {
    totalTrades: botPerformance.totalTrades || 0,
    winningTrades: botPerformance.winningTrades || 0,
    winRate: botPerformance.winRate || 0,
    consecutiveWins: botPerformance.consecutiveWins || 0,
    consecutiveLosses: botPerformance.consecutiveLosses || 0,
    profitLast24h: botPerformance.profitToday || 0,
    profitLastHour: botPerformance.profitPerHour || 0,
    tradesLast24h: botPerformance.tradesThisHour || 0,
    tradesLastHour: botPerformance.tradesThisHour || 0,
    portfolioValue: parseFloat(botData?.settings?.portfolioValue || '0')
  };

  const scaling = botData?.state?.scaling || {
    currentTier: 1,
    nextScaleTarget: 5,
    progressToNext: 0
  };

  const currentPositionSize = botData?.settings?.currentPositionSize || '0';
  const winRateColor = performanceData.winRate >= 60 ? 'text-green-400' : 
                      performanceData.winRate >= 40 ? 'text-yellow-400' : 'text-red-400';

  const profitColor = (value: number) => 
    value >= 0 ? 'text-green-400' : 'text-red-400';

  const formatProfit = (value: number) => 
    `${value >= 0 ? '+' : ''}$${value.toFixed(2)}`;

  const getScalingProgress = () => {
    if (scaling.nextScaleTarget === 0) return 100;
    return Math.min((scaling.progressToNext / scaling.nextScaleTarget) * 100, 100);
  };

  const getStreakColor = (consecutive: number, type: 'wins' | 'losses') => {
    if (type === 'wins') {
      return consecutive >= 3 ? 'text-green-400' : 'text-gray-400';
    } else {
      return consecutive >= 2 ? 'text-red-400' : 'text-gray-400';
    }
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Overall Performance */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-chart-line text-blue-400" />
            <span>Performance Overview</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-gray-700 rounded-lg p-3">
              <div className="text-sm text-gray-400 mb-1">Portfolio Value</div>
              <div className="text-xl font-bold text-white">
                ${performanceData.portfolioValue.toLocaleString()}
              </div>
            </div>

            <div className="bg-gray-700 rounded-lg p-3">
              <div className="text-sm text-gray-400 mb-1">Total Trades</div>
              <div className="text-xl font-bold text-white">
                {performanceData.totalTrades}
              </div>
            </div>

            <div className="bg-gray-700 rounded-lg p-3">
              <div className="text-sm text-gray-400 mb-1">Win Rate</div>
              <div className={`text-xl font-bold ${winRateColor}`}>
                {performanceData.winRate.toFixed(1)}%
              </div>
            </div>

            <div className="bg-gray-700 rounded-lg p-3">
              <div className="text-sm text-gray-400 mb-1">Winning Trades</div>
              <div className="text-xl font-bold text-green-400">
                {performanceData.winningTrades}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Recent Performance */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-clock text-green-400" />
            <span>Recent Performance</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {/* 24h Performance */}
            <div className="bg-gray-700 rounded-lg p-4">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-400">Last 24 Hours</span>
                <span className={`font-semibold ${profitColor(performanceData.profitLast24h)}`}>
                  {formatProfit(performanceData.profitLast24h)}
                </span>
              </div>
              <div className="text-xs text-gray-400">
                {performanceData.tradesLast24h} trades executed
              </div>
            </div>

            {/* 1h Performance */}
            <div className="bg-gray-700 rounded-lg p-4">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-400">Last Hour</span>
                <span className={`font-semibold ${profitColor(performanceData.profitLastHour)}`}>
                  {formatProfit(performanceData.profitLastHour)}
                </span>
              </div>
              <div className="text-xs text-gray-400">
                {performanceData.tradesLastHour} trades executed
              </div>
            </div>

            {/* Streak Information */}
            <div className="grid grid-cols-2 gap-3">
              <div className="bg-gray-700 rounded-lg p-3">
                <div className="text-sm text-gray-400 mb-1">Win Streak</div>
                <div className={`text-lg font-bold ${getStreakColor(performanceData.consecutiveWins, 'wins')}`}>
                  {performanceData.consecutiveWins}
                </div>
              </div>
              <div className="bg-gray-700 rounded-lg p-3">
                <div className="text-sm text-gray-400 mb-1">Loss Streak</div>
                <div className={`text-lg font-bold ${getStreakColor(performanceData.consecutiveLosses, 'losses')}`}>
                  {performanceData.consecutiveLosses}
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Scaling Progress */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-arrow-up text-yellow-400" />
            <span>Scaling Progress</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="bg-gray-700 rounded-lg p-4">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-400">Current Position Size</span>
                <span className="text-lg font-bold text-white">
                  ${currentPositionSize}
                </span>
              </div>
              <div className="text-xs text-gray-400">
                Tier {scaling.currentTier}
              </div>
            </div>

            {scaling.nextScaleTarget > 0 && (
              <div className="bg-gray-700 rounded-lg p-4">
                <div className="flex justify-between items-center mb-3">
                  <span className="text-sm text-gray-400">Next Scale</span>
                  <span className="text-sm text-blue-400">
                    {scaling.progressToNext} / {scaling.nextScaleTarget} wins
                  </span>
                </div>
                <Progress 
                  value={getScalingProgress()} 
                  className="h-2 bg-gray-600"
                />
                <div className="text-xs text-gray-400 mt-2">
                  {scaling.nextScaleTarget - scaling.progressToNext} more wins to scale up
                </div>
              </div>
            )}

            {scaling.nextScaleTarget === 0 && (
              <div className="bg-gradient-to-r from-yellow-500/20 to-orange-500/20 rounded-lg p-4 border border-yellow-500/30">
                <div className="flex items-center space-x-2">
                  <i className="fas fa-crown text-yellow-400" />
                  <span className="text-yellow-400 font-semibold">Maximum Tier Reached</span>
                </div>
                <div className="text-xs text-gray-400 mt-1">
                  You've reached the maximum position size
                </div>
              </div>
            )}

            {/* Hot Streak Indicator */}
            {performance.consecutiveWins >= 5 && (
              <div className="bg-gradient-to-r from-orange-500/20 to-red-500/20 rounded-lg p-4 border border-orange-500/30">
                <div className="flex items-center space-x-2">
                  <i className="fas fa-fire text-orange-400" />
                  <span className="text-orange-400 font-semibold">Hot Streak Active!</span>
                </div>
                <div className="text-xs text-gray-400 mt-1">
                  Next win triggers 3x position scaling bonus
                </div>
              </div>
            )}

            {/* Risk Warning */}
            {performance.consecutiveLosses >= 2 && (
              <div className="bg-gradient-to-r from-red-500/20 to-red-600/20 rounded-lg p-4 border border-red-500/30">
                <div className="flex items-center space-x-2">
                  <i className="fas fa-exclamation-triangle text-red-400" />
                  <span className="text-red-400 font-semibold">Risk Alert</span>
                </div>
                <div className="text-xs text-gray-400 mt-1">
                  Emergency scale-down active after consecutive losses
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/simple-live-chart..content:
import { useEffect, useRef } from 'react';
import { Chart, ChartConfiguration } from 'chart.js/auto';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface SimpleLiveChartProps {
  className?: string;
}

export function SimpleLiveChart({ className }: SimpleLiveChartProps) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstance = useRef<Chart | null>(null);

  // Fetch real-time OHLC data
  const { data: ohlcData, isLoading } = useQuery({
    queryKey: ['/api/market/ohlc'],
    refetchInterval: 5000,
  });

  // Fetch AI predictions
  const { data: aiData } = useQuery({
    queryKey: ['/api/ai/prediction'],
    refetchInterval: 15000,
  });

  // Fetch technical indicators
  const { data: indicatorData } = useQuery({
    queryKey: ['/api/market/indicators'],
    refetchInterval: 10000,
  });

  useEffect(() => {
    if (!chartRef.current || !ohlcData || !Array.isArray(ohlcData)) return;

    const ctx = chartRef.current.getContext('2d');
    if (!ctx) return;

    // Destroy existing chart
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // Prepare line chart data
    const chartData = ohlcData.map((item: any) => ({
      x: item.timestamp,
      y: item.close,
    }));

    // Get sentiment-based color
    const bullishColor = 'rgba(34, 197, 94, 0.8)';
    const bearishColor = 'rgba(239, 68, 68, 0.8)';
    const neutralColor = 'rgba(234, 179, 8, 0.8)';

    let sentimentColor = neutralColor;
    if (aiData?.prediction?.priceDirection === 'UP') {
      sentimentColor = bullishColor;
    } else if (aiData?.prediction?.priceDirection === 'DOWN') {
      sentimentColor = bearishColor;
    }

    const config: ChartConfiguration = {
      type: 'line',
      data: {
        datasets: [
          {
            label: 'Bitcoin Price',
            data: chartData,
            borderColor: sentimentColor,
            backgroundColor: sentimentColor.replace('0.8', '0.1'),
            borderWidth: 2,
            fill: true,
            tension: 0.2,
            pointRadius: 0,
            pointHoverRadius: 6,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        interaction: {
          intersect: false,
          mode: 'index',
        },
        plugins: {
          legend: {
            display: false,
          },
          tooltip: {
            backgroundColor: 'rgba(17, 24, 39, 0.95)',
            titleColor: 'rgb(243, 244, 246)',
            bodyColor: 'rgb(209, 213, 219)',
            borderColor: 'rgb(75, 85, 99)',
            borderWidth: 1,
            callbacks: {
              title: (context) => {
                return new Date(context[0].parsed.x).toLocaleString();
              },
              label: (context) => {
                return `Price: $${context.parsed.y.toLocaleString()}`;
              },
            },
          },
        },
        scales: {
          x: {
            type: 'time',
            time: {
              displayFormats: {
                minute: 'HH:mm',
                hour: 'MMM dd HH:mm',
              },
            },
            grid: {
              color: 'rgba(75, 85, 99, 0.3)',
            },
            ticks: {
              color: 'rgb(156, 163, 175)',
            },
          },
          y: {
            type: 'linear',
            position: 'right',
            grid: {
              color: 'rgba(75, 85, 99, 0.3)',
            },
            ticks: {
              color: 'rgb(156, 163, 175)',
              callback: (value) => `$${Number(value).toLocaleString()}`,
            },
          },
        },
        animation: {
          duration: 750,
        },
      },
    };

    chartInstance.current = new Chart(ctx, config);

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [ohlcData, aiData]);

  const getSentimentBadge = () => {
    if (!aiData?.prediction) return null;

    const prediction = aiData.prediction;
    let color = 'bg-yellow-600';
    let direction = prediction.priceDirection;

    if (direction === 'UP') {
      color = 'bg-green-600';
      direction = 'BULLISH';
    } else if (direction === 'DOWN') {
      color = 'bg-red-600';
      direction = 'BEARISH';
    }

    return (
      <Badge className={color}>
        {direction} {prediction.confidence}%
      </Badge>
    );
  };

  const getRSIStatus = () => {
    if (!indicatorData?.indicators?.rsi) return null;

    const rsi = indicatorData.indicators.rsi;
    let status = 'Neutral';
    let color = 'text-yellow-400';

    if (rsi < 30) {
      status = 'Oversold (Buy Signal)';
      color = 'text-green-400';
    } else if (rsi > 70) {
      status = 'Overbought (Sell Signal)';
      color = 'text-red-400';
    }

    return (
      <div className="text-xs">
        <span className="text-gray-400">RSI: </span>
        <span className={`font-semibold ${color}`}>
          {rsi.toFixed(1)} - {status}
        </span>
      </div>
    );
  };

  if (isLoading) {
    return (
      <Card className={`bg-gray-800 border-gray-700 ${className}`}>
        <CardHeader>
          <CardTitle>Live Bitcoin Chart</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-96 flex items-center justify-center">
            <div className="text-gray-400">Loading live data...</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`bg-gray-800 border-gray-700 ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-chart-line text-orange-400" />
            <span>Live Bitcoin Chart</span>
            {getSentimentBadge()}
          </CardTitle>

          <div className="text-right text-sm">
            {ohlcData && Array.isArray(ohlcData) && ohlcData.length > 0 && (
              <div className="space-y-1">
                <div>
                  <span className="text-gray-400">Price: </span>
                  <span className="font-semibold text-white">
                    ${ohlcData[ohlcData.length - 1]?.close?.toLocaleString()}
                  </span>
                </div>
                {getRSIStatus()}
              </div>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent>
        <div className="relative h-96">
