import { useEffect, useRef, useState } from 'react';
import { Chart, ChartConfiguration, registerables } from 'chart.js';
import 'chartjs-adapter-date-fns';

Chart.register(...registerables);

interface PriceData {
  timestamp: number;
  open: number;
  high: number;
  low: number;
  close: number;
  volume: number;
}

interface TradeMarker {
  timestamp: number;
  price: number;
  type: 'BUY' | 'SELL';
  amount: number;
  signal: string;
}

interface TradingChartProps {
  priceData?: PriceData[];
  tradeMarkers?: TradeMarker[];
  indicators?: {
    rsi: number;
    sma20: number;
    sma50: number;
  };
  className?: string;
}

export function TradingChart({ priceData = [], tradeMarkers = [], indicators }: TradingChartProps) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstance = useRef<Chart | null>(null);
  const [timeframe, setTimeframe] = useState('1m');

  useEffect(() => {
    if (!chartRef.current || !priceData.length) return;

    const ctx = chartRef.current.getContext('2d');
    if (!ctx) return;

    // Destroy existing chart
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // Prepare data for Chart.js
    const chartData = priceData.map(d => ({
      x: d.timestamp,
      y: d.close
    }));

    const sma20Data = indicators ? priceData.map(d => ({
      x: d.timestamp,
      y: indicators.sma20
    })) : [];

    const sma50Data = indicators ? priceData.map(d => ({
      x: d.timestamp,
      y: indicators.sma50
    })) : [];

    const config: ChartConfiguration = {
      type: 'line',
      data: {
        datasets: [
          {
            label: 'BTC Price',
            data: chartData,
            borderColor: '#3B82F6',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            borderWidth: 2,
            fill: true,
            tension: 0.1,
            pointRadius: 0,
            pointHoverRadius: 4,
          },
          ...(indicators ? [
            {
              label: 'SMA 20',
              data: sma20Data,
              borderColor: '#10B981',
              backgroundColor: 'transparent',
              borderWidth: 1,
              fill: false,
              pointRadius: 0,
            },
            {
              label: 'SMA 50',
              data: sma50Data,
              borderColor: '#F59E0B',
              backgroundColor: 'transparent',
              borderWidth: 1,
              fill: false,
              pointRadius: 0,
            }
          ] : [])
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        interaction: {
          intersect: false,
          mode: 'index'
        },
        plugins: {
