import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { ScrollArea } from '@/components/ui/scroll-area';
import { 
  BookOpen, 
  PenTool, 
  Calendar, 
  Target,
  CheckCircle,
  AlertTriangle,
  Clock,
  Users,
  Activity,
  TrendingUp,
  BarChart3,
  Settings,
  Shield,
  Database,
  Server,
  Zap,
  Globe,
  Brain,
  Heart
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface JournalEntry {
  id: string;
  title: string;
  content: string;
  category: 'development' | 'operations' | 'strategy' | 'issues' | 'achievements' | 'notes';
  priority: 'low' | 'medium' | 'high' | 'critical';
  status: 'draft' | 'published' | 'archived';
  tags: string[];
  author: string;
  createdAt: string;
  updatedAt: string;
  attachments?: string[];
}

interface AdminTask {
  id: string;
  title: string;
  description: string;
  category: 'maintenance' | 'feature' | 'bug' | 'security' | 'optimization';
  status: 'pending' | 'in-progress' | 'completed' | 'blocked';
  priority: 'low' | 'medium' | 'high' | 'critical';
  assignedTo: string;
  dueDate: string;
  progress: number;
  dependencies: string[];
}

interface SystemMetric {
  name: string;
  value: string | number;
  status: 'good' | 'warning' | 'critical';
  lastUpdated: string;
  trend: 'up' | 'down' | 'stable';
}

export default function AdminJournal() {
  const [newEntry, setNewEntry] = useState({
    title: '',
    content: '',
    category: 'notes' as const,
    priority: 'medium' as const,
    tags: '',
  });
  const [selectedEntry, setSelectedEntry] = useState<string>('');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [activeTab, setActiveTab] = useState('journal');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch journal entries
  const { data: entries, isLoading } = useQuery({
    queryKey: ['/api/admin/journal'],
    refetchInterval: 30000,
  });

  // Fetch admin tasks
  const { data: tasks } = useQuery({
    queryKey: ['/api/admin/tasks'],
    refetchInterval: 15000,
  });

  // Fetch system metrics
  const { data: metrics } = useQuery({
    queryKey: ['/api/admin/metrics'],
    refetchInterval: 10000,
  });

  // Create journal entry mutation
  const createEntryMutation = useMutation({
    mutationFn: (entryData: any) => apiRequest('/api/admin/journal', 'POST', entryData),
