} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface CafeItem {
  id: string;
  name: string;
  description: string;
  price: number;
  category: 'coffee' | 'food' | 'pastry' | 'special' | 'mining-boost' | 'hardware' | 'software';
  available: boolean;
  rating: number;
  orders: number;
  image?: string;
}

interface CafeOrder {
  id: string;
  items: { item: CafeItem; quantity: number }[];
  total: number;
  status: 'pending' | 'preparing' | 'ready' | 'completed';
  customerName: string;
  orderTime: string;
  estimatedReady: string;
}

interface CafeEvent {
  id: string;
  title: string;
  description: string;
  date: string;
  time: string;
  capacity: number;
  registered: number;
  type: 'workshop' | 'meetup' | 'special' | 'live-music';
}

interface PoolConnection {
  poolId: string;
  poolName: string;
  status: 'connected' | 'disconnected' | 'connecting' | 'error';
  shares: { accepted: number; rejected: number };
  lastActivity: number;
  websocketUrl?: string;
  isPrimary: boolean;
}

interface MiningRig {
  id: string;
  name: string;
  type: 'web' | 'python' | 'wasm' | 'cloud' | 'asic' | 'cpu' | 'gpu' | 'java' | 'browser' | 'javascript' | 'webassembly' | 'tera-ai' | 'cluster' | 'lamam';
  status: 'online' | 'offline' | 'starting' | 'stopping' | 'error' | 'loading' | 'validating' | 'configuring';
  hashrate: number;
  power: number;
  temperature?: number;
  pool: string; // Primary pool name for display
  address: string;
  shares: { accepted: number; rejected: number }; // Combined shares from all pools
  balance: number;
  uptime: number;
  logs: string[];
  enabled: boolean;
  algorithm?: string;
  threads?: number;
  totalHashes?: number;
  efficiency?: number;
  aiModel?: string;
  clusterNodes?: number;
  poolConnections: PoolConnection[]; // Multiple pool connections
  activePool: string; // Currently active pool for mining
  failoverEnabled: boolean;
  loadBalancing: boolean;
}

interface MiningPool {
  id: string;
  name: string;
  url: string;
  port: number;
  status: 'connected' | 'disconnected' | 'error';
  hashRate: number;
  workers: number;
  fees: number;
  isCustom: boolean;
}

export default function Cafe() {
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [cart, setCart] = useState<{ item: CafeItem; quantity: number }[]>([]);
  const [customerName, setCustomerName] = useState('');
  const [activeTab, setActiveTab] = useState('mining');
  const [selectedMiningType, setSelectedMiningType] = useState<'all' | MiningRig['type']>('all');
  const [uploading, setUploading] = useState(false);
  const [pythonFile, setPythonFile] = useState<File | null>(null);
  const [newMinerName, setNewMinerName] = useState('');
  const [newMinerAlgorithm, setNewMinerAlgorithm] = useState('RandomX');
  const [newMinerPool, setNewMinerPool] = useState('KLOUDBUGSCAFE POOL');
  const [editingMiner, setEditingMiner] = useState<string | null>(null);
  const [editingName, setEditingName] = useState('');
  const [btcTestAddress, setBtcTestAddress] = useState('bc1qj93mnxgm0xuwyh3jvvqurjxjyq8uktg4y0sad6');
  const [selectedPool, setSelectedPool] = useState('kloudbugscafe-pool');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch cafe menu
  const { data: menu, isLoading } = useQuery({
    queryKey: ['/api/cafe/menu'],
    refetchInterval: 30000,
  });

  // Fetch current orders
  const { data: orders } = useQuery({
    queryKey: ['/api/cafe/orders'],
    refetchInterval: 5000,
  });

  // Fetch cafe events
  const { data: events } = useQuery({
    queryKey: ['/api/cafe/events'],
  });

  // Place order mutation
  const placeOrderMutation = useMutation({
    mutationFn: (orderData: any) => apiRequest('/api/cafe/orders', 'POST', orderData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cafe/orders'] });
      setCart([]);
      setCustomerName('');
      toast({ title: "Order placed successfully", description: "Your order is being prepared!" });
    },
  });

  // Register for event mutation
  const registerEventMutation = useMutation({
    mutationFn: (data: { eventId: string; customerName: string }) => 
      apiRequest('/api/cafe/events/register', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cafe/events'] });
      toast({ title: "Registration successful", description: "See you at the event!" });
    },
  });

  // Mock data
  const defaultMenu: CafeItem[] = [
    // Mining Enhancement Items
    {
      id: 'boost-1',
      name: 'Hash Rate Booster',
      description: 'Increase your mining hash rate by 15% for 24 hours',
      price: 25.00,
      category: 'mining-boost',
      available: true,
      rating: 4.9,
      orders: 567
    },
    {
      id: 'boost-2',
      name: 'Cooling Optimizer',
      description: 'Advanced cooling system upgrade for better performance',
      price: 45.00,
      category: 'mining-boost',
      available: true,
      rating: 4.8,
      orders: 234
    },
    {
      id: 'boost-3',
      name: 'Power Efficiency Pack',
      description: 'Reduce power consumption by 20% while maintaining hash rate',
      price: 35.00,
      category: 'mining-boost',
      available: true,
      rating: 4.7,
      orders: 345
    },
    {
      id: 'hardware-1',
      name: 'Mining Rig Upgrade Kit',
      description: 'Complete hardware upgrade package with GPU enhancement',
      price: 150.00,
      category: 'hardware',
      available: true,
      rating: 4.9,
      orders: 89
    },
    {
      id: 'hardware-2',
      name: 'ASIC Miner S19 Pro',
      description: 'Professional-grade ASIC miner for Bitcoin mining',
      price: 2500.00,
      category: 'hardware',
      available: true,
      rating: 4.8,
      orders: 12
    },
    {
      id: 'software-1',
      name: 'Mining Pool Optimizer',
      description: 'AI-powered pool switching for maximum profitability',
      price: 75.00,
      category: 'software',
      available: true,
      rating: 4.8,
      orders: 178
    },
    {
      id: 'software-2',
      name: 'Advanced Mining Analytics',
      description: 'Real-time mining performance analytics and insights',
      price: 50.00,
      category: 'software',
      available: true,
      rating: 4.7,
      orders: 203
    },
    // Traditional Cafe Items
    {
      id: 'coffee-1',
      name: 'KLOUD Espresso',
      description: 'Signature dark roast blend with cosmic energy',
      price: 4.50,
      category: 'coffee',
      available: true,
      rating: 4.8,
      orders: 1247
    },
    {
      id: 'coffee-2',
      name: 'TERA Latte',
      description: 'Smooth latte with justice-inspired foam art',
      price: 5.25,
      category: 'coffee',
      available: true,
      rating: 4.9,
      orders: 892
    },
    {
      id: 'food-1',
      name: 'Miner\'s Breakfast',
      description: 'Power breakfast for crypto warriors',
      price: 12.00,
      category: 'food',
      available: true,
      rating: 4.7,
      orders: 445
    }
  ];

  const defaultOrders: CafeOrder[] = [
    {
      id: 'order-1',
      items: [{ item: defaultMenu[0], quantity: 2 }],
      total: 9.00,
      status: 'preparing',
      customerName: 'Alex',
      orderTime: '09:15 AM',
      estimatedReady: '09:25 AM'
    }
  ];

  const defaultEvents: CafeEvent[] = [
    {
      id: 'event-1',
      title: 'Crypto Trading Workshop',
      description: 'Learn advanced trading strategies with KLOUD BOT PRO',
      date: '2024-02-15',
      time: '2:00 PM',
      capacity: 25,
      registered: 18,
      type: 'workshop'
    },
    {
      id: 'event-2',
      title: 'AI Ethics Discussion',
      description: 'Community discussion on AI in social justice',
      date: '2024-02-20',
      time: '6:00 PM',
      capacity: 40,
      registered: 32,
      type: 'meetup'
    }
  ];

  // Mining Pools (from your existing infrastructure)
  const miningPools: MiningPool[] = [
    {
      id: 'kloudbugscafe-pool',
      name: 'KLOUDBUGSCAFE POOL',
      url: 'stratum+tcp://kloudbugscafe.pool',
      port: 4444,
      status: 'connected',
      hashRate: 450,
      workers: 4,
      fees: 1.5,
      isCustom: true
    },
    {
      id: 'tera-social-justice-pool',
      name: 'TERA SOCIAL JUSTICE POOL',
      url: 'stratum+tcp://terasocial.pool',
      port: 3333,
      status: 'connected',
      hashRate: 490,
      workers: 4,
      fees: 0.5,
      isCustom: true
    },
    {
      id: 'unminable-btc',
      name: 'Unminable BTC Pool',
      url: 'stratum+tcp://btc.unmineable.com',
      port: 3333,
      status: 'connected',
      hashRate: 0,
      workers: 0,
      fees: 1.0,
      isCustom: false
    }
  ];

  // Upload Python miner mutation
  const uploadPythonMinerMutation = useMutation({
    mutationFn: (formData: FormData) => 
      fetch('/api/webmining/upload-python', {
        method: 'POST',
        body: formData
      }).then(res => res.json()),
    onSuccess: () => {
      toast({ 
        title: "Python Miner Uploaded", 
        description: "Python miner uploaded and validated successfully" 
      });
      setUploading(false);
      setPythonFile(null);
    },
    onError: () => {
      toast({ 
        title: "Upload Failed", 
        description: "Failed to upload Python miner",
        variant: "destructive"
      });
      setUploading(false);
    }
  });

  // Create new web miner mutation
  const createWebMinerMutation = useMutation({
    mutationFn: (data: { name: string; algorithm: string; pool: string; type: string }) => 
      apiRequest('/api/webmining/create', 'POST', data),
    onSuccess: () => {
      toast({ title: "Miner Created", description: "New miner created successfully" });
      setNewMinerName('');
      setNewMinerAlgorithm('RandomX');
      setNewMinerPool('KLOUDBUGSCAFE POOL');
    }
  });

  // Delete miner mutation
  const deleteMinerMutation = useMutation({
    mutationFn: (minerId: string) => apiRequest('/api/mining/delete', 'DELETE', { minerId }),
    onSuccess: (_, minerId) => {
      setMiningRigs(prev => prev.filter(rig => rig.id !== minerId));
      toast({ title: "Miner Deleted", description: "Miner removed successfully" });
    }
  });

  // Rename miner mutation
  const renameMinerMutation = useMutation({
    mutationFn: (data: { minerId: string; newName: string }) => 
      apiRequest('/api/mining/rename', 'PUT', data),
    onSuccess: (_, variables) => {
      setMiningRigs(prev => prev.map(rig => 
        rig.id === variables.minerId ? { ...rig, name: variables.newName } : rig
      ));
      setEditingMiner(null);
      setEditingName('');
      toast({ title: "Miner Renamed", description: "Miner name updated successfully" });
    }
  });

  // Handle Python file upload
  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.name.endsWith('.py')) {
      toast({ 
        title: "Invalid File", 
        description: "Only Python (.py) files are allowed",
        variant: "destructive"
      });
      return;
    }

    setPythonFile(file);
    setUploading(true);
    const formData = new FormData();
    formData.append('pythonMiner', file);
    uploadPythonMinerMutation.mutate(formData);
  };

  // Helper functions
  const createNewMiner = (type: MiningRig['type']) => {
    const existingCount = miningRigs.filter(rig => rig.type === type).length;
    if (existingCount >= 25) {
      toast({ 
        title: "Limit Reached", 
        description: `Maximum 25 ${type} miners allowed`,
        variant: "destructive"
      });
      return;
    }

    // Create connections to all available pools
    const poolConnections: PoolConnection[] = miningPools.map((pool, index) => ({
      poolId: pool.id,
      poolName: pool.name,
      status: 'disconnected',
      shares: { accepted: 0, rejected: 0 },
      lastActivity: 0,
      websocketUrl: `ws://localhost:${pool.port}/mining-ws`,
      isPrimary: index === 0 // First pool is primary
    }));

    const newMiner: MiningRig = {
      id: `${type}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      name: newMinerName || `${type.toUpperCase()} Miner ${existingCount + 1}`,
      type,
      status: 'offline',
      hashrate: Math.random() * 100 + 10,
      power: Math.random() * 200 + 50,
      temperature: type === 'cloud' ? undefined : Math.floor(Math.random() * 20) + 50,
      pool: newMinerPool,
      address: btcTestAddress,
      shares: { accepted: 0, rejected: 0 },
      balance: 0,
      uptime: 0,
      logs: [`${type} miner initialized`, 'Multi-pool connections ready', 'Ready to start'],
      enabled: true,
      algorithm: newMinerAlgorithm,
      threads: Math.floor(Math.random() * 8) + 1,
      totalHashes: 0,
      efficiency: type.includes('ai') ? Math.random() * 5 + 95 : Math.random() * 10 + 85,
      aiModel: type === 'tera-ai' ? 'TERA-Mining-Optimizer-v2.1' : 
               type === 'lamam' ? 'Llama-Mining-Specialist-v1.7' : undefined,
      clusterNodes: type === 'cluster' ? Math.floor(Math.random() * 8) + 4 : undefined,
      poolConnections,
      activePool: poolConnections[0]?.poolId || 'kloudbugscafe-pool',
      failoverEnabled: true,
      loadBalancing: false
    };

    setMiningRigs(prev => [...prev, newMiner]);
    toast({ title: "Miner Created", description: `${newMiner.name} created with ${poolConnections.length} pool connections` });
    setNewMinerName('');
  };

  const deleteMiner = (minerId: string) => {
    deleteMinerMutation.mutate(minerId);
  };

  const startRename = (miner: MiningRig) => {
    setEditingMiner(miner.id);
    setEditingName(miner.name);
  };

  const saveRename = () => {
    if (editingMiner && editingName.trim()) {
      renameMinerMutation.mutate({ minerId: editingMiner, newName: editingName.trim() });
    }
  };

  const cancelRename = () => {
    setEditingMiner(null);
    setEditingName('');
  };

  // Multi-pool connection management
  const connectToAllPools = (minerId: string) => {
    setMiningRigs(prev => prev.map(rig => {
      if (rig.id === minerId) {
        const updatedConnections = rig.poolConnections.map(conn => ({
          ...conn,
          status: 'connecting' as const
        }));

        // Simulate WebSocket connections
        setTimeout(() => {
          setMiningRigs(prev2 => prev2.map(rig2 => {
            if (rig2.id === minerId) {
              return {
                ...rig2,
                poolConnections: rig2.poolConnections.map(conn => ({
                  ...conn,
                  status: 'connected' as const,
                  lastActivity: Date.now()
                }))
              };
            }
            return rig2;
          }));
        }, 2000);

        return {
          ...rig,
          poolConnections: updatedConnections,
          logs: [...rig.logs, `Connecting to ${updatedConnections.length} pools via WebSocket`]
        };
      }
      return rig;
    }));

    toast({ 
      title: "Multi-Pool Connection", 
      description: "Establishing WebSocket connections to all pools" 
    });
  };

  const disconnectFromAllPools = (minerId: string) => {
    setMiningRigs(prev => prev.map(rig => {
      if (rig.id === minerId) {
        const updatedConnections = rig.poolConnections.map(conn => ({
          ...conn,
          status: 'disconnected' as const,
          lastActivity: 0
        }));

        return {
          ...rig,
          poolConnections: updatedConnections,
          logs: [...rig.logs, 'Disconnected from all pools']
        };
      }
      return rig;
    }));

    toast({ 
      title: "Pools Disconnected", 
      description: "All WebSocket connections closed" 
    });
  };

  const switchActivePool = (minerId: string, poolId: string) => {
    setMiningRigs(prev => prev.map(rig => {
      if (rig.id === minerId) {
        const poolName = rig.poolConnections.find(p => p.poolId === poolId)?.poolName || poolId;
        return {
          ...rig,
          activePool: poolId,
          pool: poolName, // Update display pool
          logs: [...rig.logs, `Switched active mining to ${poolName}`]
        };
      }
      return rig;
    }));

    const poolName = miningRigs.find(r => r.id === minerId)?.poolConnections.find(p => p.poolId === poolId)?.poolName;
    toast({ 
      title: "Pool Switched", 
      description: `Now mining to ${poolName}` 
