                      <span className="text-muted-foreground">Shares:</span>
                      <p className="font-medium text-green-500">{rig.shares.accepted}</p>
                    </div>
                  </div>

                  {/* Multi-Pool Connection Status */}
                  <div className="space-y-2">
                    <div className="flex justify-between items-center">
                      <span className="text-xs font-medium text-muted-foreground">Pool Connections:</span>
                      <div className="flex gap-1">
                        <Badge variant={rig.failoverEnabled ? 'default' : 'secondary'} className="text-xs px-1">
                          {rig.failoverEnabled ? 'Failover ON' : 'Failover OFF'}
                        </Badge>
                        <Badge variant={rig.loadBalancing ? 'default' : 'secondary'} className="text-xs px-1">
                          {rig.loadBalancing ? 'Load Balance' : 'Single Pool'}
                        </Badge>
                      </div>
                    </div>

                    <div className="space-y-1">
                      {rig.poolConnections?.map(conn => (
                        <div key={conn.poolId} className="flex items-center justify-between text-xs">
                          <div className="flex items-center gap-2">
                            <div className={`w-2 h-2 rounded-full ${
                              conn.status === 'connected' ? 'bg-green-500' :
                              conn.status === 'connecting' ? 'bg-yellow-500' :
                              conn.status === 'error' ? 'bg-red-500' : 'bg-gray-500'
                            }`} />
                            <span className={`${conn.isPrimary ? 'font-medium' : ''} ${conn.poolId === rig.activePool ? 'text-cyan-400' : 'text-muted-foreground'}`}>
                              {conn.poolName.replace(' POOL', '')}
                              {conn.isPrimary && ' (Primary)'}
                              {conn.poolId === rig.activePool && ' (Active)'}
                            </span>
                          </div>
                          <span className="text-muted-foreground">
                            {conn.shares.accepted}/{conn.shares.rejected}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Enhanced Info for AI/Cluster Mining */}
                  {(rig.type === 'tera-ai' || rig.type === 'lamam') && rig.aiModel && (
                    <div className="p-2 bg-cyan-500/10 rounded text-xs space-y-1">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">AI Model:</span>
                        <span className="font-medium text-cyan-400">{rig.aiModel}</span>
                      </div>
                      {rig.efficiency && (
                        <div className="flex justify-between">
                          <span className="text-muted-foreground">Efficiency:</span>
                          <span className="font-medium text-green-400">{rig.efficiency}%</span>
                        </div>
                      )}
                    </div>
                  )}

                  {rig.type === 'cluster' && rig.clusterNodes && (
                    <div className="p-2 bg-green-500/10 rounded text-xs space-y-1">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Cluster Nodes:</span>
                        <span className="font-medium text-green-400">{rig.clusterNodes}</span>
                      </div>
                      {rig.efficiency && (
                        <div className="flex justify-between">
                          <span className="text-muted-foreground">Efficiency:</span>
                          <span className="font-medium text-green-400">{rig.efficiency}%</span>
                        </div>
                      )}
                    </div>
                  )}

                  {(rig.algorithm && !rig.aiModel) && (
                    <div className="p-2 bg-purple-500/10 rounded text-xs">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Algorithm:</span>
                        <span className="font-medium text-purple-400">{rig.algorithm}</span>
                      </div>
                    </div>
                  )}

                  <div className="space-y-1">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Balance:</span>
                      <span className="font-medium">â‚¿{rig.balance.toFixed(8)}</span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Pool:</span>
                      <span className="font-medium text-xs">{rig.pool}</span>
                    </div>
                  </div>

                  <div className="flex gap-1">
                    <Button 
                      size="sm" 
                      className="flex-1"
                      disabled={rig.status === 'starting' || rig.status === 'stopping'}
                      onClick={() => {
                        if (rig.status === 'online') {
                          // Stop mining and disconnect from pools
                          setMiningRigs(prev => prev.map(r => 
                            r.id === rig.id ? { ...r, status: 'offline' as const, uptime: 0 } : r
                          ));
                          disconnectFromAllPools(rig.id);
                        } else {
                          // Start mining and connect to all pools
                          setMiningRigs(prev => prev.map(r => 
                            r.id === rig.id ? { ...r, status: 'starting' as const } : r
                          ));
                          connectToAllPools(rig.id);
                          setTimeout(() => {
                            setMiningRigs(prev => prev.map(r => 
                              r.id === rig.id && r.status === 'starting' ? { ...r, status: 'online' as const, uptime: Date.now() } : r
                            ));
                          }, 2000);
                        }
                      }}
                    >
                      {rig.status === 'online' ? (
                        <><Square className="h-3 w-3 mr-1" />Stop</>
                      ) : (
                        <><Play className="h-3 w-3 mr-1" />Start</>
                      )}
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => startRename(rig)}
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                    <Button 
                      size="sm" 
                      variant="destructive"
                      onClick={() => deleteMiner(rig.id)}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                    <Button 
                      size="sm" 
                      variant="secondary"
                      onClick={() => {
                        // Toggle failover
                        setMiningRigs(prev => prev.map(r => 
                          r.id === rig.id ? { ...r, failoverEnabled: !r.failoverEnabled } : r
                        ));
                        toast({ title: "Failover Toggled", description: `Failover ${!rig.failoverEnabled ? 'enabled' : 'disabled'}` });
                      }}
                    >
                      <Shield className="h-3 w-3" />
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => {
                        // Toggle load balancing
                        setMiningRigs(prev => prev.map(r => 
                          r.id === rig.id ? { ...r, loadBalancing: !r.loadBalancing } : r
                        ));
                        toast({ title: "Load Balancing Toggled", description: `Load balancing ${!rig.loadBalancing ? 'enabled' : 'disabled'}` });
                      }}
                    >
                      <Activity className="h-3 w-3" />
                    </Button>
                  </div>

                  {/* Mining Logs */}
                  <div className="mt-3">
                    <div className="text-xs text-muted-foreground mb-1 flex justify-between">
                      <span>Recent Logs:</span>
                      <span>WebSocket Connections: {rig.poolConnections?.filter(p => p.status === 'connected').length || 0}/{rig.poolConnections?.length || 0}</span>
                    </div>
                    <div className="bg-black/20 rounded p-2 text-xs font-mono max-h-16 overflow-y-auto">
                      {rig.logs.slice(-3).map((log, i) => (
                        <div key={i} className="text-green-400">
                          {new Date().toLocaleTimeString()}: {log}
                        </div>
                      ))}
                    </div>

                    {/* Pool Switch Controls */}
                    {rig.poolConnections && rig.poolConnections.length > 1 && (
                      <div className="mt-2 flex gap-1">
                        {rig.poolConnections.map(conn => (
                          <Button
                            key={conn.poolId}
                            size="sm"
                            variant={conn.poolId === rig.activePool ? 'default' : 'outline'}
                            className="h-6 px-2 text-xs"
                            onClick={() => switchActivePool(rig.id, conn.poolId)}
                            disabled={conn.status !== 'connected'}
                          >
                            {conn.poolName.split(' ')[0]}
                            {conn.status === 'connected' && (
                              <div className="w-1 h-1 bg-green-500 rounded-full ml-1" />
                            )}
                          </Button>
                        ))}
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Pool Status */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Server className="h-5 w-5" />
                Mining Pool Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {miningPools.map(pool => (
                  <div key={pool.id} className="border rounded-lg p-4 space-y-2">
                    <div className="flex items-center justify-between">
                      <h4 className="font-medium">{pool.name}</h4>
                      <Badge variant={pool.status === 'connected' ? 'default' : 'destructive'}>
                        {pool.status}
                      </Badge>
                    </div>
                    <div className="text-sm space-y-1">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">URL:</span>
                        <span className="font-mono text-xs">{pool.url}:{pool.port}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Workers:</span>
                        <span>{pool.workers}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Hashrate:</span>
                        <span>{pool.hashRate} TH/s</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Fees:</span>
                        <span>{pool.fees}%</span>
                      </div>
                      {pool.isCustom && (
                        <Badge variant="outline" className="text-xs">
                          Custom TCP Pool
                        </Badge>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Menu & Orders Tab */}
        <TabsContent value="menu" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Menu */}
            <div className="lg:col-span-2 space-y-4">
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="flex items-center gap-2">
                      <ChefHat className="h-5 w-5" />
                      Cafe Menu
                    </CardTitle>
                    <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                      <SelectTrigger className="w-32">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">All Items</SelectItem>
                        <SelectItem value="mining-boost">âš¡ Mining Boosts</SelectItem>
                        <SelectItem value="hardware">ðŸ”§ Hardware</SelectItem>
                        <SelectItem value="software">ðŸ’» Software</SelectItem>
                        <SelectItem value="coffee">â˜• Coffee</SelectItem>
                        <SelectItem value="food">ðŸ” Food</SelectItem>
                        <SelectItem value="pastry">ðŸ¥ Pastries</SelectItem>
                        <SelectItem value="special">âœ¨ Specials</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {filteredMenu.map((item: CafeItem) => (
                      <div key={item.id} className="border rounded-lg p-4">
