import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { Database, Settings, Wifi, WifiOff, Zap, DollarSign, Shield, Users, Activity } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

export default function CustomPools() {
  const [connecting, setConnecting] = useState(false);
  const [withdrawalForm, setWithdrawalForm] = useState({
    poolName: 'KLOUDBUGSCAFE',
    walletAddress: '',
    threshold: 0.01,
  });
  const [connectionForm, setConnectionForm] = useState({
    rigIds: ['rig-1', 'rig-2'],
    poolName: 'KLOUDBUGSCAFE'
  });
  const [pythonConfig, setPythonConfig] = useState({
    minerId: 'python-miner-1',
    algorithm: 'SHA256',
    poolName: 'KLOUDBUGSCAFE'
  });

  const queryClient = useQueryClient();

  // Fetch live pool status
  const { data: poolData, isLoading: poolsLoading } = useQuery({
    queryKey: ['/api/pools/status'],
    refetchInterval: 5000,
  });

  // Start mining pools
  const startPoolsMutation = useMutation({
    mutationFn: () => apiRequest('/api/pools/start', { method: 'POST' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/pools/status'] });
    },
  });

  // Stop mining pools
  const stopPoolsMutation = useMutation({
    mutationFn: () => apiRequest('/api/pools/stop', { method: 'POST' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/pools/status'] });
    },
  });

  // Connect rigs to pool
  const connectRigsMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/mining/connect-to-pool', { 
      method: 'POST', 
      body: JSON.stringify(data) 
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/pools/status'] });
    },
  });

  // Connect Python miner to pool
  const connectPythonMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/webmining/connect-python-to-pool', { 
      method: 'POST', 
      body: JSON.stringify(data) 
    }),
  });

  // Setup auto-withdrawal
  const autoWithdrawMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/pools/auto-withdraw', { 
      method: 'POST', 
      body: JSON.stringify(data) 
    }),
  });

  const handleStartPools = () => {
    startPoolsMutation.mutate();
  };

  const handleStopPools = () => {
    stopPoolsMutation.mutate();
  };

  const handleConnectRigs = () => {
    setConnecting(true);
    connectRigsMutation.mutate(connectionForm, {
      onSettled: () => setConnecting(false)
    });
  };

  const handleConnectPython = () => {
    connectPythonMutation.mutate(pythonConfig);
  };

  const handleAutoWithdraw = () => {
    autoWithdrawMutation.mutate(withdrawalForm);
  };

  const pools = poolData?.pools || [];
  const totalHashRate = pools.reduce((sum: number, pool: any) => sum + pool.hashRate, 0);
  const totalWorkers = pools.reduce((sum: number, pool: any) => sum + pool.workers.length, 0);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="relative p-8 rounded-lg bg-gradient-to-r from-slate-900/50 via-slate-800/30 to-slate-900/50 backdrop-blur-sm border border-cyan-500/30">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/5 via-purple-500/5 to-pink-500/5 rounded-lg"></div>
        <div className="relative z-10">
          <div className="flex items-center gap-4 mb-4">
            <div className="p-3 bg-gradient-to-r from-cyan-500/20 to-purple-500/20 rounded-lg">
              <Database className="h-8 w-8 text-cyan-400" />
            </div>
            <div>
              <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent">
                KLOUD BUGS PRO - Independent Mining Pools
              </h1>
              <p className="text-lg text-cyan-300/80 font-light">
                Self-contained TCP Stratum pools with auto-withdrawal to wallet addresses
              </p>
            </div>
          </div>

          {/* Pool Status Summary */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="bg-slate-800/30 rounded-lg p-4">
              <div className="text-sm text-muted-foreground">Pool Status</div>
              <div className="text-2xl font-bold text-cyan-400">
                {pools.length > 0 ? 'LIVE' : 'OFFLINE'}
              </div>
            </div>
            <div className="bg-slate-800/30 rounded-lg p-4">
              <div className="text-sm text-muted-foreground">Active Pools</div>
              <div className="text-2xl font-bold text-purple-400">{pools.length}</div>
            </div>
            <div className="bg-slate-800/30 rounded-lg p-4">
              <div className="text-sm text-muted-foreground">Total Workers</div>
              <div className="text-2xl font-bold text-pink-400">{totalWorkers}</div>
            </div>
            <div className="bg-slate-800/30 rounded-lg p-4">
              <div className="text-sm text-muted-foreground">Total Hash Rate</div>
              <div className="text-2xl font-bold text-green-400">
                {(totalHashRate / 1000000).toFixed(2)} MH/s
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Pool Control */}
      <Card className="cosmic-card">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Settings className="h-5 w-5" />
            Pool Management
          </CardTitle>
          <CardDescription>
            Start/stop independent mining pools and configure connections
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex gap-4">
            <Button 
              onClick={handleStartPools}
              disabled={startPoolsMutation.isPending || pools.length > 0}
              size="lg"
              className="bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700"
            >
              {startPoolsMutation.isPending ? 'Starting...' : 'Start Mining Pools'}
            </Button>
            <Button 
              onClick={handleStopPools}
              disabled={stopPoolsMutation.isPending || pools.length === 0}
              variant="destructive"
              size="lg"
            >
              {stopPoolsMutation.isPending ? 'Stopping...' : 'Stop Mining Pools'}
            </Button>
          </div>

          {pools.length > 0 && (
            <div className="mt-6 p-4 bg-green-900/20 border border-green-600/30 rounded-lg">
              <h4 className="font-semibold text-green-400 mb-2">ðŸŸ¢ Pools Active & Ready</h4>
              <div className="space-y-2 text-sm">
                <div>â€¢ KLOUDBUGSCAFE POOL: <code className="bg-slate-800 px-2 py-1 rounded">stratum+tcp://127.0.0.1:4001</code></div>
                <div>â€¢ TERA SOCIAL JUSTICE POOL: <code className="bg-slate-800 px-2 py-1 rounded">stratum+tcp://127.0.0.1:4002</code></div>
                <div className="text-cyan-300 mt-2">Connect your miners using these addresses. No external dependencies required.</div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Tabs defaultValue="pools" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="pools">Live Pools</TabsTrigger>
          <TabsTrigger value="connect">Connect Miners</TabsTrigger>
          <TabsTrigger value="withdraw">Auto-Withdraw</TabsTrigger>
          <TabsTrigger value="python">Python Integration</TabsTrigger>
        </TabsList>

        {/* Live Pools Display */}
        <TabsContent value="pools" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {pools.map((pool: any) => (
              <Card key={pool.id} className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>{pool.name}</span>
                    <div className="flex items-center gap-2">
                      <Badge className="bg-green-600">LIVE</Badge>
                      <Badge variant="outline">{pool.workers.length} Workers</Badge>
                    </div>
                  </CardTitle>
                  <CardDescription>
                    Port {pool.port} â€¢ {pool.protocol} â€¢ Uptime: {Math.floor(pool.uptime / 60000)}min
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <div className="text-sm text-muted-foreground">Hash Rate</div>
                      <div className="text-2xl font-bold text-cyan-400">
                        {(pool.hashRate / 1000000).toFixed(2)} MH/s
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">Shares</div>
                      <div className="text-2xl font-bold text-purple-400">{pool.shares}</div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">Blocks Found</div>
                      <div className="text-2xl font-bold text-green-400">{pool.blocks}</div>
                    </div>
                    <div>
                      <div className="text-sm text-muted-foreground">Difficulty</div>
                      <div className="text-2xl font-bold text-pink-400">
                        {(pool.difficulty / 1000000000).toFixed(1)}G
                      </div>
                    </div>
                  </div>

                  <Separator />

                  <div>
                    <h4 className="font-medium mb-2">Connected Workers</h4>
                    {pool.workers.length > 0 ? (
                      <div className="space-y-2 max-h-32 overflow-y-auto">
                        {pool.workers.map((worker: any) => (
                          <div key={worker.id} className="flex items-center justify-between text-sm bg-slate-800/30 rounded p-2">
                            <span>{worker.username}</span>
                            <div className="flex items-center gap-2">
                              <span>{(worker.hashRate / 1000).toFixed(1)} KH/s</span>
                              <Badge variant={worker.connected ? 'default' : 'secondary'} className="text-xs">
                                {worker.connected ? 'Online' : 'Offline'}
                              </Badge>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-4 text-muted-foreground text-sm">
                        No workers connected yet
                      </div>
                    )}
                  </div>

                  <div className="p-3 bg-slate-800/30 rounded-lg">
                    <div className="text-xs text-muted-foreground mb-1">Stratum Connection</div>
                    <code className="text-xs bg-slate-900 px-2 py-1 rounded">
                      stratum+tcp://127.0.0.1:{pool.port}
                    </code>
                  </div>
                </CardContent>
              </Card>
            ))}

            {pools.length === 0 && (
              <Card className="cosmic-card lg:col-span-2">
                <CardContent className="text-center py-12">
                  <Database className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-medium mb-2">No Active Pools</h3>
                  <p className="text-muted-foreground mb-4">
                    Start the mining pools to begin accepting connections from your rigs and miners
                  </p>
                  <Button onClick={handleStartPools} disabled={startPoolsMutation.isPending}>
                    Start Mining Pools
                  </Button>
                </CardContent>
              </Card>
            )}
          </div>
        </TabsContent>

        {/* Connect Mining Rigs */}
        <TabsContent value="connect" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Zap className="h-5 w-5" />
                Connect Mining Rigs to Pools
              </CardTitle>
              <CardDescription>
                Connect your physical mining rigs directly to the internal pools
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Select Pool</label>
                  <Select value={connectionForm.poolName} onValueChange={(value) => 
                    setConnectionForm({...connectionForm, poolName: value})
                  }>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="KLOUDBUGSCAFE">KLOUDBUGSCAFE POOL (Port 4001)</SelectItem>
                      <SelectItem value="TERA SOCIAL JUSTICE">TERA SOCIAL JUSTICE POOL (Port 4002)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium mb-2 block">Mining Rigs</label>
                  <Input 
                    placeholder="rig-1,rig-2,rig-3" 
                    value={connectionForm.rigIds.join(',')}
                    onChange={(e) => setConnectionForm({
                      ...connectionForm, 
                      rigIds: e.target.value.split(',').map(s => s.trim()).filter(Boolean)
                    })}
                  />
                </div>
              </div>
              <Button 
                onClick={handleConnectRigs}
                disabled={connecting || pools.length === 0}
                className="w-full"
              >
                {connecting ? 'Connecting...' : `Connect ${connectionForm.rigIds.length} Rigs to ${connectionForm.poolName}`}
              </Button>

              {pools.length === 0 && (
                <div className="p-3 bg-yellow-900/20 border border-yellow-600/30 rounded-lg">
                  <p className="text-yellow-400 text-sm">Start the mining pools first before connecting rigs.</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Auto-Withdrawal Configuration */}
        <TabsContent value="withdraw" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <DollarSign className="h-5 w-5" />
                Auto-Withdrawal to Wallet
              </CardTitle>
              <CardDescription>
                Automatically withdraw mining profits to your wallet addresses
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Pool</label>
                  <Select value={withdrawalForm.poolName} onValueChange={(value) => 
                    setWithdrawalForm({...withdrawalForm, poolName: value})
                  }>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="KLOUDBUGSCAFE">KLOUDBUGSCAFE POOL</SelectItem>
                      <SelectItem value="TERA SOCIAL JUSTICE">TERA SOCIAL JUSTICE POOL</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium mb-2 block">Wallet Address</label>
                  <Input 
                    placeholder="bc1q..." 
                    value={withdrawalForm.walletAddress}
                    onChange={(e) => setWithdrawalForm({...withdrawalForm, walletAddress: e.target.value})}
                  />
                </div>
                <div>
                  <label className="text-sm font-medium mb-2 block">Threshold (BTC)</label>
                  <Input 
                    type="number" 
                    step="0.001"
                    placeholder="0.01" 
                    value={withdrawalForm.threshold}
                    onChange={(e) => setWithdrawalForm({...withdrawalForm, threshold: parseFloat(e.target.value) || 0})}
                  />
                </div>
              </div>
              <Button 
                onClick={handleAutoWithdraw}
                disabled={!withdrawalForm.walletAddress || pools.length === 0}
                className="w-full"
              >
                Setup Auto-Withdrawal
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Python Miner Integration */}
        <TabsContent value="python" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Shield className="h-5 w-5" />
                Python Miner Stratum Integration
              </CardTitle>
              <CardDescription>
                Generate Python Stratum client code for direct pool connection
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Miner ID</label>
                  <Input 
                    placeholder="python-miner-1" 
                    value={pythonConfig.minerId}
                    onChange={(e) => setPythonConfig({...pythonConfig, minerId: e.target.value})}
                  />
                </div>
                <div>
                  <label className="text-sm font-medium mb-2 block">Algorithm</label>
                  <Select value={pythonConfig.algorithm} onValueChange={(value) => 
                    setPythonConfig({...pythonConfig, algorithm: value})
                  }>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="SHA256">SHA256 (Bitcoin)</SelectItem>
                      <SelectItem value="Scrypt">Scrypt (Litecoin)</SelectItem>
                      <SelectItem value="X11">X11 (Dash)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium mb-2 block">Target Pool</label>
                  <Select value={pythonConfig.poolName} onValueChange={(value) => 
                    setPythonConfig({...pythonConfig, poolName: value})
                  }>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="KLOUDBUGSCAFE">KLOUDBUGSCAFE POOL</SelectItem>
                      <SelectItem value="TERA SOCIAL JUSTICE">TERA SOCIAL JUSTICE POOL</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
              <Button 
                onClick={handleConnectPython}
                disabled={!pythonConfig.minerId || pools.length === 0}
                className="w-full"
              >
                Generate Python Stratum Client Code
              </Button>

              {connectPythonMutation.data && (
                <div className="mt-4">
                  <h4 className="font-medium mb-2">Generated Python Stratum Client:</h4>
                  <Textarea 
                    value={connectPythonMutation.data.connection.pythonCode}
                    readOnly
                    rows={20}
                    className="font-mono text-sm"
                  />
                  <div className="mt-2 p-3 bg-green-900/20 border border-green-600/30 rounded-lg">
                    <p className="text-green-400 text-sm">
                      Python client generated successfully! Save this code and run it to connect your Python miner directly to the {pythonConfig.poolName} pool.
                    </p>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

