import { 
  Layers, 
  Cpu, 
  Cloud, 
  Shield,
  Zap,
  Globe,
  Users,
  Settings,
  Monitor,
  Database,
  Network,
  Server,
  Activity,
  BarChart3,
  TrendingUp,
  AlertTriangle,
  CheckCircle,
  Clock,
  Folder,
  FileCode,
  Package,
  Pickaxe,
  HardHat,
  Radio,
  Wifi,
  WifiOff,
  Heart,
  Award,
  Play
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface PlatformService {
  id: string;
  name: string;
  status: 'running' | 'stopped' | 'error' | 'deploying';
  type: 'microservice' | 'database' | 'api' | 'frontend' | 'ai' | 'blockchain';
  version: string;
  uptime: number;
  cpu: number;
  memory: number;
  requests: number;
  errors: number;
  endpoint?: string;
  description: string;
}

interface AppIntegration {
  id: string;
  name: string;
  type: 'external' | 'local' | 'container' | 'serverless';
  status: 'active' | 'inactive' | 'pending' | 'error';
  path?: string;
  port?: number;
  endpoints: string[];
  dependencies: string[];
  config: Record<string, any>;
}

interface DeploymentConfig {
  id: string;
  name: string;
  environment: 'development' | 'staging' | 'production';
  replicas: number;
  resources: {
    cpu: string;
    memory: string;
    storage: string;
  };
  scaling: {
    min: number;
    max: number;
    targetCpu: number;
  };
}

export default function Platform() {
  const [selectedService, setSelectedService] = useState<string>('');
  const [newAppPath, setNewAppPath] = useState('');
  const [newAppName, setNewAppName] = useState('');
  const [deploymentEnv, setDeploymentEnv] = useState<string>('development');
  const [activeTab, setActiveTab] = useState('overview');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch platform services
  const { data: services, isLoading } = useQuery({
    queryKey: ['/api/platform/services'],
    refetchInterval: 10000,
  });

  // Fetch app integrations
  const { data: integrations } = useQuery({
    queryKey: ['/api/platform/integrations'],
    refetchInterval: 15000,
  });

  // Fetch deployment configs
  const { data: deployments } = useQuery({
    queryKey: ['/api/platform/deployments'],
  });

  // Fetch mining data
  const { data: miningStats } = useQuery({
    queryKey: ['/api/mining/stats'],
    refetchInterval: 15000,
  });

  const { data: miningConnections } = useQuery({
    queryKey: ['/api/mining/connections'],
    refetchInterval: 10000,
  });

  // Deploy service mutation
  const deployMutation = useMutation({
    mutationFn: (data: { serviceId: string; config: any }) => 
      apiRequest('/api/platform/deploy', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/platform/services'] });
      toast({ title: "Service deployed", description: "Service is now running successfully!" });
    },
  });

  // Integrate app mutation
  const integrateMutation = useMutation({
