interface AlgorithmMetrics {
  id: string;
  name: string;
  type: 'trading' | 'mining' | 'prediction' | 'optimization';
  accuracy: number;
  performance: number;
  efficiency: number;
  lastOptimized: string;
  status: 'active' | 'learning' | 'optimizing' | 'standby';
}

interface TradingSignal {
  id: string;
  pair: string;
  signal: 'BUY' | 'SELL' | 'HOLD';
  confidence: number;
  timeframe: string;
  price: number;
  targetPrice: number;
  stopLoss: number;
  timestamp: string;
}

interface AlgoPerformance {
  totalTrades: number;
  winRate: number;
  profitLoss: number;
  sharpeRatio: number;
  maxDrawdown: number;
  averageReturn: number;
}

export default function TeraAlgo() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
  const [customParameters, setCustomParameters] = useState('');
  const [backtestPeriod, setBacktestPeriod] = useState('30d');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch algorithm metrics
  const { data: algorithms, isLoading } = useQuery({
    queryKey: ['/api/tera/algo/metrics'],
    refetchInterval: 5000,
  });

  // Fetch trading signals
