    queryKey: ['/api/tera/algo/performance'],
    refetchInterval: 30000,
  });

  // Optimize algorithm mutation
  const optimizeMutation = useMutation({
    mutationFn: (data: { algorithmId: string; parameters: string }) => 
      apiRequest('/api/tera/algo/optimize', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/algo'] });
      toast({ title: "Optimization started", description: "TERA-Algo is optimizing the algorithm" });
    },
  });

  // Run backtest mutation
  const backtestMutation = useMutation({
    mutationFn: (data: { algorithmId: string; period: string }) => 
      apiRequest('/api/tera/algo/backtest', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/algo/performance'] });
