    {
      id: 'algo-mining-optimizer',
      name: 'Mining Efficiency Optimizer',
      type: 'mining',
      accuracy: 96.4,
      performance: 98.1,
      efficiency: 97.3,
      lastOptimized: '2024-02-09T09:15:00Z',
      status: 'active'
    },
    {
      id: 'algo-market-predictor',
