    {
      id: 'algo-portfolio-optimizer',
      name: 'Portfolio Risk Optimizer',
      type: 'optimization',
      accuracy: 92.3,
      performance: 90.7,
      efficiency: 94.5,
      lastOptimized: '2024-02-07T11:20:00Z',
      status: 'active'
    }
  ];

  const defaultSignals: TradingSignal[] = [
    {
      id: 'signal-1',
      pair: 'BTC/USD',
      signal: 'BUY',
      confidence: 87.5,
      timeframe: '4h',
      price: 43250,
      targetPrice: 45000,
      stopLoss: 41500,
      timestamp: new Date().toISOString()
    },
    {
      id: 'signal-2',
      pair: 'ETH/USD',
      signal: 'HOLD',
      confidence: 72.3,
      timeframe: '1h',
      price: 2650,
      targetPrice: 2750,
      stopLoss: 2550,
      timestamp: new Date(Date.now() - 300000).toISOString()
    }
