  const handleOptimize = () => {
    if (!selectedAlgorithm) {
      toast({ title: "No algorithm selected", description: "Please select an algorithm to optimize", variant: "destructive" });
      return;
    }
    optimizeMutation.mutate({ algorithmId: selectedAlgorithm, parameters: customParameters });
  };

  const handleBacktest = () => {
    if (!selectedAlgorithm) {
      toast({ title: "No algorithm selected", description: "Please select an algorithm for backtesting", variant: "destructive" });
      return;
    }
    backtestMutation.mutate({ algorithmId: selectedAlgorithm, period: backtestPeriod });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'text-green-500 bg-green-500/10';
      case 'learning': return 'text-blue-500 bg-blue-500/10';
      case 'optimizing': return 'text-purple-500 bg-purple-500/10';
      case 'standby': return 'text-yellow-500 bg-yellow-500/10';
