                    placeholder='{"learning_rate": 0.001, "batch_size": 32}'
                    value={customParameters}
                    onChange={(e) => setCustomParameters(e.target.value)}
                    className="min-h-24"
                  />
                </div>

                <Button 
                  onClick={handleOptimize}
                  disabled={!selectedAlgorithm || optimizeMutation.isPending}
                  className="cosmic-action-btn"
                >
                  <Zap className="h-4 w-4 mr-2" />
                  Start Optimization
                </Button>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Backtesting Tab */}
        <TabsContent value="backtest" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Algorithm Backtesting
              </CardTitle>
              <CardDescription>
                Test algorithm performance on historical data
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Select Algorithm</label>
                  <Select value={selectedAlgorithm} onValueChange={setSelectedAlgorithm}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose algorithm to backtest" />
                    </SelectTrigger>
                    <SelectContent>
                      {displayAlgorithms.map((algo) => (
                        <SelectItem key={algo.id} value={algo.id}>
                          {algo.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium">Backtest Period</label>
                  <Select value={backtestPeriod} onValueChange={setBacktestPeriod}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="7d">7 Days</SelectItem>
                      <SelectItem value="30d">30 Days</SelectItem>
                      <SelectItem value="90d">90 Days</SelectItem>
                      <SelectItem value="1y">1 Year</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <Button 
                onClick={handleBacktest}
                disabled={!selectedAlgorithm || backtestMutation.isPending}
                className="cosmic-action-btn"
              >
                <BarChart3 className="h-4 w-4 mr-2" />
                Run Backtest
              </Button>

              <Separator />

              <div className="space-y-3">
                <h4 className="font-medium">Latest Backtest Results</h4>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-green-500">{displayPerformance.winRate}%</div>
                    <div className="text-sm text-muted-foreground">Win Rate</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold">{displayPerformance.sharpeRatio}</div>
                    <div className="text-sm text-muted-foreground">Sharpe Ratio</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-green-500">${displayPerformance.profitLoss.toLocaleString()}</div>
                    <div className="text-sm text-muted-foreground">Total P&L</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-red-500">{displayPerformance.maxDrawdown}%</div>
                    <div className="text-sm text-muted-foreground">Max Drawdown</div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

