                {[
                  { type: 'reported_post', content: 'Post reported for inappropriate content', severity: 'medium' },
                  { type: 'spam_detection', content: 'Potential spam post detected by AI', severity: 'low' },
                  { type: 'user_report', content: 'User reported for harassment', severity: 'high' }
                ].map((item, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <AlertTriangle className={`h-4 w-4 ${
                        item.severity === 'high' ? 'text-red-500' : 
                        item.severity === 'medium' ? 'text-yellow-500' : 'text-blue-500'
                      }`} />
                      <span className="text-sm">{item.content}</span>
                    </div>
                    <div className="flex gap-2">
                      <Button size="sm" variant="outline">
                        Review
                      </Button>
                      <Button size="sm" variant="outline">
                        Resolve
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

