import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { 
  DollarSign, 
  TrendingUp, 
  TrendingDown, 
  BarChart3,
  PieChart,
  Calculator,
  CreditCard,
  Wallet,
  ArrowUpDown,
  Target,
  Activity,
  Clock,
  CheckCircle
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface ExchangeRate {
  pair: string;
  price: number;
  change24h: number;
  volume24h: number;
  high24h: number;
  low24h: number;
  lastUpdate: string;
}

interface Transaction {
  id: string;
  type: 'buy' | 'sell' | 'transfer' | 'mining_payout';
  pair: string;
  amount: number;
  price: number;
  fee: number;
  status: 'pending' | 'completed' | 'failed';
  timestamp: string;
}

interface ProfitMetrics {
  totalProfit: number;
  dailyProfit: number;
  monthlyProfit: number;
  profitMargin: number;
  bestPerformer: string;
  worstPerformer: string;
  totalVolume: number;
  averageROI: number;
}

interface MarketAnalysis {
  sentiment: 'bullish' | 'bearish' | 'neutral';
  confidence: number;
  priceTarget: number;
  resistance: number;
  support: number;
  recommendation: 'strong_buy' | 'buy' | 'hold' | 'sell' | 'strong_sell';
}

export default function TeraExchange() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedPair, setSelectedPair] = useState('BTC/USD');
  const [tradeAmount, setTradeAmount] = useState('');
  const [tradeType, setTradeType] = useState('buy');
  const [analysisTimeframe, setAnalysisTimeframe] = useState('24h');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch exchange rates
  const { data: rates, isLoading } = useQuery({
    queryKey: ['/api/tera/exchange/rates'],
    refetchInterval: 5000,
  });

  // Fetch transactions
  const { data: transactions } = useQuery({
    queryKey: ['/api/tera/exchange/transactions'],
    refetchInterval: 10000,
  });

  // Fetch profit metrics
  const { data: profitMetrics } = useQuery({
    queryKey: ['/api/tera/exchange/profit'],
    refetchInterval: 30000,
  });

  // Fetch market analysis
  const { data: marketAnalysis } = useQuery({
    queryKey: ['/api/tera/exchange/analysis', selectedPair, analysisTimeframe],
    refetchInterval: 60000,
  });

  // Execute trade mutation
  const tradeMutation = useMutation({
    mutationFn: (data: { pair: string; type: string; amount: number }) => 
      apiRequest('/api/tera/exchange/trade', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/exchange'] });
      toast({ title: "Trade executed", description: "TeraExchange has processed your order" });
      setTradeAmount('');
    },
  });

  // Mock data
  const defaultRates: ExchangeRate[] = [
    {
      pair: 'BTC/USD',
      price: 43250.50,
      change24h: 2.34,
      volume24h: 28456789,
      high24h: 43890.00,
      low24h: 42100.00,
      lastUpdate: new Date().toISOString()
    },
    {
      pair: 'ETH/USD',
      price: 2654.75,
      change24h: -1.25,
      volume24h: 15234567,
      high24h: 2720.00,
      low24h: 2580.00,
      lastUpdate: new Date().toISOString()
    },
    {
      pair: 'TERA/USD',
      price: 0.0847,
      change24h: 5.67,
      volume24h: 892345,
      high24h: 0.0892,
      low24h: 0.0801,
      lastUpdate: new Date().toISOString()
    }
  ];

  const defaultTransactions: Transaction[] = [
    {
      id: 'tx-001',
      type: 'mining_payout',
      pair: 'BTC/USD',
      amount: 0.00234,
      price: 43200.00,
      fee: 0.001,
      status: 'completed',
      timestamp: '2024-02-10T15:30:00Z'
    },
    {
      id: 'tx-002',
      type: 'buy',
      pair: 'TERA/USD',
      amount: 1000,
      price: 0.0845,
      fee: 0.25,
      status: 'completed',
      timestamp: '2024-02-10T14:15:00Z'
    },
    {
      id: 'tx-003',
      type: 'sell',
      pair: 'ETH/USD',
      amount: 0.5,
      price: 2650.00,
      fee: 3.98,
      status: 'pending',
      timestamp: '2024-02-10T13:45:00Z'
    }
  ];

  const defaultProfitMetrics: ProfitMetrics = {
    totalProfit: 12456.78,
    dailyProfit: 234.56,
    monthlyProfit: 7890.12,
    profitMargin: 15.8,
    bestPerformer: 'TERA/USD',
    worstPerformer: 'ETH/USD',
    totalVolume: 156789.45,
    averageROI: 18.7
  };

  const defaultMarketAnalysis: MarketAnalysis = {
    sentiment: 'bullish',
    confidence: 78.5,
    priceTarget: 45000,
    resistance: 44200,
    support: 42800,
    recommendation: 'buy'
  };

  const displayRates = (rates as ExchangeRate[]) || defaultRates;
  const displayTransactions = (transactions as Transaction[]) || defaultTransactions;
  const displayProfitMetrics = (profitMetrics as ProfitMetrics) || defaultProfitMetrics;
  const displayMarketAnalysis = (marketAnalysis as MarketAnalysis) || defaultMarketAnalysis;

  const selectedRate = displayRates.find(rate => rate.pair === selectedPair) || displayRates[0];

  const handleExecuteTrade = () => {
    if (!tradeAmount || parseFloat(tradeAmount) <= 0) {
      toast({ title: "Invalid amount", description: "Please enter a valid trade amount", variant: "destructive" });
      return;
    }

    tradeMutation.mutate({
      pair: selectedPair,
      type: tradeType,
      amount: parseFloat(tradeAmount)
    });
  };

  const getChangeColor = (change: number) => {
    return change >= 0 ? 'text-green-500' : 'text-red-500';
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-500 bg-green-500/10';
      case 'pending': return 'text-yellow-500 bg-yellow-500/10';
      case 'failed': return 'text-red-500 bg-red-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getSentimentColor = (sentiment: string) => {
    switch (sentiment) {
      case 'bullish': return 'text-green-500 bg-green-500/10';
      case 'bearish': return 'text-red-500 bg-red-500/10';
      case 'neutral': return 'text-yellow-500 bg-yellow-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getRecommendationColor = (recommendation: string) => {
    switch (recommendation) {
      case 'strong_buy': return 'text-green-500 bg-green-500/10';
      case 'buy': return 'text-green-500 bg-green-500/10';
      case 'hold': return 'text-yellow-500 bg-yellow-500/10';
      case 'sell': return 'text-red-500 bg-red-500/10';
      case 'strong_sell': return 'text-red-500 bg-red-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <DollarSign className="h-12 w-12 mx-auto mb-4 text-green-500" />
          <p>Loading TeraExchange AI...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-3">
            <img 
              src="/tera-logo-new.png" 
              alt="TERA Civil Rights Logo" 
              className="h-12 w-12 object-contain tera-logo"
            />
            TeraExchange AI
          </h1>
          <p className="text-muted-foreground">
            Finance Specialist â€¢ Market Analysis â€¢ Exchange Rate Tracking â€¢ Payment Processing
          </p>
        </div>
        <div className="flex gap-2">
          <Badge variant="default" className="text-lg px-3 py-1">
            <DollarSign className="h-4 w-4 mr-1" />
            ${displayProfitMetrics.totalProfit.toLocaleString()}
          </Badge>
          <Badge variant="secondary" className="text-lg px-3 py-1">
            <TrendingUp className="h-4 w-4 mr-1" />
            {displayProfitMetrics.profitMargin}% Margin
          </Badge>
        </div>
      </div>

      {/* Financial Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Profit</CardTitle>
            <DollarSign className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">${displayProfitMetrics.totalProfit.toLocaleString()}</div>
            <p className="text-xs text-muted-foreground">
              Daily: ${displayProfitMetrics.dailyProfit}
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Trading Volume</CardTitle>
            <BarChart3 className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">${displayProfitMetrics.totalVolume.toLocaleString()}</div>
            <p className="text-xs text-muted-foreground">
              24h volume
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Average ROI</CardTitle>
            <Target className="h-4 w-4 text-purple-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayProfitMetrics.averageROI}%</div>
            <p className="text-xs text-muted-foreground">
              Return on investment
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Best Performer</CardTitle>
            <TrendingUp className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayProfitMetrics.bestPerformer}</div>
            <p className="text-xs text-muted-foreground">
              Top trading pair
            </p>
          </CardContent>
        </Card>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="rates">Exchange Rates</TabsTrigger>
          <TabsTrigger value="trading">Trading</TabsTrigger>
          <TabsTrigger value="analysis">Market Analysis</TabsTrigger>
          <TabsTrigger value="transactions">Transactions</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <PieChart className="h-5 w-5" />
                  Profit Distribution
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Mining Revenue</span>
                      <span className="text-sm font-medium">65%</span>
                    </div>
                    <Progress value={65} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Trading Profits</span>
                      <span className="text-sm font-medium">25%</span>
                    </div>
                    <Progress value={25} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">TERA Token Gains</span>
                      <span className="text-sm font-medium">10%</span>
                    </div>
                    <Progress value={10} className="h-2" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Activity className="h-5 w-5" />
                  Recent Financial Activity
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {[
                    'Received mining payout: 0.00234 BTC',
                    'Executed buy order: 1000 TERA tokens',
                    'Market analysis completed for BTC/USD',
                    'Profit calculation updated',
                    'Exchange rates synchronized'
                  ].map((activity, index) => (
                    <div key={index} className="flex items-center gap-3">
                      <CheckCircle className="h-4 w-4 text-green-500" />
                      <span className="text-sm">{activity}</span>
                      <span className="text-xs text-muted-foreground ml-auto">
                        {Math.floor(Math.random() * 60)} mins ago
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Monthly Profit</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-green-500">
                  ${displayProfitMetrics.monthlyProfit.toLocaleString()}
                </div>
                <div className="text-sm text-muted-foreground">This month</div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Profit Margin</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-blue-500">{displayProfitMetrics.profitMargin}%</div>
                <div className="text-sm text-muted-foreground">Overall margin</div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Daily Revenue</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-purple-500">
                  ${displayProfitMetrics.dailyProfit}
                </div>
                <div className="text-sm text-muted-foreground">Today</div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Exchange Rates Tab */}
        <TabsContent value="rates" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {displayRates.map((rate) => (
              <Card key={rate.pair} className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>{rate.pair}</span>
                    <Badge variant="default" className={getChangeColor(rate.change24h)}>
                      {rate.change24h >= 0 ? <TrendingUp className="h-3 w-3 mr-1" /> : <TrendingDown className="h-3 w-3 mr-1" />}
                      {rate.change24h.toFixed(2)}%
                    </Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold">${rate.price.toLocaleString()}</div>
                    <div className="text-sm text-muted-foreground">Current Price</div>
                  </div>

                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <div className="text-muted-foreground">24h High</div>
                      <div className="font-medium">${rate.high24h.toLocaleString()}</div>
                    </div>
                    <div>
                      <div className="text-muted-foreground">24h Low</div>
                      <div className="font-medium">${rate.low24h.toLocaleString()}</div>
                    </div>
                    <div className="col-span-2">
                      <div className="text-muted-foreground">24h Volume</div>
                      <div className="font-medium">${rate.volume24h.toLocaleString()}</div>
                    </div>
                  </div>

                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={() => setSelectedPair(rate.pair)}
                    className="w-full"
                  >
                    Select for Trading
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Trading Tab */}
        <TabsContent value="trading" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <ArrowUpDown className="h-5 w-5" />
                  Execute Trade
                </CardTitle>
                <CardDescription>
                  Place buy or sell orders through TeraExchange
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="text-sm font-medium">Trading Pair</label>
                    <Select value={selectedPair} onValueChange={setSelectedPair}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {displayRates.map((rate) => (
                          <SelectItem key={rate.pair} value={rate.pair}>
                            {rate.pair}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-sm font-medium">Order Type</label>
                    <Select value={tradeType} onValueChange={setTradeType}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="buy">Buy</SelectItem>
                        <SelectItem value="sell">Sell</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="col-span-2">
                    <label className="text-sm font-medium">Amount</label>
                    <Input
                      type="number"
                      placeholder="Enter amount"
                      value={tradeAmount}
                      onChange={(e) => setTradeAmount(e.target.value)}
                    />
                  </div>
                </div>

                {selectedRate && (
                  <div className="p-4 border rounded-lg bg-muted/50">
                    <div className="text-sm">
                      <div className="flex justify-between mb-1">
                        <span>Current Price:</span>
                        <span className="font-medium">${selectedRate.price.toLocaleString()}</span>
                      </div>
                      <div className="flex justify-between mb-1">
                        <span>24h Change:</span>
                        <span className={`font-medium ${getChangeColor(selectedRate.change24h)}`}>
                          {selectedRate.change24h.toFixed(2)}%
                        </span>
                      </div>
                      {tradeAmount && (
                        <div className="flex justify-between">
                          <span>Total Cost:</span>
                          <span className="font-medium">
                            ${(parseFloat(tradeAmount) * selectedRate.price).toLocaleString()}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                )}

                <Button 
                  onClick={handleExecuteTrade}
                  disabled={!tradeAmount || parseFloat(tradeAmount) <= 0 || tradeMutation.isPending}
                  className="cosmic-action-btn w-full"
                >
                  <ArrowUpDown className="h-4 w-4 mr-2" />
                  Execute {tradeType.charAt(0).toUpperCase() + tradeType.slice(1)} Order
                </Button>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Wallet className="h-5 w-5" />
                  Portfolio Balance
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {[
                    { symbol: 'BTC', amount: 0.0547, value: 2365.78, change: 2.34 },
                    { symbol: 'ETH', amount: 1.234, value: 3275.45, change: -1.25 },
                    { symbol: 'TERA', amount: 15678, value: 1328.44, change: 5.67 },
                    { symbol: 'USD', amount: 8945.67, value: 8945.67, change: 0 }
                  ].map((asset, index) => (
                    <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                      <div>
                        <div className="font-medium">{asset.symbol}</div>
                        <div className="text-sm text-muted-foreground">
                          {asset.amount.toLocaleString()} {asset.symbol}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-medium">${asset.value.toLocaleString()}</div>
                        <div className={`text-sm ${getChangeColor(asset.change)}`}>
                          {asset.change !== 0 && (asset.change > 0 ? '+' : '')}{asset.change.toFixed(2)}%
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Market Analysis Tab */}
        <TabsContent value="analysis" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BarChart3 className="h-5 w-5" />
                  Market Analysis: {selectedPair}
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between mb-4">
                  <Select value={analysisTimeframe} onValueChange={setAnalysisTimeframe}>
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="1h">1 Hour</SelectItem>
                      <SelectItem value="24h">24 Hours</SelectItem>
                      <SelectItem value="7d">7 Days</SelectItem>
                      <SelectItem value="30d">30 Days</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Market Sentiment</span>
                    <Badge variant="default" className={getSentimentColor(displayMarketAnalysis.sentiment)}>
                      {displayMarketAnalysis.sentiment.toUpperCase()}
                    </Badge>
                  </div>

                  <div className="flex items-center justify-between">
                    <span className="text-sm">Confidence Level</span>
                    <span className="font-medium">{displayMarketAnalysis.confidence}%</span>
                  </div>

                  <div className="flex items-center justify-between">
                    <span className="text-sm">Price Target</span>
                    <span className="font-medium">${displayMarketAnalysis.priceTarget.toLocaleString()}</span>
                  </div>

                  <div className="flex items-center justify-between">
                    <span className="text-sm">Resistance Level</span>
                    <span className="font-medium">${displayMarketAnalysis.resistance.toLocaleString()}</span>
                  </div>

                  <div className="flex items-center justify-between">
                    <span className="text-sm">Support Level</span>
                    <span className="font-medium">${displayMarketAnalysis.support.toLocaleString()}</span>
                  </div>

                  <Separator />

                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium">Recommendation</span>
                    <Badge variant="default" className={getRecommendationColor(displayMarketAnalysis.recommendation)}>
                      {displayMarketAnalysis.recommendation.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Calculator className="h-5 w-5" />
                  Profit Calculator
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {[
                    { metric: 'Daily Mining Revenue', value: 234.56, unit: 'USD' },
                    { metric: 'Trading Profit (24h)', value: 45.78, unit: 'USD' },
                    { metric: 'TERA Token Gains', value: 12.34, unit: 'USD' },
                    { metric: 'Total Daily Profit', value: 292.68, unit: 'USD' },
                    { metric: 'Monthly Projection', value: 8780.40, unit: 'USD' },
                    { metric: 'Annual Projection', value: 106894.80, unit: 'USD' }
                  ].map((item, index) => (
                    <div key={index} className="flex items-center justify-between">
                      <span className="text-sm">{item.metric}</span>
                      <span className="font-medium">
                        {item.unit === 'USD' ? '$' : ''}{item.value.toLocaleString()} {item.unit !== 'USD' ? item.unit : ''}
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Transactions Tab */}
        <TabsContent value="transactions" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CreditCard className="h-5 w-5" />
                Transaction History
              </CardTitle>
              <CardDescription>
                Recent trading and payment transactions
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {displayTransactions.map((transaction) => (
                  <div key={transaction.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <div className="flex items-center gap-2">
                        <Badge variant="outline" className="capitalize">
                          {transaction.type.replace('_', ' ')}
                        </Badge>
                        <span className="font-medium">{transaction.pair}</span>
                      </div>
                      <Badge variant="default" className={getStatusColor(transaction.status)}>
                        {transaction.status}
                      </Badge>
                    </div>

                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                      <div>
                        <div className="text-muted-foreground">Amount</div>
                        <div className="font-medium">{transaction.amount.toLocaleString()}</div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Price</div>
                        <div className="font-medium">${transaction.price.toLocaleString()}</div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Fee</div>
                        <div className="font-medium">${transaction.fee}</div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Total</div>
                        <div className="font-medium">
                          ${(transaction.amount * transaction.price + transaction.fee).toLocaleString()}
                        </div>
                      </div>
                    </div>

                    <div className="mt-3 text-xs text-muted-foreground">
                      {new Date(transaction.timestamp).toLocaleString()}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

