import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { 
  Cpu, 
  Zap, 
  Thermometer, 
  Activity,
  Settings,
  TrendingUp,
  BarChart3,
  Power,
  HardDrive,
  Monitor,
  AlertTriangle,
  CheckCircle,
  Clock
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface MiningRig {
  id: string;
  name: string;
  type: 'ASIC' | 'GPU' | 'FPGA';
  status: 'online' | 'offline' | 'maintenance' | 'error';
  hashrate: number;
  power: number;
  temperature: number;
  efficiency: number;
  uptime: number;
  lastOptimized: string;
}

interface OptimizationMetrics {
  powerReduction: number;
  hashrateIncrease: number;
  temperatureReduction: number;
  efficiencyGain: number;
  estimatedSavings: number;
}

interface MiningStats {
  totalHashrate: number;
  totalPower: number;
  averageTemp: number;
  totalRigs: number;
  onlineRigs: number;
  dailyRevenue: number;
  dailyCosts: number;
  profitMargin: number;
}

export default function TeraMiner() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedRig, setSelectedRig] = useState('');
  const [optimizationMode, setOptimizationMode] = useState('balanced');
  const [powerLimit, setPowerLimit] = useState('');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch mining rigs
  const { data: rigs, isLoading } = useQuery({
    queryKey: ['/api/tera/miner/rigs'],
    refetchInterval: 5000,
  });

  // Fetch mining stats
  const { data: stats } = useQuery({
    queryKey: ['/api/tera/miner/stats'],
    refetchInterval: 10000,
  });

  // Fetch optimization metrics
  const { data: optimization } = useQuery({
    queryKey: ['/api/tera/miner/optimization'],
    refetchInterval: 30000,
  });

  // Optimize rig mutation
  const optimizeRigMutation = useMutation({
    mutationFn: (data: { rigId: string; mode: string; powerLimit?: number }) => 
      apiRequest('/api/tera/miner/optimize', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/miner'] });
      toast({ title: "Optimization started", description: "TeraMiner is optimizing the selected rig" });
    },
  });

  // Restart rig mutation
  const restartRigMutation = useMutation({
    mutationFn: (rigId: string) => apiRequest('/api/tera/miner/restart', 'POST', { rigId }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/miner/rigs'] });
      toast({ title: "Rig restarted", description: "Mining rig is coming back online" });
    },
  });

  // Mock data
  const defaultRigs: MiningRig[] = [
    {
      id: 'rig-btc-001',
      name: 'Bitcoin ASIC Miner #1',
      type: 'ASIC',
      status: 'online',
      hashrate: 110,
      power: 3250,
      temperature: 68,
      efficiency: 29.5,
      uptime: 99.7,
      lastOptimized: '2024-02-10T08:30:00Z'
    },
    {
      id: 'rig-btc-002',
      name: 'Bitcoin ASIC Miner #2',
      type: 'ASIC',
      status: 'online',
      hashrate: 108,
      power: 3100,
      temperature: 72,
      efficiency: 34.8,
      uptime: 98.9,
      lastOptimized: '2024-02-09T14:15:00Z'
    },
    {
      id: 'rig-eth-001',
      name: 'Ethereum GPU Rig #1',
      type: 'GPU',
      status: 'maintenance',
      hashrate: 420,
      power: 1800,
      temperature: 65,
      efficiency: 4.3,
      uptime: 96.2,
      lastOptimized: '2024-02-08T11:45:00Z'
    },
    {
      id: 'rig-eth-002',
      name: 'Ethereum GPU Rig #2',
      type: 'GPU',
      status: 'online',
      hashrate: 445,
      power: 1950,
      temperature: 70,
      efficiency: 4.4,
      uptime: 99.1,
      lastOptimized: '2024-02-10T16:20:00Z'
    }
  ];

  const defaultStats: MiningStats = {
    totalHashrate: 1083,
    totalPower: 10100,
    averageTemp: 68.75,
    totalRigs: 4,
    onlineRigs: 3,
    dailyRevenue: 847.50,
    dailyCosts: 242.40,
    profitMargin: 71.4
  };

  const defaultOptimization: OptimizationMetrics = {
    powerReduction: 15.2,
    hashrateIncrease: 8.7,
    temperatureReduction: 12.1,
    efficiencyGain: 23.4,
    estimatedSavings: 127.80
  };

  const displayRigs = (rigs as MiningRig[]) || defaultRigs;
  const displayStats = (stats as MiningStats) || defaultStats;
  const displayOptimization = (optimization as OptimizationMetrics) || defaultOptimization;

  const handleOptimizeRig = () => {
    if (!selectedRig) {
      toast({ title: "No rig selected", description: "Please select a rig to optimize", variant: "destructive" });
      return;
    }
    const data: any = { rigId: selectedRig, mode: optimizationMode };
    if (powerLimit) data.powerLimit = parseInt(powerLimit);
    optimizeRigMutation.mutate(data);
  };

  const handleRestartRig = (rigId: string) => {
    restartRigMutation.mutate(rigId);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'text-green-500 bg-green-500/10';
      case 'offline': return 'text-red-500 bg-red-500/10';
      case 'maintenance': return 'text-yellow-500 bg-yellow-500/10';
      case 'error': return 'text-red-500 bg-red-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'ASIC': return <Cpu className="h-4 w-4" />;
      case 'GPU': return <Monitor className="h-4 w-4" />;
      case 'FPGA': return <HardDrive className="h-4 w-4" />;
      default: return <Cpu className="h-4 w-4" />;
    }
  };

  const getTempColor = (temp: number) => {
    if (temp < 60) return 'text-green-500';
    if (temp < 75) return 'text-yellow-500';
    return 'text-red-500';
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <Cpu className="h-12 w-12 mx-auto mb-4 text-orange-500" />
          <p>Loading TeraMiner AI...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-3">
            <img 
              src="/mining-figure.png" 
              alt="TERA Mining Figure" 
              className="h-12 w-12 object-contain tera-logo"
            />
            TeraMiner AI
          </h1>
          <p className="text-muted-foreground">
            Mining Specialist â€¢ Hardware Optimization â€¢ Power Management
          </p>
        </div>
        <div className="flex gap-2">
          <Badge variant="default" className="text-lg px-3 py-1">
            <Cpu className="h-4 w-4 mr-1" />
            {displayStats.onlineRigs}/{displayStats.totalRigs} Online
          </Badge>
          <Badge variant="secondary" className="text-lg px-3 py-1">
            <Zap className="h-4 w-4 mr-1" />
            {displayStats.profitMargin.toFixed(1)}% Profit
          </Badge>
        </div>
      </div>

      {/* Mining Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Hashrate</CardTitle>
            <Activity className="h-4 w-4 text-cyan-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayStats.totalHashrate} TH/s</div>
            <p className="text-xs text-muted-foreground">
              Combined mining power
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Power Usage</CardTitle>
            <Power className="h-4 w-4 text-yellow-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayStats.totalPower}W</div>
            <p className="text-xs text-muted-foreground">
              Daily cost: ${displayStats.dailyCosts}
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Avg Temperature</CardTitle>
            <Thermometer className="h-4 w-4 text-orange-500" />
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold ${getTempColor(displayStats.averageTemp)}`}>
              {displayStats.averageTemp}Â°C
            </div>
            <p className="text-xs text-muted-foreground">
              Optimal range: 60-70Â°C
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Daily Revenue</CardTitle>
            <TrendingUp className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">${displayStats.dailyRevenue}</div>
            <p className="text-xs text-muted-foreground">
              Profit: ${(displayStats.dailyRevenue - displayStats.dailyCosts).toFixed(2)}
            </p>
          </CardContent>
        </Card>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="rigs">Mining Rigs</TabsTrigger>
          <TabsTrigger value="optimization">Optimization</TabsTrigger>
          <TabsTrigger value="performance">Performance</TabsTrigger>
          <TabsTrigger value="controls">Controls</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BarChart3 className="h-5 w-5" />
                  Optimization Gains
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Power Reduction</span>
                      <span className="text-sm font-medium">{displayOptimization.powerReduction}%</span>
                    </div>
                    <Progress value={displayOptimization.powerReduction} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Hashrate Increase</span>
                      <span className="text-sm font-medium">{displayOptimization.hashrateIncrease}%</span>
                    </div>
                    <Progress value={displayOptimization.hashrateIncrease} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Temperature Reduction</span>
                      <span className="text-sm font-medium">{displayOptimization.temperatureReduction}%</span>
                    </div>
                    <Progress value={displayOptimization.temperatureReduction} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Efficiency Gain</span>
                      <span className="text-sm font-medium">{displayOptimization.efficiencyGain}%</span>
                    </div>
                    <Progress value={displayOptimization.efficiencyGain} className="h-2" />
                  </div>
                </div>

                <Separator />

                <div className="text-center">
                  <div className="text-2xl font-bold text-green-500">
                    ${displayOptimization.estimatedSavings}
                  </div>
                  <div className="text-sm text-muted-foreground">Estimated daily savings</div>
                </div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Activity className="h-5 w-5" />
                  Recent Optimizations
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {[
                    'Optimized Bitcoin ASIC Miner #1 - 12% efficiency gain',
                    'Reduced power consumption by 200W on GPU Rig #2',
                    'Temperature optimization completed on all rigs',
                    'Hashrate difficulty adjustment applied',
                    'Power management algorithm updated'
                  ].map((optimization, index) => (
                    <div key={index} className="flex items-center gap-3">
                      <CheckCircle className="h-4 w-4 text-green-500" />
                      <span className="text-sm">{optimization}</span>
                      <span className="text-xs text-muted-foreground ml-auto">
                        {Math.floor(Math.random() * 120)} mins ago
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Cpu className="h-5 w-5" />
                Rig Status Overview
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {displayRigs.map((rig) => (
                  <div key={rig.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        {getTypeIcon(rig.type)}
                        <span className="font-medium text-sm">{rig.name}</span>
                      </div>
                      <Badge variant="default" className={`text-xs ${getStatusColor(rig.status)}`}>
                        {rig.status}
                      </Badge>
                    </div>

                    <div className="space-y-1 text-sm">
                      <div className="flex justify-between">
                        <span>Hashrate:</span>
                        <span className="font-medium">{rig.hashrate} {rig.type === 'ASIC' ? 'TH/s' : 'MH/s'}</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Power:</span>
                        <span className="font-medium">{rig.power}W</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Temp:</span>
                        <span className={`font-medium ${getTempColor(rig.temperature)}`}>{rig.temperature}Â°C</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Uptime:</span>
                        <span className="font-medium">{rig.uptime}%</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Mining Rigs Tab */}
        <TabsContent value="rigs" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {displayRigs.map((rig) => (
              <Card key={rig.id} className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      {getTypeIcon(rig.type)}
                      {rig.name}
                    </div>
                    <Badge variant="default" className={getStatusColor(rig.status)}>
                      {rig.status}
                    </Badge>
                  </CardTitle>
                  <CardDescription>{rig.type} Mining Hardware</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <div className="text-muted-foreground">Hashrate</div>
                      <div className="text-lg font-bold">{rig.hashrate} {rig.type === 'ASIC' ? 'TH/s' : 'MH/s'}</div>
                    </div>
                    <div>
                      <div className="text-muted-foreground">Power Usage</div>
                      <div className="text-lg font-bold">{rig.power}W</div>
                    </div>
                    <div>
                      <div className="text-muted-foreground">Temperature</div>
                      <div className={`text-lg font-bold ${getTempColor(rig.temperature)}`}>
                        {rig.temperature}Â°C
                      </div>
                    </div>
                    <div>
                      <div className="text-muted-foreground">Efficiency</div>
                      <div className="text-lg font-bold">{rig.efficiency} J/TH</div>
                    </div>
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Uptime</span>
                      <span className="text-sm font-medium">{rig.uptime}%</span>
                    </div>
                    <Progress value={rig.uptime} className="h-2" />
                  </div>

                  <Separator />

                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => setSelectedRig(rig.id)}
                      className="flex-1"
                    >
                      Select
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => handleRestartRig(rig.id)}
                      disabled={restartRigMutation.isPending}
                    >
                      Restart
                    </Button>
                  </div>

                  <div className="text-xs text-muted-foreground">
                    Last optimized: {new Date(rig.lastOptimized).toLocaleDateString()}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Optimization Tab */}
        <TabsContent value="optimization" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Settings className="h-5 w-5" />
                Hardware Optimization
              </CardTitle>
              <CardDescription>
                Optimize mining rig performance and efficiency
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Select Mining Rig</label>
                  <Select value={selectedRig} onValueChange={setSelectedRig}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose rig to optimize" />
                    </SelectTrigger>
                    <SelectContent>
                      {displayRigs.map((rig) => (
                        <SelectItem key={rig.id} value={rig.id}>
                          {rig.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium">Optimization Mode</label>
                  <Select value={optimizationMode} onValueChange={setOptimizationMode}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="power">Power Efficiency</SelectItem>
                      <SelectItem value="performance">Maximum Performance</SelectItem>
                      <SelectItem value="balanced">Balanced</SelectItem>
                      <SelectItem value="temperature">Temperature Control</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium">Power Limit (Watts)</label>
                  <Input
                    type="number"
                    placeholder="Optional power limit"
                    value={powerLimit}
                    onChange={(e) => setPowerLimit(e.target.value)}
                  />
                </div>
              </div>

              <Button 
                onClick={handleOptimizeRig}
                disabled={!selectedRig || optimizeRigMutation.isPending}
                className="cosmic-action-btn"
              >
                <Zap className="h-4 w-4 mr-2" />
                Start Optimization
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Performance Tab */}
        <TabsContent value="performance" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Power Efficiency</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-green-500">15.2%</div>
                <div className="text-sm text-muted-foreground">Improvement</div>
                <Progress value={85} className="h-2 mt-2" />
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Hashrate Optimization</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-blue-500">8.7%</div>
                <div className="text-sm text-muted-foreground">Increase</div>
                <Progress value={92} className="h-2 mt-2" />
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Temperature Control</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-orange-500">12.1%</div>
                <div className="text-sm text-muted-foreground">Reduction</div>
                <Progress value={78} className="h-2 mt-2" />
              </CardContent>
            </Card>
          </div>

          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Performance History
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {[
                  { date: '2024-02-10', hashrate: 1083, power: 10100, efficiency: 92.1 },
                  { date: '2024-02-09', hashrate: 1057, power: 10350, efficiency: 89.8 },
                  { date: '2024-02-08', hashrate: 1034, power: 10580, efficiency: 87.2 },
                  { date: '2024-02-07', hashrate: 1012, power: 10720, efficiency: 85.6 }
                ].map((day, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="text-sm">{day.date}</div>
                    <div className="flex gap-4 text-sm">
                      <span>Hashrate: {day.hashrate} TH/s</span>
                      <span>Power: {day.power}W</span>
                      <span>Efficiency: {day.efficiency}%</span>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Controls Tab */}
        <TabsContent value="controls" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Power className="h-5 w-5" />
                  Rig Controls
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {displayRigs.map((rig) => (
                    <div key={rig.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div>
                        <div className="font-medium">{rig.name}</div>
                        <div className="text-sm text-muted-foreground">{rig.type} - {rig.status}</div>
                      </div>
                      <div className="flex gap-2">
                        <Button size="sm" variant="outline">
                          {rig.status === 'online' ? 'Stop' : 'Start'}
                        </Button>
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => handleRestartRig(rig.id)}
                        >
                          Restart
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <AlertTriangle className="h-5 w-5" />
                  System Alerts
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {[
                  { type: 'warning', message: 'GPU Rig #1 in maintenance mode', time: '5 mins ago' },
                  { type: 'info', message: 'Optimization completed on ASIC #2', time: '15 mins ago' },
                  { type: 'success', message: 'Temperature normalized on all rigs', time: '1 hour ago' }
                ].map((alert, index) => (
                  <div key={index} className="flex items-center gap-3 p-3 border rounded-lg">
                    {alert.type === 'warning' && <AlertTriangle className="h-4 w-4 text-yellow-500" />}
                    {alert.type === 'info' && <Clock className="h-4 w-4 text-blue-500" />}
                    {alert.type === 'success' && <CheckCircle className="h-4 w-4 text-green-500" />}
                    <div className="flex-1">
                      <div className="text-sm">{alert.message}</div>
                      <div className="text-xs text-muted-foreground">{alert.time}</div>
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

