import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { 
  Shield, 
  Lock, 
  Eye, 
  AlertTriangle,
  CheckCircle,
  Scan,
  Key,
  Fingerprint,
  Activity,
  Settings,
  Database,
  Network,
  Server,
  FileText
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface SecurityThreat {
  id: string;
  type: 'intrusion' | 'malware' | 'phishing' | 'ddos' | 'unauthorized_access';
  severity: 'low' | 'medium' | 'high' | 'critical';
  status: 'detected' | 'blocked' | 'investigating' | 'resolved';
  description: string;
  timestamp: string;
  source: string;
}

interface SecurityMetrics {
  threatsBlocked: number;
  vulnerabilitiesFixed: number;
  securityScore: number;
  uptime: number;
  lastScan: string;
  activeSessions: number;
  failedAttempts: number;
  encryptionLevel: number;
}

interface AccessControl {
  id: string;
  user: string;
  role: 'owner' | 'admin' | 'user' | 'guest';
  permissions: string[];
  lastAccess: string;
  status: 'active' | 'suspended' | 'pending';
  twoFactorEnabled: boolean;
}

export default function TeraSecure() {
  const [activeTab, setActiveTab] = useState('overview');
  const [scanType, setScanType] = useState('full');
  const [threatFilter, setThreatFilter] = useState('all');
  const [selectedUser, setSelectedUser] = useState('');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch security metrics
  const { data: metrics, isLoading } = useQuery({
    queryKey: ['/api/tera/security/metrics'],
    refetchInterval: 5000,
  });

  // Fetch security threats
  const { data: threats } = useQuery({
    queryKey: ['/api/tera/security/threats'],
    refetchInterval: 10000,
  });

  // Fetch access controls
  const { data: accessControls } = useQuery({
    queryKey: ['/api/tera/security/access'],
    refetchInterval: 30000,
  });

  // Start security scan mutation
  const scanMutation = useMutation({
    mutationFn: (type: string) => apiRequest('/api/tera/security/scan', 'POST', { type }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/security'] });
      toast({ title: "Security scan started", description: "TeraSecure is performing a comprehensive scan" });
    },
  });

  // Update access control mutation
  const updateAccessMutation = useMutation({
    mutationFn: (data: { userId: string; action: string }) => 
      apiRequest('/api/tera/security/access/update', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tera/security/access'] });
      toast({ title: "Access updated", description: "User permissions have been modified" });
    },
  });

  // Mock data
  const defaultMetrics: SecurityMetrics = {
    threatsBlocked: 247,
    vulnerabilitiesFixed: 89,
    securityScore: 98.7,
    uptime: 99.9,
    lastScan: '2024-02-10T14:30:00Z',
    activeSessions: 12,
    failedAttempts: 3,
    encryptionLevel: 256
  };

  const defaultThreats: SecurityThreat[] = [
    {
      id: 'threat-001',
      type: 'intrusion',
      severity: 'high',
      status: 'blocked',
      description: 'Attempted unauthorized access to mining control system',
      timestamp: '2024-02-10T15:45:00Z',
      source: '192.168.1.100'
    },
    {
      id: 'threat-002',
      type: 'malware',
      severity: 'medium',
      status: 'resolved',
      description: 'Suspicious executable detected and quarantined',
      timestamp: '2024-02-10T12:30:00Z',
      source: 'email_attachment'
    },
    {
      id: 'threat-003',
      type: 'ddos',
      severity: 'critical',
      status: 'investigating',
      description: 'Distributed denial of service attack on API endpoints',
      timestamp: '2024-02-10T10:15:00Z',
      source: 'multiple_ips'
    }
  ];

  const defaultAccessControls: AccessControl[] = [
    {
      id: 'user-001',
      user: 'admin@kloudbugscafe.com',
      role: 'owner',
      permissions: ['all'],
      lastAccess: '2024-02-10T15:30:00Z',
      status: 'active',
      twoFactorEnabled: true
    },
    {
      id: 'user-002',
      user: 'mining.operator@kloudbugscafe.com',
      role: 'admin',
      permissions: ['mining', 'monitoring', 'optimization'],
      lastAccess: '2024-02-10T14:15:00Z',
      status: 'active',
      twoFactorEnabled: true
    },
    {
      id: 'user-003',
      user: 'viewer@kloudbugscafe.com',
      role: 'user',
      permissions: ['view', 'basic_controls'],
      lastAccess: '2024-02-09T16:45:00Z',
      status: 'active',
      twoFactorEnabled: false
    }
  ];

  const displayMetrics = (metrics as SecurityMetrics) || defaultMetrics;
  const displayThreats = (threats as SecurityThreat[]) || defaultThreats;
  const displayAccessControls = (accessControls as AccessControl[]) || defaultAccessControls;

  const filteredThreats = threatFilter === 'all' 
    ? displayThreats 
    : displayThreats.filter(threat => threat.severity === threatFilter);

  const handleStartScan = () => {
    scanMutation.mutate(scanType);
  };

  const handleUpdateAccess = (userId: string, action: string) => {
    updateAccessMutation.mutate({ userId, action });
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'low': return 'text-green-500 bg-green-500/10';
      case 'medium': return 'text-yellow-500 bg-yellow-500/10';
      case 'high': return 'text-orange-500 bg-orange-500/10';
      case 'critical': return 'text-red-500 bg-red-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'text-green-500 bg-green-500/10';
      case 'blocked': return 'text-red-500 bg-red-500/10';
      case 'resolved': return 'text-green-500 bg-green-500/10';
      case 'investigating': return 'text-yellow-500 bg-yellow-500/10';
      case 'suspended': return 'text-red-500 bg-red-500/10';
      case 'pending': return 'text-yellow-500 bg-yellow-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getThreatIcon = (type: string) => {
    switch (type) {
      case 'intrusion': return <Shield className="h-4 w-4" />;
      case 'malware': return <AlertTriangle className="h-4 w-4" />;
      case 'phishing': return <Eye className="h-4 w-4" />;
      case 'ddos': return <Network className="h-4 w-4" />;
      case 'unauthorized_access': return <Lock className="h-4 w-4" />;
      default: return <AlertTriangle className="h-4 w-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <Shield className="h-12 w-12 mx-auto mb-4 text-red-500" />
          <p>Loading TeraSecure AI...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-3">
            <img 
              src="/tera-logo-new.png" 
              alt="TERA Civil Rights Logo" 
              className="h-12 w-12 object-contain tera-logo"
            />
            TeraSecure AI
          </h1>
          <p className="text-muted-foreground">
            Security Specialist â€¢ Threat Detection â€¢ Access Control â€¢ Vulnerability Management
          </p>
        </div>
        <div className="flex gap-2">
          <Badge variant="default" className="text-lg px-3 py-1">
            <Shield className="h-4 w-4 mr-1" />
            Security Score: {displayMetrics.securityScore}%
          </Badge>
          <Badge variant="secondary" className="text-lg px-3 py-1">
            <CheckCircle className="h-4 w-4 mr-1" />
            Protected
          </Badge>
        </div>
      </div>

      {/* Security Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Threats Blocked</CardTitle>
            <Shield className="h-4 w-4 text-red-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayMetrics.threatsBlocked}</div>
            <p className="text-xs text-muted-foreground">
              Last 24 hours
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Security Score</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayMetrics.securityScore}%</div>
            <p className="text-xs text-muted-foreground">
              System integrity
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Sessions</CardTitle>
            <Activity className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayMetrics.activeSessions}</div>
            <p className="text-xs text-muted-foreground">
              Current users
            </p>
          </CardContent>
        </Card>

        <Card className="cosmic-card">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">System Uptime</CardTitle>
            <Server className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{displayMetrics.uptime}%</div>
            <p className="text-xs text-muted-foreground">
              Security systems
            </p>
          </CardContent>
        </Card>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="threats">Threats</TabsTrigger>
          <TabsTrigger value="access">Access Control</TabsTrigger>
          <TabsTrigger value="scanning">Scanning</TabsTrigger>
          <TabsTrigger value="audit">Audit Log</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Shield className="h-5 w-5" />
                  Security Status
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Overall Security</span>
                      <span className="text-sm font-medium">{displayMetrics.securityScore}%</span>
                    </div>
                    <Progress value={displayMetrics.securityScore} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">Encryption Level</span>
                      <span className="text-sm font-medium">AES-{displayMetrics.encryptionLevel}</span>
                    </div>
                    <Progress value={100} className="h-2" />
                  </div>

                  <div>
                    <div className="flex justify-between mb-1">
                      <span className="text-sm">System Uptime</span>
                      <span className="text-sm font-medium">{displayMetrics.uptime}%</span>
                    </div>
                    <Progress value={displayMetrics.uptime} className="h-2" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Activity className="h-5 w-5" />
                  Recent Security Events
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {[
                    'Blocked intrusion attempt from external IP',
                    'Updated firewall rules automatically',
                    'Completed vulnerability scan - no issues found',
                    'Enhanced encryption protocols applied',
                    'Security audit completed successfully'
                  ].map((event, index) => (
                    <div key={index} className="flex items-center gap-3">
                      <CheckCircle className="h-4 w-4 text-green-500" />
                      <span className="text-sm">{event}</span>
                      <span className="text-xs text-muted-foreground ml-auto">
                        {Math.floor(Math.random() * 60)} mins ago
                      </span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Threats Blocked</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-red-500">{displayMetrics.threatsBlocked}</div>
                <div className="text-sm text-muted-foreground">Last 24 hours</div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Vulnerabilities Fixed</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-green-500">{displayMetrics.vulnerabilitiesFixed}</div>
                <div className="text-sm text-muted-foreground">This month</div>
              </CardContent>
            </Card>

            <Card className="cosmic-card">
              <CardHeader>
                <CardTitle className="text-center">Failed Attempts</CardTitle>
              </CardHeader>
              <CardContent className="text-center">
                <div className="text-3xl font-bold text-yellow-500">{displayMetrics.failedAttempts}</div>
                <div className="text-sm text-muted-foreground">Last hour</div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Threats Tab */}
        <TabsContent value="threats" className="space-y-4">
          <div className="flex items-center gap-4 mb-4">
            <Select value={threatFilter} onValueChange={setThreatFilter}>
              <SelectTrigger className="w-48">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Threats</SelectItem>
                <SelectItem value="critical">Critical</SelectItem>
                <SelectItem value="high">High</SelectItem>
                <SelectItem value="medium">Medium</SelectItem>
                <SelectItem value="low">Low</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-4">
            {filteredThreats.map((threat) => (
              <Card key={threat.id} className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      {getThreatIcon(threat.type)}
                      <span className="capitalize">{threat.type.replace('_', ' ')}</span>
                    </div>
                    <div className="flex gap-2">
                      <Badge variant="default" className={getSeverityColor(threat.severity)}>
                        {threat.severity}
                      </Badge>
                      <Badge variant="default" className={getStatusColor(threat.status)}>
                        {threat.status}
                      </Badge>
                    </div>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <p className="text-sm">{threat.description}</p>

                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-muted-foreground">Source:</span>
                        <span className="ml-2 font-medium">{threat.source}</span>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Detected:</span>
                        <span className="ml-2 font-medium">
                          {new Date(threat.timestamp).toLocaleString()}
                        </span>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button size="sm" variant="outline">
                        View Details
                      </Button>
                      {threat.status === 'investigating' && (
                        <Button size="sm" variant="outline">
                          Mark Resolved
                        </Button>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Access Control Tab */}
        <TabsContent value="access" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Key className="h-5 w-5" />
                User Access Management
              </CardTitle>
              <CardDescription>
                Manage user permissions and access controls
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {displayAccessControls.map((user) => (
                  <div key={user.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <div>
                        <div className="font-medium">{user.user}</div>
                        <div className="text-sm text-muted-foreground capitalize">
                          Role: {user.role} â€¢ Last access: {new Date(user.lastAccess).toLocaleDateString()}
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <Badge variant="default" className={getStatusColor(user.status)}>
                          {user.status}
                        </Badge>
                        {user.twoFactorEnabled && (
                          <Badge variant="secondary">
                            <Fingerprint className="h-3 w-3 mr-1" />
                            2FA
                          </Badge>
                        )}
                      </div>
                    </div>

                    <div className="mb-3">
                      <div className="text-sm text-muted-foreground mb-1">Permissions:</div>
                      <div className="flex flex-wrap gap-1">
                        {user.permissions.map((permission, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {permission}
                          </Badge>
                        ))}
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button 
                        size="sm" 
                        variant="outline"
                        onClick={() => handleUpdateAccess(user.id, 'edit')}
                      >
                        Edit Permissions
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline"
                        onClick={() => handleUpdateAccess(user.id, user.status === 'active' ? 'suspend' : 'activate')}
                      >
                        {user.status === 'active' ? 'Suspend' : 'Activate'}
                      </Button>
                      {!user.twoFactorEnabled && (
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => handleUpdateAccess(user.id, 'enable_2fa')}
                        >
                          Enable 2FA
                        </Button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Scanning Tab */}
        <TabsContent value="scanning" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Scan className="h-5 w-5" />
                Security Scanning
              </CardTitle>
              <CardDescription>
                Perform comprehensive security scans
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Scan Type</label>
                  <Select value={scanType} onValueChange={setScanType}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="quick">Quick Scan</SelectItem>
                      <SelectItem value="full">Full System Scan</SelectItem>
                      <SelectItem value="vulnerability">Vulnerability Assessment</SelectItem>
                      <SelectItem value="network">Network Security Scan</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <Button 
                onClick={handleStartScan}
                disabled={scanMutation.isPending}
                className="cosmic-action-btn"
              >
                <Scan className="h-4 w-4 mr-2" />
                Start Security Scan
              </Button>

              <Separator />

              <div className="space-y-3">
                <h4 className="font-medium">Last Scan Results</h4>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-green-500">0</div>
                    <div className="text-sm text-muted-foreground">Critical Issues</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-yellow-500">2</div>
                    <div className="text-sm text-muted-foreground">Warnings</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-blue-500">15</div>
                    <div className="text-sm text-muted-foreground">Files Checked</div>
                  </div>
                  <div className="text-center p-3 border rounded-lg">
                    <div className="text-2xl font-bold text-green-500">98.7%</div>
                    <div className="text-sm text-muted-foreground">Security Score</div>
                  </div>
                </div>

                <div className="text-xs text-muted-foreground">
                  Last scan: {new Date(displayMetrics.lastScan).toLocaleString()}
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Audit Log Tab */}
        <TabsContent value="audit" className="space-y-4">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Security Audit Log
              </CardTitle>
              <CardDescription>
                Comprehensive log of all security events
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {[
                  { time: '15:45:23', event: 'Blocked intrusion attempt', severity: 'high', user: 'system' },
                  { time: '14:30:17', event: 'User login successful', severity: 'low', user: 'admin@kloudbugscafe.com' },
                  { time: '13:22:09', event: 'Failed login attempt', severity: 'medium', user: 'unknown' },
                  { time: '12:15:44', event: 'Security scan completed', severity: 'low', user: 'system' },
                  { time: '11:08:31', event: 'Firewall rule updated', severity: 'medium', user: 'admin@kloudbugscafe.com' },
                  { time: '10:45:18', event: 'Malware detected and quarantined', severity: 'high', user: 'system' },
                  { time: '09:33:27', event: 'User permissions modified', severity: 'medium', user: 'admin@kloudbugscafe.com' },
                  { time: '08:19:55', event: 'System startup - all services online', severity: 'low', user: 'system' }
                ].map((log, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Badge variant="default" className={getSeverityColor(log.severity)}>
                        {log.severity}
                      </Badge>
                      <span className="text-sm">{log.event}</span>
                    </div>
                    <div className="text-right text-sm">
                      <div className="font-medium">{log.time}</div>
                      <div className="text-muted-foreground">{log.user}</div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

