import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Terminal, 
  Play, 
  Copy, 
  Trash2, 
  Save, 
  Download,
  Settings,
  Globe,
  Zap,
  Database,
  Code,
  Send,
  History,
  BookOpen,
  CheckCircle,
  AlertTriangle,
  Clock,
  Server
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface ApiRequest {
  id: string;
  name: string;
  method: 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH';
  url: string;
  headers: Record<string, string>;
  body: string;
  timestamp: string;
}

interface ApiResponse {
  id: string;
  status: number;
  statusText: string;
  headers: Record<string, string>;
  data: any;
  duration: number;
  timestamp: string;
  size: number;
}

interface TerminalLog {
  id: string;
  type: 'request' | 'response' | 'error' | 'info';
  message: string;
  timestamp: string;
  requestId?: string;
}

export default function ApiTesting() {
  const [activeTab, setActiveTab] = useState('builder');
  const [method, setMethod] = useState<'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH'>('GET');
  const [url, setUrl] = useState('');
  const [headers, setHeaders] = useState('{"Content-Type": "application/json"}');
  const [body, setBody] = useState('{}');
  const [requestName, setRequestName] = useState('');
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState<ApiResponse | null>(null);
  const [savedRequests, setSavedRequests] = useState<ApiRequest[]>([]);
  const [history, setHistory] = useState<(ApiRequest & { response?: ApiResponse })[]>([]);
  const [terminalLogs, setTerminalLogs] = useState<TerminalLog[]>([]);
  const [autoScroll, setAutoScroll] = useState(true);

  const terminalRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  // Predefined API endpoints for the platform
  const platformEndpoints = [
    { name: 'Bot Status', method: 'GET', url: '/api/bot/status', category: 'Trading' },
    { name: 'Start Trading', method: 'POST', url: '/api/bot/start', category: 'Trading' },
    { name: 'Stop Trading', method: 'POST', url: '/api/bot/stop', category: 'Trading' },
    { name: 'Emergency Stop', method: 'POST', url: '/api/bot/emergency-stop', category: 'Trading' },
    { name: 'Market Data', method: 'GET', url: '/api/market/ohlc', category: 'Market' },
    { name: 'Technical Indicators', method: 'GET', url: '/api/market/indicators', category: 'Market' },
    { name: 'AI Prediction', method: 'GET', url: '/api/ai/prediction', category: 'AI' },
    { name: 'Mining Rigs', method: 'GET', url: '/api/mining/rigs', category: 'Mining' },
    { name: 'Web Miners', method: 'GET', url: '/api/webmining/miners', category: 'Web Mining' },
    { name: 'Python Miners', method: 'GET', url: '/api/webmining/python-miners', category: 'Web Mining' },
    { name: 'TERA AI Metrics', method: 'GET', url: '/api/tera/system/metrics', category: 'TERA AI' },
    { name: 'TERJustice Cases', method: 'GET', url: '/api/tera/cases', category: 'TERA AI' },
    { name: 'Social Projects', method: 'GET', url: '/api/social/projects', category: 'Social Impact' },
    { name: 'Token Metrics', method: 'GET', url: '/api/social/token-metrics', category: 'Social Impact' },
    { name: 'Cafe Menu', method: 'GET', url: '/api/cafe/menu', category: 'Cafe' },
    { name: 'Cafe Orders', method: 'GET', url: '/api/cafe/orders', category: 'Cafe' },
    { name: 'Admin Tasks', method: 'GET', url: '/api/admin/tasks', category: 'Admin' },
    { name: 'Admin Metrics', method: 'GET', url: '/api/admin/metrics', category: 'Admin' }
  ];

  const addLog = (type: TerminalLog['type'], message: string, requestId?: string) => {
    const log: TerminalLog = {
      id: `log-${Date.now()}-${Math.random()}`,
      type,
      message,
      timestamp: new Date().toISOString(),
      requestId
    };
    setTerminalLogs(prev => [...prev, log]);
  };

  useEffect(() => {
    if (autoScroll && terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [terminalLogs, autoScroll]);

  const executeRequest = async () => {
    if (!url) {
      toast({ title: "Error", description: "URL is required", variant: "destructive" });
      return;
    }

    setLoading(true);
    const requestId = `req-${Date.now()}`;
    const startTime = Date.now();

    // Parse headers
    let parsedHeaders: Record<string, string> = {};
    try {
      parsedHeaders = JSON.parse(headers);
    } catch (e) {
      addLog('error', 'Invalid JSON in headers');
      setLoading(false);
      return;
    }

    // Parse body for non-GET requests
    let parsedBody: any = undefined;
    if (method !== 'GET' && body.trim()) {
      try {
        parsedBody = JSON.parse(body);
      } catch (e) {
        addLog('error', 'Invalid JSON in request body');
        setLoading(false);
        return;
      }
    }

    const request: ApiRequest = {
      id: requestId,
      name: requestName || `${method} ${url}`,
      method,
      url,
      headers: parsedHeaders,
      body,
      timestamp: new Date().toISOString()
    };

    addLog('request', `âž¤ ${method} ${url}`, requestId);

    try {
      const fetchOptions: RequestInit = {
        method,
        headers: parsedHeaders,
      };

      if (parsedBody !== undefined) {
        fetchOptions.body = JSON.stringify(parsedBody);
      }

      const response = await fetch(url, fetchOptions);
      const duration = Date.now() - startTime;

      // Get response data
      const contentType = response.headers.get('content-type');
      let responseData: any;

      if (contentType && contentType.includes('application/json')) {
        responseData = await response.json();
      } else {
        responseData = await response.text();
      }

      // Build response headers object
      const responseHeaders: Record<string, string> = {};
      response.headers.forEach((value, key) => {
        responseHeaders[key] = value;
      });

      const apiResponse: ApiResponse = {
        id: `res-${requestId}`,
        status: response.status,
        statusText: response.statusText,
        headers: responseHeaders,
        data: responseData,
        duration,
        timestamp: new Date().toISOString(),
        size: JSON.stringify(responseData).length
      };

      setResponse(apiResponse);

      // Add to history
      setHistory(prev => [{ ...request, response: apiResponse }, ...prev].slice(0, 50));

      // Log response
      const statusColor = response.status < 300 ? 'âœ“' : response.status < 500 ? 'âš ' : 'âœ—';
      addLog(
        response.status < 300 ? 'response' : 'error',
        `${statusColor} ${response.status} ${response.statusText} (${duration}ms)`,
        requestId
      );

      toast({ 
        title: "Request Complete", 
        description: `${response.status} ${response.statusText} in ${duration}ms` 
      });

    } catch (error) {
      const duration = Date.now() - startTime;
      addLog('error', `âœ— Request failed: ${error instanceof Error ? error.message : 'Unknown error'}`, requestId);

      toast({ 
        title: "Request Failed", 
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const loadPredefinedRequest = (endpoint: typeof platformEndpoints[0]) => {
    setMethod(endpoint.method as any);
    setUrl(endpoint.url);
    setRequestName(endpoint.name);

    if (endpoint.method === 'POST' || endpoint.method === 'PUT' || endpoint.method === 'PATCH') {
      setBody('{}');
    } else {
      setBody('');
    }
  };

  const saveRequest = () => {
    if (!url) {
      toast({ title: "Error", description: "URL is required to save", variant: "destructive" });
      return;
    }

    const request: ApiRequest = {
      id: `saved-${Date.now()}`,
      name: requestName || `${method} ${url}`,
      method,
      url,
      headers: JSON.parse(headers),
      body,
      timestamp: new Date().toISOString()
    };

    setSavedRequests(prev => [request, ...prev]);
    toast({ title: "Saved", description: "Request saved successfully" });
  };

  const loadRequest = (request: ApiRequest) => {
    setMethod(request.method);
    setUrl(request.url);
    setHeaders(JSON.stringify(request.headers, null, 2));
    setBody(request.body);
    setRequestName(request.name);
  };

  const clearTerminal = () => {
    setTerminalLogs([]);
  };

  const copyResponse = () => {
    if (response) {
      navigator.clipboard.writeText(JSON.stringify(response.data, null, 2));
      toast({ title: "Copied", description: "Response copied to clipboard" });
    }
  };

  const getStatusColor = (status: number) => {
    if (status < 300) return 'text-green-500';
    if (status < 500) return 'text-yellow-500';
    return 'text-red-500';
  };

  const getLogIcon = (type: TerminalLog['type']) => {
    switch (type) {
      case 'request': return <Send className="h-3 w-3 text-blue-400" />;
      case 'response': return <CheckCircle className="h-3 w-3 text-green-400" />;
      case 'error': return <AlertTriangle className="h-3 w-3 text-red-400" />;
      case 'info': return <Globe className="h-3 w-3 text-cyan-400" />;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="relative p-8 rounded-lg bg-gradient-to-r from-slate-900/50 via-slate-800/30 to-slate-900/50 backdrop-blur-sm border border-cyan-500/30">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/5 via-purple-500/5 to-pink-500/5 rounded-lg"></div>
        <div className="relative z-10">
          <div className="flex items-center gap-4 mb-4">
            <div className="p-3 bg-gradient-to-r from-cyan-500/20 to-purple-500/20 rounded-lg">
              <Terminal className="h-8 w-8 text-cyan-400" />
            </div>
            <div>
              <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent">
                API Testing Platform
              </h1>
              <p className="text-lg text-cyan-300/80 font-light">
                Comprehensive REST API testing with live terminal and response analysis
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Left Panel - Request Builder */}
        <div className="space-y-6">
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="builder">Request Builder</TabsTrigger>
              <TabsTrigger value="presets">Presets</TabsTrigger>
              <TabsTrigger value="saved">Saved</TabsTrigger>
            </TabsList>

            <TabsContent value="builder" className="space-y-4">
              <Card className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Code className="h-5 w-5" />
                    Request Configuration
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex gap-2">
                    <Select value={method} onValueChange={(value: any) => setMethod(value)}>
                      <SelectTrigger className="w-32">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="GET">GET</SelectItem>
                        <SelectItem value="POST">POST</SelectItem>
                        <SelectItem value="PUT">PUT</SelectItem>
                        <SelectItem value="DELETE">DELETE</SelectItem>
                        <SelectItem value="PATCH">PATCH</SelectItem>
                      </SelectContent>
                    </Select>
                    <Input
                      placeholder="Request URL (e.g., /api/bot/status)"
                      value={url}
                      onChange={(e) => setUrl(e.target.value)}
                      className="flex-1"
                    />
                  </div>

                  <Input
                    placeholder="Request Name (optional)"
                    value={requestName}
                    onChange={(e) => setRequestName(e.target.value)}
                  />

                  <div>
                    <label className="text-sm font-medium mb-2 block">Headers (JSON)</label>
                    <Textarea
                      placeholder='{"Content-Type": "application/json"}'
                      value={headers}
                      onChange={(e) => setHeaders(e.target.value)}
                      rows={3}
                    />
                  </div>

                  {method !== 'GET' && (
                    <div>
                      <label className="text-sm font-medium mb-2 block">Request Body (JSON)</label>
                      <Textarea
                        placeholder="{}"
                        value={body}
                        onChange={(e) => setBody(e.target.value)}
                        rows={6}
                      />
                    </div>
                  )}

                  <div className="flex gap-2">
                    <Button 
                      onClick={executeRequest} 
                      disabled={loading || !url}
                      className="flex-1"
                    >
                      {loading ? (
                        <><Clock className="h-4 w-4 mr-2 animate-spin" /> Sending...</>
                      ) : (
                        <><Play className="h-4 w-4 mr-2" /> Send Request</>
                      )}
                    </Button>
                    <Button variant="outline" onClick={saveRequest}>
                      <Save className="h-4 w-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="presets" className="space-y-4">
              <Card className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <BookOpen className="h-5 w-5" />
                    Platform API Endpoints
                  </CardTitle>
                  <CardDescription>
                    Pre-configured endpoints for all platform features
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <ScrollArea className="h-96">
                    <div className="space-y-2">
                      {Object.entries(
                        platformEndpoints.reduce((acc, endpoint) => {
                          if (!acc[endpoint.category]) acc[endpoint.category] = [];
                          acc[endpoint.category].push(endpoint);
                          return acc;
                        }, {} as Record<string, typeof platformEndpoints>)
                      ).map(([category, endpoints]) => (
                        <div key={category} className="space-y-2">
                          <h4 className="font-medium text-sm text-muted-foreground">{category}</h4>
                          {endpoints.map((endpoint, index) => (
                            <Button
                              key={index}
                              variant="outline"
                              size="sm"
                              className="w-full justify-start"
                              onClick={() => loadPredefinedRequest(endpoint)}
                            >
                              <Badge variant="outline" className="mr-2 text-xs">
                                {endpoint.method}
                              </Badge>
                              {endpoint.name}
                            </Button>
                          ))}
                        </div>
                      ))}
                    </div>
                  </ScrollArea>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="saved" className="space-y-4">
              <Card className="cosmic-card">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <History className="h-5 w-5" />
                    Saved Requests
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <ScrollArea className="h-96">
                    {savedRequests.length === 0 ? (
                      <p className="text-muted-foreground text-center py-8">No saved requests yet</p>
                    ) : (
                      <div className="space-y-2">
                        {savedRequests.map((request) => (
                          <Button
                            key={request.id}
                            variant="outline"
                            size="sm"
                            className="w-full justify-start"
                            onClick={() => loadRequest(request)}
                          >
                            <Badge variant="outline" className="mr-2 text-xs">
                              {request.method}
                            </Badge>
                            {request.name}
                          </Button>
                        ))}
                      </div>
                    )}
                  </ScrollArea>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>

        {/* Right Panel - Response & Terminal */}
        <div className="space-y-6">
          {/* Response */}
          {response && (
            <Card className="cosmic-card">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="flex items-center gap-2">
                    <Server className="h-5 w-5" />
                    Response
                    <Badge className={getStatusColor(response.status)}>
                      {response.status} {response.statusText}
                    </Badge>
                  </CardTitle>
                  <div className="flex gap-2">
                    <span className="text-sm text-muted-foreground">
                      {response.duration}ms â€¢ {(response.size / 1024).toFixed(1)}KB
                    </span>
                    <Button size="sm" variant="outline" onClick={copyResponse}>
                      <Copy className="h-3 w-3" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <ScrollArea className="h-64">
                  <pre className="text-xs bg-slate-800/50 p-4 rounded-lg overflow-x-auto">
                    {JSON.stringify(response.data, null, 2)}
                  </pre>
                </ScrollArea>
              </CardContent>
            </Card>
          )}

          {/* Terminal */}
          <Card className="cosmic-card">
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center gap-2">
                  <Terminal className="h-5 w-5" />
                  Request Terminal
                </CardTitle>
                <div className="flex gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => setAutoScroll(!autoScroll)}
                  >
                    {autoScroll ? 'Auto Scroll: ON' : 'Auto Scroll: OFF'}
                  </Button>
                  <Button size="sm" variant="outline" onClick={clearTerminal}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <ScrollArea 
                className="h-80 bg-slate-900/50 rounded-lg p-4 font-mono text-xs"
                ref={terminalRef}
              >
                {terminalLogs.length === 0 ? (
                  <p className="text-muted-foreground">Terminal ready. Send a request to see logs...</p>
                ) : (
                  <div className="space-y-1">
                    {terminalLogs.map((log) => (
                      <div key={log.id} className="flex items-start gap-2">
                        <span className="text-muted-foreground text-xs">
                          {new Date(log.timestamp).toLocaleTimeString()}
                        </span>
                        {getLogIcon(log.type)}
                        <span className={`flex-1 ${
                          log.type === 'error' ? 'text-red-400' :
                          log.type === 'response' ? 'text-green-400' :
                          log.type === 'request' ? 'text-blue-400' :
                          'text-cyan-400'
                        }`}>
                          {log.message}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </ScrollArea>
            </CardContent>
          </Card>

          {/* Request History */}
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <History className="h-5 w-5" />
                Recent Requests
              </CardTitle>
            </CardHeader>
            <CardContent>
              <ScrollArea className="h-32">
                {history.length === 0 ? (
                  <p className="text-muted-foreground text-center py-4">No requests yet</p>
                ) : (
                  <div className="space-y-2">
                    {history.slice(0, 10).map((item) => (
                      <div key={item.id} className="flex items-center justify-between text-sm">
                        <div className="flex items-center gap-2">
                          <Badge variant="outline" className="text-xs">
                            {item.method}
                          </Badge>
                          <span className="truncate">{item.url}</span>
                        </div>
                        {item.response && (
                          <Badge className={`text-xs ${getStatusColor(item.response.status)}`}>
                            {item.response.status}
                          </Badge>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </ScrollArea>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

