import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Globe, 
  Cpu, 
  Play, 
  Square, 
  Upload, 
  Download,
  FileCode,
  AlertTriangle,
  CheckCircle,
  Activity,
  Monitor,
  Settings,
  Zap,
  Shield,
  FolderOpen,
  Trash2,
  RefreshCw,
  Terminal,
  Plus
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface WebMiner {
  id: string;
  name: string;
  type: 'browser' | 'javascript' | 'webassembly' | 'python';
  status: 'running' | 'stopped' | 'error' | 'loading';
  hashrate: number;
  threads: number;
  power: number;
  algorithm: string;
  pool: string;
  uptime: number;
  totalHashes: number;
  acceptedShares: number;
  rejectedShares: number;
  lastUpdate: string;
}

interface PythonMiner {
  id: string;
  filename: string;
  name: string;
  status: 'uploaded' | 'validated' | 'running' | 'error' | 'stopped';
  type: 'python';
  size: number;
  lastModified: string;
  errorMessage?: string;
  process?: {
    pid: number;
    cpu: number;
    memory: number;
    uptime: number;
  };
}

export default function WebMining() {
  const [activeTab, setActiveTab] = useState('webminers');
  const [selectedMiner, setSelectedMiner] = useState('');
  const [uploading, setUploading] = useState(false);
  const [newMinerName, setNewMinerName] = useState('');
  const [newMinerAlgorithm, setNewMinerAlgorithm] = useState('');
  const [newMinerPool, setNewMinerPool] = useState('');

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch web miners
  const { data: webMiners, isLoading: webMinersLoading } = useQuery({
    queryKey: ['/api/webmining/miners'],
    refetchInterval: 5000,
  });

  // Fetch Python miners
  const { data: pythonMiners, isLoading: pythonMinersLoading } = useQuery({
    queryKey: ['/api/webmining/python-miners'],
    refetchInterval: 10000,
  });

  // Start/Stop web miner
  const toggleMinerMutation = useMutation({
    mutationFn: (data: { minerId: string; action: 'start' | 'stop' }) => 
      apiRequest('/api/webmining/control', 'POST', data),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/webmining/miners'] });
      toast({ 
        title: `Miner ${variables.action === 'start' ? 'Started' : 'Stopped'}`, 
        description: `Web miner ${variables.action} successful` 
      });
    },
    onError: () => {
      toast({ 
        title: "Control Failed", 
        description: "Failed to control web miner",
        variant: "destructive"
      });
    }
  });

  // Upload Python miner
  const uploadPythonMinerMutation = useMutation({
    mutationFn: (formData: FormData) => 
      fetch('/api/webmining/upload-python', {
        method: 'POST',
        body: formData
      }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/webmining/python-miners'] });
      toast({ 
        title: "Upload Successful", 
        description: "Python miner uploaded and validated safely" 
      });
      setUploading(false);
    },
    onError: () => {
      toast({ 
        title: "Upload Failed", 
        description: "Failed to upload Python miner",
        variant: "destructive"
      });
      setUploading(false);
    }
  });

  // Start/Stop Python miner
  const togglePythonMinerMutation = useMutation({
    mutationFn: (data: { minerId: string; action: 'start' | 'stop' }) => 
      apiRequest('/api/webmining/python-control', 'POST', data),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/webmining/python-miners'] });
      toast({ 
        title: `Python Miner ${variables.action === 'start' ? 'Started' : 'Stopped'}`, 
        description: `Python miner ${variables.action} successful` 
      });
    }
  });

  // Delete Python miner
  const deletePythonMinerMutation = useMutation({
    mutationFn: (minerId: string) => apiRequest('/api/webmining/python-delete', 'DELETE', { minerId }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/webmining/python-miners'] });
      toast({ title: "Deleted", description: "Python miner removed successfully" });
    }
  });

  // Create new web miner
  const createWebMinerMutation = useMutation({
    mutationFn: (data: { name: string; algorithm: string; pool: string }) => 
      apiRequest('/api/webmining/create', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/webmining/miners'] });
      toast({ title: "Created", description: "New web miner created successfully" });
      setNewMinerName('');
      setNewMinerAlgorithm('');
      setNewMinerPool('');
    }
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.name.endsWith('.py')) {
      toast({ 
        title: "Invalid File", 
        description: "Only Python (.py) files are allowed",
        variant: "destructive"
      });
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('pythonMiner', file);
    uploadPythonMinerMutation.mutate(formData);
  };

  // Mock data for web miners
  const defaultWebMiners: WebMiner[] = [
    {
      id: 'web-1',
      name: 'Browser Miner Alpha',
      type: 'browser',
      status: 'running',
      hashrate: 125.5,
      threads: 4,
      power: 85,
      algorithm: 'RandomX',
      pool: 'KLOUDBUGSCAFE POOL',
      uptime: 23.5,
      totalHashes: 45678901,
      acceptedShares: 1247,
      rejectedShares: 3,
      lastUpdate: new Date().toISOString()
    },
    {
      id: 'web-2',
      name: 'WASM Miner Beta',
      type: 'webassembly',
      status: 'running',
      hashrate: 89.2,
      threads: 2,
      power: 45,
      algorithm: 'CryptoNight',
      pool: 'TERA SOCIAL JUSTICE POOL',
      uptime: 15.2,
      totalHashes: 23456789,
      acceptedShares: 834,
      rejectedShares: 1,
      lastUpdate: new Date().toISOString()
    },
    {
      id: 'web-3',
      name: 'JS Miner Gamma',
      type: 'javascript',
      status: 'stopped',
      hashrate: 0,
      threads: 1,
      power: 0,
      algorithm: 'Scrypt',
      pool: 'KLOUDBUGSCAFE POOL',
      uptime: 0,
      totalHashes: 12345678,
      acceptedShares: 456,
      rejectedShares: 0,
      lastUpdate: new Date().toISOString()
    }
  ];

  const defaultPythonMiners: PythonMiner[] = [
    {
      id: 'py-1',
      filename: 'advanced_gpu_miner.py',
      name: 'Advanced GPU Miner',
      status: 'running',
      type: 'python',
      size: 45678,
      lastModified: '2024-02-10T14:30:00Z',
      process: {
        pid: 12345,
        cpu: 15.8,
        memory: 125.6,
        uptime: 3600
      }
    },
    {
      id: 'py-2',
      filename: 'cpu_optimizer.py',
      name: 'CPU Optimizer',
      status: 'validated',
      type: 'python',
      size: 23456,
      lastModified: '2024-02-09T09:15:00Z'
    }
  ];

  const webMinersData = webMiners || defaultWebMiners;
  const pythonMinersData = pythonMiners || defaultPythonMiners;

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'running': return 'text-green-500 bg-green-500/10';
      case 'stopped': return 'text-gray-500 bg-gray-500/10';
      case 'error': return 'text-red-500 bg-red-500/10';
      case 'loading': case 'uploaded': case 'validated': return 'text-yellow-500 bg-yellow-500/10';
      default: return 'text-gray-500 bg-gray-500/10';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'running': return <CheckCircle className="h-4 w-4" />;
      case 'stopped': return <Square className="h-4 w-4" />;
      case 'error': return <AlertTriangle className="h-4 w-4" />;
      case 'loading': case 'uploaded': case 'validated': return <RefreshCw className="h-4 w-4" />;
      default: return <Activity className="h-4 w-4" />;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="relative p-8 rounded-lg bg-gradient-to-r from-slate-900/50 via-slate-800/30 to-slate-900/50 backdrop-blur-sm border border-cyan-500/30">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/5 via-purple-500/5 to-pink-500/5 rounded-lg"></div>
        <div className="relative z-10">
          <div className="flex items-center gap-4 mb-4">
            <div className="p-3 bg-gradient-to-r from-cyan-500/20 to-purple-500/20 rounded-lg">
              <Globe className="h-8 w-8 text-cyan-400" />
            </div>
            <div>
              <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent">
                Web Mining Hub
              </h1>
              <p className="text-lg text-cyan-300/80 font-light">
                Browser-based mining and secure Python miner integration
              </p>
            </div>
          </div>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="webminers">Web Miners</TabsTrigger>
          <TabsTrigger value="python">Python Miners</TabsTrigger>
          <TabsTrigger value="upload">Upload & Manage</TabsTrigger>
        </TabsList>

        {/* Web Miners Tab */}
        <TabsContent value="webminers" className="space-y-6">
          {/* Create New Web Miner */}
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Plus className="h-5 w-5" />
                Create New Web Miner
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <Input
                  placeholder="Miner Name"
                  value={newMinerName}
                  onChange={(e) => setNewMinerName(e.target.value)}
                />
                <Select value={newMinerAlgorithm} onValueChange={setNewMinerAlgorithm}>
                  <SelectTrigger>
                    <SelectValue placeholder="Algorithm" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="RandomX">RandomX</SelectItem>
                    <SelectItem value="CryptoNight">CryptoNight</SelectItem>
                    <SelectItem value="Scrypt">Scrypt</SelectItem>
                    <SelectItem value="Ethash">Ethash</SelectItem>
                  </SelectContent>
                </Select>
                <Input
                  placeholder="Pool URL"
                  value={newMinerPool}
                  onChange={(e) => setNewMinerPool(e.target.value)}
                />
              </div>
              <Button 
                onClick={() => createWebMinerMutation.mutate({
                  name: newMinerName,
                  algorithm: newMinerAlgorithm,
                  pool: newMinerPool
                })}
                disabled={!newMinerName || !newMinerAlgorithm || !newMinerPool || createWebMinerMutation.isPending}
                className="w-full"
              >
                <Plus className="h-4 w-4 mr-2" />
                {createWebMinerMutation.isPending ? 'Creating...' : 'Create Web Miner'}
              </Button>
            </CardContent>
          </Card>

          {/* Web Miners Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {webMinersData.map((miner) => (
              <Card key={miner.id} className="cosmic-card">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-gradient-to-r from-cyan-500/20 to-blue-500/20 rounded-lg">
                        <Globe className="h-6 w-6 text-cyan-400" />
                      </div>
                      <div>
                        <CardTitle className="text-lg">{miner.name}</CardTitle>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge className={`text-xs ${getStatusColor(miner.status)}`}>
                            {getStatusIcon(miner.status)}
                            <span className="ml-1 capitalize">{miner.status}</span>
                          </Badge>
                          <span className="text-xs text-muted-foreground">{miner.type}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="space-y-4">
                  {/* Performance Metrics */}
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-muted-foreground">Hashrate</span>
                      <div className="font-bold text-green-500">{miner.hashrate} H/s</div>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Threads</span>
                      <div className="font-bold">{miner.threads}</div>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Power</span>
                      <div className="font-bold text-orange-500">{miner.power}W</div>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Uptime</span>
                      <div className="font-bold">{miner.uptime}h</div>
                    </div>
                  </div>

                  {/* Pool and Algorithm */}
                  <div className="space-y-2">
                    <div className="text-xs">
                      <span className="text-muted-foreground">Algorithm: </span>
                      <Badge variant="outline">{miner.algorithm}</Badge>
                    </div>
                    <div className="text-xs">
                      <span className="text-muted-foreground">Pool: </span>
                      <span className="font-medium">{miner.pool}</span>
                    </div>
                  </div>

                  {/* Shares Statistics */}
                  <div className="space-y-2">
                    <div className="flex justify-between text-xs">
                      <span>Accepted Shares</span>
                      <span className="text-green-500 font-bold">{miner.acceptedShares}</span>
                    </div>
                    <div className="flex justify-between text-xs">
                      <span>Rejected Shares</span>
                      <span className="text-red-500 font-bold">{miner.rejectedShares}</span>
                    </div>
                  </div>

                  {/* Control Buttons */}
                  <div className="flex gap-2 mt-4">
                    <Button 
                      size="sm" 
                      className="flex-1"
                      variant={miner.status === 'running' ? 'destructive' : 'default'}
                      onClick={() => toggleMinerMutation.mutate({
                        minerId: miner.id,
                        action: miner.status === 'running' ? 'stop' : 'start'
                      })}
                      disabled={toggleMinerMutation.isPending}
                    >
                      {miner.status === 'running' ? (
                        <><Square className="h-3 w-3 mr-1" /> Stop</>
                      ) : (
                        <><Play className="h-3 w-3 mr-1" /> Start</>
                      )}
                    </Button>
                    <Button size="sm" variant="outline">
                      <Settings className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Python Miners Tab */}
        <TabsContent value="python" className="space-y-6">
          <Alert>
            <Shield className="h-4 w-4" />
            <AlertDescription>
              Python miners run in isolated containers for security. All miners are validated before execution.
            </AlertDescription>
          </Alert>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {pythonMinersData.map((miner) => (
              <Card key={miner.id} className="cosmic-card">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-gradient-to-r from-yellow-500/20 to-orange-500/20 rounded-lg">
                        <FileCode className="h-6 w-6 text-yellow-400" />
                      </div>
                      <div>
                        <CardTitle className="text-lg">{miner.name}</CardTitle>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge className={`text-xs ${getStatusColor(miner.status)}`}>
                            {getStatusIcon(miner.status)}
                            <span className="ml-1 capitalize">{miner.status}</span>
                          </Badge>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="space-y-4">
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Filename</span>
                      <span className="font-mono">{miner.filename}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Size</span>
                      <span>{(miner.size / 1024).toFixed(1)} KB</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Modified</span>
                      <span>{new Date(miner.lastModified).toLocaleDateString()}</span>
                    </div>
                  </div>

                  {miner.process && (
                    <div className="space-y-2 p-3 bg-slate-800/50 rounded-lg">
                      <div className="text-xs font-medium text-green-500">Process Information</div>
                      <div className="grid grid-cols-2 gap-2 text-xs">
                        <div>
                          <span className="text-muted-foreground">PID:</span> {miner.process.pid}
                        </div>
                        <div>
                          <span className="text-muted-foreground">CPU:</span> {miner.process.cpu}%
                        </div>
                        <div>
                          <span className="text-muted-foreground">Memory:</span> {miner.process.memory} MB
                        </div>
                        <div>
                          <span className="text-muted-foreground">Uptime:</span> {Math.floor(miner.process.uptime / 60)}m
                        </div>
                      </div>
                    </div>
                  )}

                  {miner.errorMessage && (
                    <Alert variant="destructive">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertDescription className="text-xs">{miner.errorMessage}</AlertDescription>
                    </Alert>
                  )}

                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      className="flex-1"
                      variant={miner.status === 'running' ? 'destructive' : 'default'}
                      onClick={() => togglePythonMinerMutation.mutate({
                        minerId: miner.id,
                        action: miner.status === 'running' ? 'stop' : 'start'
                      })}
                      disabled={togglePythonMinerMutation.isPending || miner.status === 'error'}
                    >
                      {miner.status === 'running' ? (
                        <><Square className="h-3 w-3 mr-1" /> Stop</>
                      ) : (
                        <><Play className="h-3 w-3 mr-1" /> Start</>
                      )}
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => deletePythonMinerMutation.mutate(miner.id)}
                      disabled={deletePythonMinerMutation.isPending}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                    <Button size="sm" variant="outline">
                      <Terminal className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Upload & Manage Tab */}
        <TabsContent value="upload" className="space-y-6">
          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Upload className="h-5 w-5" />
                Upload Python Miner
              </CardTitle>
              <CardDescription>
                Upload Python mining scripts safely. All files are validated and run in isolated containers.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="border-2 border-dashed border-border rounded-lg p-8 text-center">
                <input
                  type="file"
                  accept=".py"
                  onChange={handleFileUpload}
                  className="hidden"
                  id="file-upload"
                  disabled={uploading}
                />
                <label 
                  htmlFor="file-upload" 
                  className="cursor-pointer flex flex-col items-center gap-2"
                >
                  {uploading ? (
                    <>
                      <RefreshCw className="h-8 w-8 text-cyan-400 animate-spin" />
                      <p className="text-lg font-medium">Uploading and validating...</p>
                    </>
                  ) : (
                    <>
                      <FolderOpen className="h-8 w-8 text-cyan-400" />
                      <p className="text-lg font-medium">Click to upload Python miner</p>
                      <p className="text-sm text-muted-foreground">Only .py files are supported</p>
                    </>
                  )}
                </label>
              </div>

              <Alert>
                <Shield className="h-4 w-4" />
                <AlertDescription>
                  <strong>Security Features:</strong>
                  <ul className="list-disc list-inside mt-2 space-y-1">
                    <li>All Python miners run in isolated Docker containers</li>
                    <li>File validation and malware scanning before execution</li>
                    <li>Resource limits prevent system crashes</li>
                    <li>Network isolation with controlled mining pool access only</li>
                    <li>Automatic cleanup on errors or completion</li>
                  </ul>
                </AlertDescription>
              </Alert>
            </CardContent>
          </Card>

          <Card className="cosmic-card">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Monitor className="h-5 w-5" />
                System Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center p-4 bg-slate-800/50 rounded-lg">
                  <div className="text-lg font-bold text-green-500">
                    {pythonMinersData.filter(m => m.status === 'running').length}
                  </div>
                  <div className="text-sm text-muted-foreground">Active Python Miners</div>
                </div>
                <div className="text-center p-4 bg-slate-800/50 rounded-lg">
                  <div className="text-lg font-bold text-cyan-500">
                    {webMinersData.filter(m => m.status === 'running').length}
                  </div>
                  <div className="text-sm text-muted-foreground">Active Web Miners</div>
                </div>
                <div className="text-center p-4 bg-slate-800/50 rounded-lg">
                  <div className="text-lg font-bold text-purple-500">
                    {webMinersData.reduce((sum, m) => sum + m.hashrate, 0).toFixed(1)} H/s
                  </div>
                  <div className="text-sm text-muted-foreground">Total Web Hashrate</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

