const aiSystems = Object.values(aiCategories).flat();

// Placeholder slots for new AIs
const placeholderSlots = [
  { id: 'ai-slot-1', name: 'Future AI', description: 'Future AI System', icon: Code },
  { id: 'ai-slot-2', name: 'Future AI', description: 'Future AI System', icon: Database },
