  { id: 'ai-slot-15', name: 'Future AI', description: 'Future AI System', icon: Calculator },
  { id: 'ai-slot-16', name: 'Future AI', description: 'Future AI System', icon: Brain },
  { id: 'ai-slot-17', name: 'Future AI', description: 'Future AI System', icon: Cpu },
  { id: 'ai-slot-18', name: 'Future AI', description: 'Future AI System', icon: Lock },
