  { id: 'ai-slot-20', name: 'Future AI', description: 'Future AI System', icon: Users }
];

export function CircularAINav() {
  const [isExpanded, setIsExpanded] = useState(false);
  const [hoveredAI, setHoveredAI] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'circular' | 'grid'>('circular');
  const [location] = useLocation();

  // Calculate positions for circular layout
  const totalItems = aiSystems.length + placeholderSlots.length;
  const radius = 100;
  const centerX = 150;
  const centerY = 150;

  const getPosition = (index: number) => {
    const angle = (index * 360 / totalItems) - 90; // Start from top
    const radian = (angle * Math.PI) / 180;
    const x = centerX + radius * Math.cos(radian);
    const y = centerY + radius * Math.sin(radian);
    return { x, y, angle };
  };

  const isActiveRoute = (route: string) => {
    return location === route;
  };

  const handleNavClick = (route: string) => {
    window.location.href = route;
    setIsExpanded(false);
  };

  return (
    <div className="fixed bottom-6 left-6 z-50">
      {/* Main Toggle Button */}
      <motion.div
        className="relative"
        initial={false}
        animate={{ scale: isExpanded ? 0.9 : 1 }}
      >
        <motion.button
          onClick={() => setIsExpanded(!isExpanded)}
          className={cn(
            "w-20 h-20 rounded-full bg-gradient-to-r from-cyan-500 to-purple-600",
            "flex items-center justify-center cursor-pointer transition-all duration-300",
            "border-4 border-cyan-400/50 backdrop-blur-sm shadow-2xl",
            "hover:scale-110 hover:shadow-xl hover:shadow-cyan-500/60",
            "active:scale-95",
            isExpanded && "shadow-xl shadow-cyan-500/60 scale-110"
          )}
          whileHover={{ scale: 1.15 }}
          whileTap={{ scale: 0.9 }}
          style={{ 
            boxShadow: isExpanded 
              ? '0 0 30px rgba(6, 182, 212, 0.6), 0 0 60px rgba(147, 51, 234, 0.4)' 
              : '0 0 20px rgba(6, 182, 212, 0.4)'
          }}
        >
          <motion.div
            animate={{ rotate: isExpanded ? 45 : 0 }}
            transition={{ duration: 0.3 }}
          >
            <Brain className="w-10 h-10 text-white drop-shadow-lg" />
          </motion.div>
        </motion.button>

        {/* Pulse Effect when not expanded */}
        {!isExpanded && (
          <motion.div
            className="absolute inset-0 rounded-full bg-gradient-to-r from-cyan-500/30 to-purple-600/30 pointer-events-none"
            animate={{
              scale: [1, 1.3, 1],
              opacity: [0.3, 0, 0.3]
            }}
            transition={{ 
              duration: 2, 
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        )}

        {/* Ripple Effect when expanded */}
        {isExpanded && (
          <motion.div
            className="absolute inset-0 rounded-full bg-gradient-to-r from-cyan-500/20 to-purple-600/20 pointer-events-none"
            animate={{
              scale: [1, 1.5, 1],
              opacity: [0.5, 0, 0.5]
            }}
            transition={{ duration: 1, repeat: Infinity }}
          />
        )}
      </motion.div>

      {/* AI Navigation Panel */}
      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ opacity: 0, scale: 0.5 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.5 }}
            transition={{ duration: 0.4, ease: "easeOut" }}
            className="absolute bottom-24 left-0 pointer-events-auto"
          >
            {/* Toggle View Mode Button */}
            <motion.div className="mb-4 flex justify-center">
              <button
                onClick={() => setViewMode(viewMode === 'circular' ? 'grid' : 'circular')}
                className="px-4 py-2 bg-slate-800/90 border border-cyan-500/30 rounded-lg backdrop-blur-sm
                          text-cyan-400 text-sm hover:bg-slate-700/90 transition-colors flex items-center gap-2"
              >
                {viewMode === 'circular' ? <Grid3X3 className="w-4 h-4" /> : <Brain className="w-4 h-4" />}
                {viewMode === 'circular' ? 'Grid View' : 'Circle View'}
              </button>
            </motion.div>

            {viewMode === 'circular' ? (
              /* Circular Layout */
              <div className="relative w-[300px] h-[300px]">
                {/* Background Circle */}
                <div className="absolute inset-0 rounded-full bg-gradient-to-r from-slate-900/40 via-slate-800/20 to-slate-900/40 backdrop-blur-sm border border-cyan-500/20" />

            {/* AI System Nodes */}
            {aiSystems.map((ai, index) => {
              const position = getPosition(index);
              const isActive = isActiveRoute(ai.route);
              const Icon = ai.icon;

              return (
                <motion.div
                  key={ai.id}
                  className="absolute pointer-events-auto"
                  style={{
                    left: position.x - 20,
                    top: position.y - 20
                  }}
                  initial={{ opacity: 0, scale: 0 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ 
                    delay: index * 0.05,
                    type: "spring",
                    stiffness: 200,
                    damping: 15
                  }}
                  onHoverStart={() => setHoveredAI(ai.id)}
                  onHoverEnd={() => setHoveredAI(null)}
                >
                  <motion.div
                    onClick={() => handleNavClick(ai.route)}
                    className={cn(
                      "w-12 h-12 rounded-full flex items-center justify-center cursor-pointer",
                      "border-2 transition-all duration-300 backdrop-blur-sm shadow-lg",
                      "bg-gradient-to-r", ai.color,
                      isActive 
                        ? "border-white shadow-xl scale-125 " + ai.glowColor
                        : "border-white/40 hover:border-white hover:scale-125 hover:shadow-xl hover:" + ai.glowColor.replace('shadow-', 'hover:shadow-')
                    )}
                    whileHover={{ scale: 1.3 }}
                    whileTap={{ scale: 0.9 }}
                    style={{
                      boxShadow: isActive 
                        ? `0 0 20px ${ai.glowColor.includes('purple') ? 'rgba(147, 51, 234, 0.8)' : 
                            ai.glowColor.includes('green') ? 'rgba(34, 197, 94, 0.8)' :
                            ai.glowColor.includes('blue') ? 'rgba(59, 130, 246, 0.8)' :
                            ai.glowColor.includes('orange') ? 'rgba(249, 115, 22, 0.8)' :
                            ai.glowColor.includes('red') ? 'rgba(239, 68, 68, 0.8)' :
                            ai.glowColor.includes('yellow') ? 'rgba(245, 158, 11, 0.8)' :
                            ai.glowColor.includes('teal') ? 'rgba(20, 184, 166, 0.8)' :
                            'rgba(147, 51, 234, 0.8)'}`
                        : undefined
                    }}
                  >
                    <Icon className="w-5 h-5 text-white drop-shadow-md" />
                  </motion.div>

                  {/* Tooltip */}
                  <AnimatePresence>
                    {hoveredAI === ai.id && (
                      <motion.div
                        initial={{ opacity: 0, y: 10, scale: 0.8 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: 10, scale: 0.8 }}
                        className={cn(
                          "absolute top-14 left-1/2 transform -translate-x-1/2",
                          "bg-slate-900/95 border border-cyan-500/30 rounded-lg p-2 min-w-[120px]",
                          "backdrop-blur-sm z-10"
                        )}
                      >
                        <div className="text-xs font-medium text-white text-center">
                          {ai.name}
                        </div>
                        <div className="text-xs text-cyan-300/80 text-center">
                          {ai.description}
                        </div>
                        {/* Arrow */}
                        <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-slate-900 border-l border-t border-cyan-500/30 rotate-45" />
                      </motion.div>
                    )}
                  </AnimatePresence>
                </motion.div>
              );
            })}

            {/* Placeholder Slots */}
            {placeholderSlots.map((slot, index) => {
              const position = getPosition(aiSystems.length + index);
              const SlotIcon = slot.icon;

              return (
                <motion.div
                  key={slot.id}
                  className="absolute pointer-events-auto"
                  style={{
                    left: position.x - 20,
                    top: position.y - 20
                  }}
                  initial={{ opacity: 0, scale: 0 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ 
                    delay: (aiSystems.length + index) * 0.05,
                    type: "spring",
                    stiffness: 200,
                    damping: 15
                  }}
                  onHoverStart={() => setHoveredAI(slot.id)}
                  onHoverEnd={() => setHoveredAI(null)}
                >
                  <motion.div
                    onClick={() => {
                      // You can add functionality here to add new AIs
                      console.log('Add new AI slot:', slot.id);
                    }}
                    className={cn(
                      "w-12 h-12 rounded-full flex items-center justify-center cursor-pointer",
                      "border-2 border-dashed border-gray-400/50 transition-all duration-300 shadow-lg",
                      "bg-gradient-to-r from-gray-600/30 to-gray-700/30 backdrop-blur-sm",
                      "hover:border-gray-300 hover:scale-125 hover:shadow-xl hover:shadow-gray-500/40",
                      "active:scale-95"
                    )}
                    whileHover={{ scale: 1.3 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <SlotIcon className="w-5 h-5 text-gray-300 drop-shadow-md" />
                  </motion.div>

                  {/* Placeholder Tooltip */}
                  <AnimatePresence>
                    {hoveredAI === slot.id && (
                      <motion.div
                        initial={{ opacity: 0, y: 10, scale: 0.8 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: 10, scale: 0.8 }}
                        className={cn(
                          "absolute top-14 left-1/2 transform -translate-x-1/2",
                          "bg-slate-900/95 border border-gray-500/30 rounded-lg p-2 min-w-[120px]",
                          "backdrop-blur-sm z-10"
                        )}
                      >
                        <div className="text-xs font-medium text-white text-center">
                          {slot.name}
                        </div>
                        <div className="text-xs text-gray-400/80 text-center">
                          {slot.description}
                        </div>
                        {/* Arrow */}
                        <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-slate-900 border-l border-t border-gray-500/30 rotate-45" />
                      </motion.div>
                    )}
                  </AnimatePresence>
                </motion.div>
              );
            })}

                {/* Center Label */}
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.5 }}
                  className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-center pointer-events-none"
                >
                  <div className="text-sm font-bold text-cyan-400">TERA AI</div>
                  <div className="text-xs text-cyan-300/60">ECOSYSTEM</div>
                </motion.div>
              </div>
            ) : (
              /* Categorized Grid Layout */
              <div className="bg-slate-900/95 border border-cyan-500/30 rounded-lg p-4 backdrop-blur-sm w-[700px] max-h-[600px] overflow-y-auto">
                <div className="text-center mb-4">
                  <div className="text-lg font-bold text-cyan-400">TERA AI Ecosystem</div>
                  <div className="text-sm text-cyan-300/60">Organized by Category ({aiSystems.length} Active Systems)</div>
                </div>

                {/* Categories */}
                {Object.entries(aiCategories).map(([categoryName, systems], categoryIndex) => (
                  <div key={categoryName} className="mb-4">
                    <div className="flex items-center mb-2">
                      <div className="text-sm font-semibold text-cyan-300">{categoryName}</div>
                      <div className="ml-2 text-xs text-cyan-300/60">({systems.length})</div>
                      <div className="ml-auto w-16 h-px bg-cyan-500/30"></div>
                    </div>

                    <div className="grid grid-cols-6 gap-2">
                      {systems.map((ai, index) => {
                        const Icon = ai.icon;
                        const isActive = isActiveRoute(ai.route);

                        return (
                          <motion.div
                            key={ai.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: (categoryIndex * 0.1) + (index * 0.05) }}
                            className="flex flex-col items-center"
                          >
                            <motion.div
                              onClick={() => handleNavClick(ai.route)}
                              onHoverStart={() => setHoveredAI(ai.id)}
                              onHoverEnd={() => setHoveredAI(null)}
                              className={cn(
                                "w-10 h-10 rounded-lg flex items-center justify-center cursor-pointer mb-1",
                                "border transition-all duration-300 backdrop-blur-sm",
                                "bg-gradient-to-r", ai.color,
                                isActive 
                                  ? "border-white shadow-lg scale-110"
                                  : "border-white/40 hover:border-white hover:scale-110"
                              )}
                              whileHover={{ scale: 1.1 }}
                              whileTap={{ scale: 0.95 }}
                            >
                              <Icon className="w-4 h-4 text-white" />
                            </motion.div>
                            <div className="text-xs text-center text-white/80 max-w-[60px] truncate leading-tight">
                              {ai.name.replace('TERA ', '').replace(' AI', '').replace('KB-', '').replace(' Mining', '').replace(' Controls', '')}
                            </div>

                            {/* Tooltip */}
                            <AnimatePresence>
                              {hoveredAI === ai.id && (
                                <motion.div
                                  initial={{ opacity: 0, y: 10, scale: 0.8 }}
                                  animate={{ opacity: 1, y: 0, scale: 1 }}
                                  exit={{ opacity: 0, y: 10, scale: 0.8 }}
                                  className="absolute z-50 mt-12 bg-slate-800/95 border border-cyan-500/30 rounded-lg p-2 min-w-[120px] backdrop-blur-sm"
                                >
                                  <div className="text-xs font-medium text-white text-center">
                                    {ai.name}
                                  </div>
                                  <div className="text-xs text-cyan-300/80 text-center">
                                    {ai.description}
                                  </div>
                                  <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-slate-800 border-l border-t border-cyan-500/30 rotate-45" />
                                </motion.div>
                              )}
                            </AnimatePresence>
                          </motion.div>
                        );
                      })}
                    </div>
                  </div>
                ))}

                <div className="border-t border-cyan-500/20 pt-3">
                  <div className="text-xs text-cyan-300/60 mb-2 text-center">Available Slots ({placeholderSlots.length} Ready)</div>
                  <div className="grid grid-cols-10 gap-1">
                    {placeholderSlots.map((slot, index) => {
                      const SlotIcon = slot.icon;
                      return (
                        <motion.div
                          key={slot.id}
                          initial={{ opacity: 0, y: 20 }}
                          animate={{ opacity: 1, y: 0 }}
                          transition={{ delay: (aiSystems.length + index) * 0.02 }}
                          className="flex flex-col items-center"
                        >
                          <motion.div
                            onClick={() => console.log('Add new AI slot:', slot.id)}
                            className="w-7 h-7 rounded-lg flex items-center justify-center cursor-pointer mb-1
                                     border border-dashed border-gray-400/50 transition-all duration-300
                                     bg-gradient-to-r from-gray-600/30 to-gray-700/30 backdrop-blur-sm
                                     hover:border-gray-300 hover:scale-110"
                            whileHover={{ scale: 1.1 }}
                            whileTap={{ scale: 0.95 }}
                          >
                            <SlotIcon className="w-3 h-3 text-gray-300" />
                          </motion.div>
                          <div className="text-xs text-center text-gray-400 max-w-[35px] truncate leading-tight">
                            {index + 1}
                          </div>
                        </motion.div>
                      );
                    })}
                  </div>
                </div>
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

