const musicGenres: MusicGenre[] = [
  { id: 'rock', name: 'Rock', folder: '/audio/genres/rock', icon: 'ðŸŽ¸' },
  { id: 'pop', name: 'Pop', folder: '/audio/genres/pop', icon: 'ðŸŽ¤' },
  { id: 'electronic', name: 'Electronic', folder: '/audio/genres/electronic', icon: 'ðŸŽ›ï¸' },
  { id: 'jazz', name: 'Jazz', folder: '/audio/genres/jazz', icon: 'ðŸŽ·' },
  { id: 'classical', name: 'Classical', folder: '/audio/genres/classical', icon: 'ðŸŽ¼' },
  { id: 'hip-hop', name: 'Hip-Hop', folder: '/audio/genres/hip-hop', icon: 'ðŸŽ¤' },
  { id: 'country', name: 'Country', folder: '/audio/genres/country', icon: 'ðŸ¤ ' },
  { id: 'blues', name: 'Blues', folder: '/audio/genres/blues', icon: 'ðŸŽº' },
  { id: 'reggae', name: 'Reggae', folder: '/audio/genres/reggae', icon: 'ðŸŒ´' },
  { id: 'folk', name: 'Folk', folder: '/audio/genres/folk', icon: 'ðŸª•' }
];

const podcastEpisodes: PodcastEpisode[] = [
  {
    id: 'ep1',
    title: 'KLOUD BUGS Mining Update #47',
    description: 'Latest mining pool performance and new TERA token developments',
    duration: '28:34',
    publishDate: '2024-02-15',
    url: '/audio/podcasts/mining-update-47.mp3'
  },
  {
    id: 'ep2',
    title: 'Crypto Trading Strategies with AI',
    description: 'How our TERA Exchange AI optimizes trading decisions',
    duration: '35:12',
    publishDate: '2024-02-12',
    url: '/audio/podcasts/ai-trading-strategies.mp3'
  },
  {
    id: 'ep3',
    title: 'Social Justice in Blockchain',
    description: 'Building a fairer crypto ecosystem through TERA tokens',
    duration: '42:18',
    publishDate: '2024-02-10',
    url: '/audio/podcasts/social-justice-blockchain.mp3'
  }
];

// Sample local tracks (in a real implementation, these would be loaded from the filesystem)
const sampleTracks: LocalTrack[] = [
  { id: 't1', title: 'Mining Anthem', artist: 'KLOUD BUGS', album: 'Digital Dreams', genre: 'electronic', url: '/audio/genres/electronic/mining-anthem.mp3', duration: '3:45' },
  { id: 't2', title: 'Crypto Blues', artist: 'Bitcoin Betty', album: 'Hash Rate', genre: 'blues', url: '/audio/genres/blues/crypto-blues.mp3', duration: '4:12' },
  { id: 't3', title: 'TERA Rising', artist: 'Justice Collective', album: 'Social Change', genre: 'folk', url: '/audio/genres/folk/tera-rising.mp3', duration: '3:28' }
];

export function KloudRadio() {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.7);
  const [isMuted, setIsMuted] = useState(false);
  const [currentStation, setCurrentStation] = useState(radioStations[0]);
  const [currentTime, setCurrentTime] = useState('');
  const [mode, setMode] = useState<'radio' | 'podcast' | 'music'>('radio');
  const [selectedGenre, setSelectedGenre] = useState<string>('electronic');
  const [currentPodcast, setCurrentPodcast] = useState(podcastEpisodes[0]);
  const [currentTrack, setCurrentTrack] = useState(sampleTracks[0]);
  const [isShuffleMode, setIsShuffleMode] = useState(false);
  const [playlistTracks, setPlaylistTracks] = useState<LocalTrack[]>([]);
  const audioRef = useRef<HTMLAudioElement>(null);

  // Update current time
  useEffect(() => {
    const updateTime = () => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
    };
    updateTime();
    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, []);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        // Note: In a real implementation, you'd set the src to currentStation.url
        // For demo purposes, we'll just toggle the playing state
        audioRef.current.play().catch(() => {
          // Handle play promise rejection
          console.log('Audio play failed - this is expected in demo mode');
        });
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleVolumeChange = (newVolume: number) => {
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (audioRef.current) {
      audioRef.current.muted = !isMuted;
    }
  };

  const changeStation = (direction: 'next' | 'prev') => {
    if (mode === 'radio') {
      const currentIndex = radioStations.findIndex(station => station.id === currentStation.id);
      let newIndex;

      if (direction === 'next') {
        newIndex = (currentIndex + 1) % radioStations.length;
      } else {
        newIndex = currentIndex === 0 ? radioStations.length - 1 : currentIndex - 1;
      }

      setCurrentStation(radioStations[newIndex]);
    } else if (mode === 'podcast') {
      const currentIndex = podcastEpisodes.findIndex(ep => ep.id === currentPodcast.id);
      let newIndex;

      if (direction === 'next') {
        newIndex = (currentIndex + 1) % podcastEpisodes.length;
      } else {
        newIndex = currentIndex === 0 ? podcastEpisodes.length - 1 : currentIndex - 1;
      }

      setCurrentPodcast(podcastEpisodes[newIndex]);
    } else if (mode === 'music') {
      const genreTracks = sampleTracks.filter(track => track.genre === selectedGenre);
      if (genreTracks.length === 0) return;

      const currentIndex = genreTracks.findIndex(track => track.id === currentTrack.id);
      let newIndex;

      if (isShuffleMode) {
        newIndex = Math.floor(Math.random() * genreTracks.length);
      } else {
        if (direction === 'next') {
          newIndex = (currentIndex + 1) % genreTracks.length;
        } else {
          newIndex = currentIndex === 0 ? genreTracks.length - 1 : currentIndex - 1;
        }
      }

      setCurrentTrack(genreTracks[newIndex]);
    }
  };

  const loadGenreTracks = (genre: string) => {
    const tracks = sampleTracks.filter(track => track.genre === genre);
    setPlaylistTracks(tracks);
    if (tracks.length > 0) {
      setCurrentTrack(tracks[0]);
    }
  };

  useEffect(() => {
    loadGenreTracks(selectedGenre);
  }, [selectedGenre]);

  const getCurrentContent = () => {
    switch (mode) {
      case 'radio':
        return {
          title: currentStation.name,
          subtitle: `${currentStation.frequency} FM`,
          description: currentStation.genre,
          nowPlaying: `Now Playing â€¢ ${currentTime}`
        };
      case 'podcast':
        return {
          title: currentPodcast.title,
          subtitle: currentPodcast.duration,
          description: currentPodcast.description,
          nowPlaying: `Published ${currentPodcast.publishDate}`
        };
      case 'music':
        return {
          title: currentTrack.title,
          subtitle: currentTrack.artist,
          description: `${currentTrack.album} â€¢ ${currentTrack.genre}`,
          nowPlaying: `${currentTrack.duration} â€¢ ${musicGenres.find(g => g.id === selectedGenre)?.icon} ${musicGenres.find(g => g.id === selectedGenre)?.name}`
        };
      default:
        return { title: '', subtitle: '', description: '', nowPlaying: '' };
    }
  };

  return (
    <>
      {/* Hidden audio element */}
      <audio ref={audioRef} preload="none" />

      <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 z-40">
        <AnimatePresence>
          {isExpanded ? (
            <motion.div
              initial={{ opacity: 0, y: 20, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              exit={{ opacity: 0, y: 20, scale: 0.9 }}
              className="bg-slate-900/95 border border-orange-500/30 rounded-lg backdrop-blur-sm shadow-2xl w-80"
            >
              {/* Header */}
              <div className="flex items-center justify-between p-3 border-b border-orange-500/20">
                <div className="flex items-center space-x-2">
                  {mode === 'radio' && <Radio className="w-5 h-5 text-orange-400" />}
                  {mode === 'podcast' && <Mic className="w-5 h-5 text-purple-400" />}
                  {mode === 'music' && <Music className="w-5 h-5 text-green-400" />}
                  <span className="text-sm font-medium text-orange-400">
                    KLOUD BUGS {mode === 'radio' ? 'Radio' : mode === 'podcast' ? 'Podcast' : 'Music'}
                  </span>
                  {isPlaying && <div className="w-2 h-2 bg-red-400 rounded-full animate-pulse" />}
                </div>
                <button
                  onClick={() => setIsExpanded(false)}
                  className="p-1 text-gray-400 hover:text-white transition-colors"
                >
                  <Minimize2 className="w-4 h-4" />
                </button>
              </div>

              {/* Mode Selection */}
              <div className="p-3 border-b border-orange-500/20">
                <div className="flex space-x-1">
                  <Button
                    variant={mode === 'radio' ? 'default' : 'outline'}
                    size="sm"
                    onClick={() => setMode('radio')}
                    className="flex-1"
                  >
                    <Radio className="w-4 h-4 mr-1" />
                    Radio
                  </Button>
                  <Button
                    variant={mode === 'podcast' ? 'default' : 'outline'}
                    size="sm"
                    onClick={() => setMode('podcast')}
                    className="flex-1"
                  >
                    <Mic className="w-4 h-4 mr-1" />
                    Podcast
                  </Button>
                  <Button
                    variant={mode === 'music' ? 'default' : 'outline'}
                    size="sm"
                    onClick={() => setMode('music')}
                    className="flex-1"
                  >
                    <Music className="w-4 h-4 mr-1" />
                    Music
                  </Button>
                </div>
              </div>

              {/* Genre/Content Selection */}
              {mode === 'music' && (
                <div className="p-3 border-b border-orange-500/20">
                  <div className="flex items-center space-x-2 mb-2">
                    <FolderOpen className="w-4 h-4 text-gray-400" />
                    <span className="text-sm text-gray-400">Genre:</span>
                  </div>
                  <Select value={selectedGenre} onValueChange={setSelectedGenre}>
                    <SelectTrigger className="w-full">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {musicGenres.map((genre) => (
                        <SelectItem key={genre.id} value={genre.id}>
                          {genre.icon} {genre.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <div className="flex items-center justify-between mt-2">
                    <span className="text-xs text-gray-400">
                      {playlistTracks.length} tracks loaded
                    </span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setIsShuffleMode(!isShuffleMode)}
                      className={cn(
                        "text-xs",
                        isShuffleMode && "bg-orange-500/20 text-orange-400"
                      )}
                    >
                      <Shuffle className="w-3 h-3 mr-1" />
                      Shuffle
                    </Button>
                  </div>
                </div>
              )}

              {/* Content Info */}
              <div className="p-4 text-center">
                <div className="text-lg font-bold text-white">{getCurrentContent().title}</div>
                <div className="text-orange-400 text-sm">{getCurrentContent().subtitle}</div>
                <div className="text-gray-400 text-xs">{getCurrentContent().description}</div>
                <div className="text-cyan-400 text-xs mt-1">ðŸŽµ {getCurrentContent().nowPlaying}</div>
              </div>

              {/* Controls */}
              <div className="flex items-center justify-center space-x-4 p-4 border-t border-orange-500/20">
                <button
                  onClick={() => changeStation('prev')}
                  className="p-2 text-gray-400 hover:text-white transition-colors"
                >
                  <SkipBack className="w-5 h-5" />
                </button>

                <button
                  onClick={togglePlay}
                  className="p-3 bg-orange-600 hover:bg-orange-700 rounded-full transition-colors"
                >
                  {isPlaying ? <Pause className="w-6 h-6 text-white" /> : <Play className="w-6 h-6 text-white" />}
                </button>

                <button
                  onClick={() => changeStation('next')}
                  className="p-2 text-gray-400 hover:text-white transition-colors"
                >
                  <SkipForward className="w-5 h-5" />
                </button>
              </div>

              {/* Volume Control */}
              <div className="flex items-center space-x-3 p-4 border-t border-orange-500/20">
                <button onClick={toggleMute} className="text-gray-400 hover:text-white">
                  {isMuted || volume === 0 ? <VolumeX className="w-4 h-4" /> : <Volume2 className="w-4 h-4" />}
                </button>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={isMuted ? 0 : volume}
                  onChange={(e) => handleVolumeChange(parseFloat(e.target.value))}
                  className="flex-1 h-1 bg-gray-700 rounded-lg appearance-none slider"
                />
              </div>

              {/* Station List */}
              <div className="p-4 border-t border-orange-500/20 max-h-32 overflow-y-auto">
                <div className="text-xs text-gray-400 mb-2">Available Stations:</div>
                {radioStations.map((station) => (
                  <button
                    key={station.id}
                    onClick={() => setCurrentStation(station)}
                    className={cn(
                      "w-full text-left p-2 rounded text-xs transition-colors",
                      station.id === currentStation.id
                        ? "bg-orange-600/20 text-orange-400"
                        : "text-gray-400 hover:bg-gray-800 hover:text-white"
                    )}
                  >
                    <div>{station.name} â€¢ {station.frequency} FM</div>
