          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg bg-teal-400/20 flex items-center justify-center">
                <Users className="w-5 h-5 text-teal-400" />
              </div>
              <div>
                <p className="text-sm font-medium text-white">Impact Score</p>
                <p className="text-xs text-cyan-300/60">{metrics?.social.totalImpact || 8947} points</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

