import { WebSocket } from 'ws';
import { EventEmitter } from 'events';
import { storage } from './storage';
import type { MiningRig, MiningPool, PoolConnection, InsertPoolConnection } from '../shared/schema';

interface StratumMessage {
  id: number | null;
  method?: string;
  params?: any[];
  result?: any;
  error?: any;
}

interface MinerConnection {
  socket: WebSocket;
  isConnected: boolean;
  lastActivity: number;
  subscriptionId: string | null;
  difficulty: number;
  currentJob: any;
  sharesSubmitted: number;
  sharesAccepted: number;
  sharesRejected: number;
  hashRate: number;
}

export class MultiPoolManager extends EventEmitter {
  private miners: Map<string, {
    rig: MiningRig;
    primaryConnection: MinerConnection | null;
    backup1Connection: MinerConnection | null;
    backup2Connection: MinerConnection | null;
    activePool: 'primary' | 'backup1' | 'backup2';
    loadBalanceMode: boolean;
    failoverCount: number;
  }> = new Map();

  private pools: Map<string, MiningPool> = new Map();
  private reconnectTimeouts: Map<string, NodeJS.Timeout> = new Map();

  constructor() {
    super();
    this.initializePools();
    this.startHealthCheckInterval();
  }

  private async initializePools() {
    try {
      // Load all mining pools and rigs from database
      const pools = await storage.getAllMiningPools();
      const rigs = await storage.getAllMiningRigs();

      // Store pools
      pools.forEach(pool => {
        this.pools.set(pool.id, pool);
      });

      // Initialize miners with 3 connections each
      for (const rig of rigs) {
        await this.initializeMiner(rig);
      }

      console.log(`ðŸ”Œ Multi-Pool Manager initialized with ${rigs.length} miners and ${pools.length} pools`);
    } catch (error) {
      console.error('Failed to initialize pools:', error);
    }
  }

  private async initializeMiner(rig: MiningRig) {
    const minerData = {
      rig,
      primaryConnection: null,
      backup1Connection: null,
      backup2Connection: null,
      activePool: 'primary' as 'primary' | 'backup1' | 'backup2',
      loadBalanceMode: false,
      failoverCount: 0
    };

    this.miners.set(rig.id, minerData);

    // Connect to all 3 pools simultaneously
    if (rig.primaryPoolId) {
      const pool = this.pools.get(rig.primaryPoolId);
      if (pool) {
        const connection = await this.createPoolConnection(rig, pool, 'primary');
        minerData.primaryConnection = connection;
      }
    }

    if (rig.backupPool1Id) {
      const pool = this.pools.get(rig.backupPool1Id);
      if (pool) {
        const connection = await this.createPoolConnection(rig, pool, 'backup1');
        minerData.backup1Connection = connection;
      }
    }

    if (rig.backupPool2Id) {
      const pool = this.pools.get(rig.backupPool2Id);
      if (pool) {
        const connection = await this.createPoolConnection(rig, pool, 'backup2');
        minerData.backup2Connection = connection;
      }
    }
  }

  private async createPoolConnection(
    rig: MiningRig, 
    pool: MiningPool, 
    connectionType: 'primary' | 'backup1' | 'backup2'
  ): Promise<MinerConnection | null> {
    return new Promise((resolve) => {
      try {
        // Create stratum+tcp connection using WebSocket
        const wsUrl = `ws://${pool.url}:${pool.port}`;
        const socket = new WebSocket(wsUrl, {
          protocol: 'stratum+tcp',
          handshakeTimeout: 10000,
        });

        const connection: MinerConnection = {
          socket,
          isConnected: false,
          lastActivity: Date.now(),
          subscriptionId: null,
          difficulty: 1000,
          currentJob: null,
          sharesSubmitted: 0,
          sharesAccepted: 0,
          sharesRejected: 0,
          hashRate: 0
        };

        socket.on('open', async () => {
          console.log(`[${rig.name}] Connected to ${pool.name} (${connectionType})`);
          connection.isConnected = true;

          // Update database
          await this.updatePoolConnection(rig.id, pool.id, connectionType, 'connected', rig.ipAddress || undefined);

          // Send mining.subscribe
          this.sendStratumMessage(socket, {
            id: 1,
            method: 'mining.subscribe',
            params: [`${rig.name}-${connectionType}`, null, pool.url, pool.port]
          });

          this.emit('minerConnected', { rig, pool, connectionType });
        });

        socket.on('message', (data: Buffer) => {
          try {
            const message: StratumMessage = JSON.parse(data.toString());
            this.handleStratumMessage(rig, pool, connectionType, connection, message);
            connection.lastActivity = Date.now();
          } catch (error) {
            console.error(`[${rig.name}] Invalid stratum message:`, error);
          }
        });

        socket.on('close', async () => {
          console.log(`[${rig.name}] Disconnected from ${pool.name} (${connectionType})`);
          connection.isConnected = false;

          // Update database
          await this.updatePoolConnection(rig.id, pool.id, connectionType, 'disconnected');

          // Attempt failover if this was the active connection
          this.handleConnectionFailure(rig.id, connectionType);

          // Schedule reconnection
          this.scheduleReconnection(rig, pool, connectionType);

          this.emit('minerDisconnected', { rig, pool, connectionType });
        });

        socket.on('error', (error) => {
          console.error(`[${rig.name}] WebSocket error for ${pool.name} (${connectionType}):`, error);
          connection.isConnected = false;
        });

        resolve(connection);
      } catch (error) {
        console.error(`Failed to create connection for ${rig.name} to ${pool.name}:`, error);
        resolve(null);
      }
    });
  }

  private handleStratumMessage(
    rig: MiningRig,
    pool: MiningPool,
    connectionType: string,
    connection: MinerConnection,
    message: StratumMessage
  ) {
    switch (message.method) {
      case 'mining.notify':
        // New job received
        connection.currentJob = {
          jobId: message.params?.[0],
          prevHash: message.params?.[1],
          coinbase1: message.params?.[2],
          coinbase2: message.params?.[3],
          merkleTree: message.params?.[4],
          version: message.params?.[5],
          nBits: message.params?.[6],
          nTime: message.params?.[7],
          cleanJobs: message.params?.[8]
        };

        // Only mine if this is the active connection
        const minerData = this.miners.get(rig.id);
        if (minerData && this.getActiveConnection(minerData) === connection) {
          this.emit('newJob', { rig, pool, job: connection.currentJob });
        }
        break;

      case 'mining.set_difficulty':
        connection.difficulty = message.params?.[0] || 1000;
        console.log(`[${rig.name}] Difficulty set to ${connection.difficulty} on ${pool.name}`);
        break;

      default:
        if (message.result !== undefined) {
          // Handle responses to our requests
          if (message.id === 1) {
            // Subscribe response
            connection.subscriptionId = message.result?.[1];

            // Send authorization
            this.sendStratumMessage(connection.socket, {
              id: 2,
              method: 'mining.authorize',
              params: [pool.username, pool.password || '']
            });
          } else if (message.id === 2) {
            // Authorize response
            if (message.result) {
              console.log(`[${rig.name}] Authorized on ${pool.name} (${connectionType})`);
            } else {
              console.error(`[${rig.name}] Authorization failed on ${pool.name}`);
            }
          } else if (message.id && message.id > 100) {
            // Share submission response
            if (message.result) {
              connection.sharesAccepted++;
              this.emit('shareAccepted', { rig, pool, connectionType });
            } else {
              connection.sharesRejected++;
              this.emit('shareRejected', { rig, pool, connectionType, error: message.error });
            }
          }
        }
    }
  }

  private sendStratumMessage(socket: WebSocket, message: StratumMessage) {
    if (socket.readyState === WebSocket.OPEN) {
      socket.send(JSON.stringify(message) + '\n');
    }
  }

  private getActiveConnection(minerData: any): MinerConnection | null {
    switch (minerData.activePool) {
      case 'primary': return minerData.primaryConnection;
      case 'backup1': return minerData.backup1Connection;
      case 'backup2': return minerData.backup2Connection;
      default: return null;
    }
  }

  private handleConnectionFailure(rigId: string, connectionType: string) {
    const minerData = this.miners.get(rigId);
    if (!minerData) return;

    // If the failed connection was the active one, failover
    if (minerData.activePool === connectionType) {
      this.performFailover(rigId);
    }
  }

  private performFailover(rigId: string) {
    const minerData = this.miners.get(rigId);
    if (!minerData) return;

    // Try to switch to a backup pool
    if (minerData.backup1Connection?.isConnected && minerData.activePool !== 'backup1') {
      minerData.activePool = 'backup1';
      minerData.failoverCount++;
      console.log(`[${minerData.rig.name}] Failover to backup pool 1`);
      this.emit('failover', { rig: minerData.rig, newPool: 'backup1' });
    } else if (minerData.backup2Connection?.isConnected && minerData.activePool !== 'backup2') {
      minerData.activePool = 'backup2';
      minerData.failoverCount++;
      console.log(`[${minerData.rig.name}] Failover to backup pool 2`);
      this.emit('failover', { rig: minerData.rig, newPool: 'backup2' });
    } else if (minerData.primaryConnection?.isConnected && minerData.activePool !== 'primary') {
      minerData.activePool = 'primary';
      console.log(`[${minerData.rig.name}] Failover back to primary pool`);
      this.emit('failover', { rig: minerData.rig, newPool: 'primary' });
    }
  }

  private scheduleReconnection(rig: MiningRig, pool: MiningPool, connectionType: string) {
    const timeoutKey = `${rig.id}-${pool.id}-${connectionType}`;

    if (this.reconnectTimeouts.has(timeoutKey)) {
      clearTimeout(this.reconnectTimeouts.get(timeoutKey)!);
    }

    const timeout = setTimeout(async () => {
      console.log(`[${rig.name}] Attempting to reconnect to ${pool.name} (${connectionType})`);
      const newConnection = await this.createPoolConnection(rig, pool, connectionType as any);

      if (newConnection) {
        const minerData = this.miners.get(rig.id);
        if (minerData) {
          (minerData as any)[`${connectionType}Connection`] = newConnection;
        }
      }

      this.reconnectTimeouts.delete(timeoutKey);
    }, 30000); // Reconnect after 30 seconds

    this.reconnectTimeouts.set(timeoutKey, timeout);
  }

  private async updatePoolConnection(
    rigId: string,
    poolId: string,
    connectionType: string,
    status: string,
    ipAddress?: string
  ) {
    try {
      const connectionData: InsertPoolConnection = {
        rigId,
        poolId,
        connectionType,
        status,
        ipAddress,
        lastConnected: status === 'connected' ? new Date() : undefined,
        lastDisconnected: status === 'disconnected' ? new Date() : undefined,
      };

      await storage.upsertPoolConnection(connectionData);
    } catch (error) {
      console.error('Failed to update pool connection:', error);
    }
  }

  private startHealthCheckInterval() {
    setInterval(() => {
      this.miners.forEach((minerData, rigId) => {
        // Check connection health and update hash rates
        this.updateMinerStats(rigId, minerData);
      });
    }, 30000); // Check every 30 seconds
  }

  private updateMinerStats(rigId: string, minerData: any) {
    const connections = [
      minerData.primaryConnection,
      minerData.backup1Connection,
      minerData.backup2Connection
    ].filter(conn => conn?.isConnected);

    const totalHashRate = connections.reduce((sum, conn) => sum + (conn?.hashRate || 0), 0);

    // Update rig stats in database
    storage.updateMiningRig(rigId, {
      hashrate: totalHashRate.toString(),
      status: connections.length > 0 ? 'online' : 'offline'
    });

    this.emit('statsUpdate', {
      rigId,
      totalHashRate,
      activeConnections: connections.length,
      totalShares: connections.reduce((sum, conn) => sum + (conn?.sharesSubmitted || 0), 0)
    });
  }

  // Public methods for external control
  public async addMiner(rig: MiningRig) {
    await this.initializeMiner(rig);
  }

  public async removeMiner(rigId: string) {
    const minerData = this.miners.get(rigId);
    if (minerData) {
      // Close all connections
      [minerData.primaryConnection, minerData.backup1Connection, minerData.backup2Connection]
        .forEach(conn => {
          if (conn?.socket) {
            conn.socket.close();
          }
        });

      this.miners.delete(rigId);
    }
  }

  public getMinerStats(): any[] {
    const stats: any[] = [];
    this.miners.forEach((minerData, rigId) => {
      const connections = [
        { type: 'primary', conn: minerData.primaryConnection },
        { type: 'backup1', conn: minerData.backup1Connection },
        { type: 'backup2', conn: minerData.backup2Connection }
      ];

      stats.push({
        rigId,
        rigName: minerData.rig.name,
        ipAddress: minerData.rig.ipAddress,
        activePool: minerData.activePool,
        failoverCount: minerData.failoverCount,
        connections: connections.map(({ type, conn }) => ({
          type,
          isConnected: conn?.isConnected || false,
          sharesAccepted: conn?.sharesAccepted || 0,
          sharesRejected: conn?.sharesRejected || 0,
          hashRate: conn?.hashRate || 0,
          difficulty: conn?.difficulty || 0
        }))
      });
    });
    return stats;
  }

  public getTotalStats() {
    let totalHashRate = 0;
    let totalMiners = 0;
    let onlineMiners = 0;
    let totalConnections = 0;
    let activeConnections = 0;

    this.miners.forEach((minerData) => {
      totalMiners++;
      const connections = [
        minerData.primaryConnection,
        minerData.backup1Connection,
        minerData.backup2Connection
      ];

      const activeConns = connections.filter(conn => conn?.isConnected);
      if (activeConns.length > 0) {
        onlineMiners++;
        totalHashRate += activeConns.reduce((sum, conn) => sum + (conn?.hashRate || 0), 0);
      }

      totalConnections += 3; // Each miner has 3 potential connections
      activeConnections += activeConns.length;
    });

    return {
      totalMiners,
      onlineMiners,
      totalHashRate,
      totalConnections,
      activeConnections,
      pools: this.pools.size
    };
  }
}

// Export singleton instance
export const multiPoolManager = new MultiPoolManager();
